bool inTriangle(vec2 A, vec2 B, vec2 C, vec2 P, float thick)
{
  vec2 v0 = C - A ;
  vec2 v1 = B - A ;
  vec2 v2 = P - A; // Compute dot products
  float dot00 = dot(v0, v0) ;
  float dot01 = dot(v0, v1) ;
  float  dot02 = dot(v0, v2) ;
  float dot11 = dot(v1, v1) ;
  float dot12 = dot(v1, v2) ;
  // Compute barycentric coordinates
  float invDenom = 1. / (dot00 * dot11 - dot01 * dot01) ;
  float u = (dot11 * dot02 - dot01 * dot12) * invDenom;
   float v = (dot00 * dot12 - dot01 * dot02) * invDenom ;
   // Check if point is in triangle return
   ;
   return (u >= 0.) && (v >= 0.) && (u + v < 1.) &&
   (u<thick||v<thick||(u+v)>1.-thick);
}

float curve(float a)
{
  return max(mod(iTime,10.)/10.,0.5)*(sin(iTime+a*102.4)*0.5+0.5)*((sin(a*5.-iTime*15.)*0.3+0.3)+(sin((iTime+a)*13.5)*0.5+0.5));
}

vec3 mix3(vec3 a, vec3 b, vec3 c, float f)
{

  vec3 gradA = mix(a, b, sat(f*2.));
  vec3 gradB = mix(gradA, c, sat(f-0.5)*2.);
  return gradB;
}

vec3 rdr(vec2 uv)
{
  vec3 col;

col = mix(vec3(0.6,0.3,.7),vec3(0.),abs(uv.y)*0.5);
  if (uv.y <0.)
  col = mix(vec3(0.),0.5*vec3(0.81,0.1,.937),pow(-uv.y,.5)*.5);

  vec2 posT= vec2(0.,-0.6);
  float rT = 1.2;//1.7+float(mod(iTime,0.25*sin(iTime)));
  float aT = (PI*2.)/3.;
  float offA = 0.;//iTime;

  vec2 A = vec2(rT*sin(aT*1.+offA),rT *cos(aT*1.+offA))-posT;//+vec2(0.,0.1*sin(iTime));
vec2 B = vec2(rT*sin(aT*2.+offA),rT *cos(aT*2.+offA))-posT;
vec2 C = vec2(rT*sin(aT*3.+offA),rT *cos(aT*3.+offA))-posT;

vec2 posT2= vec2(0.,0.6);
float angleB = 3.14159265;
vec2 A2 = vec2(rT*sin(aT*1.+angleB),rT *cos(aT*1.+angleB))-posT2;//+vec2(0.,0.1*sin(iTime));
vec2 B2 = vec2(rT*sin(aT*2.+angleB),rT *cos(aT*2.+angleB))-posT2;
vec2 C2 = vec2(rT*sin(aT*3.+angleB),rT *cos(aT*3.+angleB))-posT2;

if(inTriangle(A,B,C,uv,1.))
  col += mix3(vec3(0.),vec3(0.6,0.,1.),vec3(1.),abs(uv.y));
if(inTriangle(A2,B2,C2,uv,1.))
  col += mix3(vec3(0.),vec3(0.7,0.,1.),vec3(1.),abs(uv.y))*float(0.02*sin(2.*uv.x+iTime*5.)+mod(abs(uv.y+iTime*0.05),0.07)<0.01);
  return col;
}


vec3 rdrChroma(vec2 uv)
{
  float r = lenny(uv)*(sin(iTime*0.4)*0.3+0.3);
//  uv = uv+vec2(sin(iTime),cos(iTime))*0.2;
  vec3 col;

  col += rdr(uv+uv*r).r*vec3(0.63,0.72,0.75);
  col += rdr(uv).r;
  col +=rdr(uv-uv*r).r;
  return col/3.;
}

vec3 rdrRetro(vec2 uv)
{
  vec2 posT= vec2(0.,-.8);
  float rT = 3.2;//1.7+float(mod(iTime,0.25*sin(iTime)));
  float aT = (PI*2.)/3.;
  float offA = PI;//sin(iTime)*0.2+1.;//iTime;

