float intersect( in vec3 ro, in vec3 rd, out vec4 res, in vec4 c )
{
    vec4 tmp;
    float resT = -1.0;
	float maxd = 10.0;
    float h = 1.0;
    float t = 0.0;
    for( int i=0; i<300; i++ )
    {
        if( h<0.0001||t>maxd ) break;
	    h = map( ro+rd*t, tmp, c );
        t += h;
    }
    if( t<maxd ) { resT=t; res = tmp; }

	return resT;
}

float softshadow( in vec3 ro, in vec3 rd, float mint, float k, in vec4 c )
{
    float res = 1.0;
    float t = mint;
    for( int i=0; i<64; i++ )
    {
        vec4 kk;
        float h = map(ro + rd*t, kk, c);
        res = min( res, k*h/t );
        if( res<0.001 ) break;
        t += clamp( h, 0.01, 0.5 );
    }
    return clamp(res,0.0,1.0);
}

vec3 render( in vec3 ro, in vec3 rd, in vec4 c )
{
	const vec3 sun = vec3(  0.577, 0.577,  0.577 );

	vec4 tra;
	vec3 col;
    float t = intersect( ro, rd, tra, c );
    if( t < 0.0 )
    {
     	col = vec3(0.7,0.9,1.0)*(0.7+0.3*rd.y);
		col += vec3(0.8,0.7,0.5)*pow( clamp(dot(rd,sun),0.0,1.0), 48.0 );
	}
	else
	{
        vec3 mate = vec3(1.0,0.8,0.7)*0.3;
		//mate.x = 1.0-10.0*tra.x;

        vec3 pos = ro + t*rd;
        vec3 nor = calcNormal( pos, c );

		float occ = clamp(2.5*tra.w-0.15,0.0,1.0);


        col = vec3(0.0);

        // sky
        {
        float co = clamp( dot(-rd,nor), 0.0, 1.0 );
        vec3 ref = reflect( rd, nor );
        //float sha = softshadow( pos+0.0005*nor, ref, 0.001, 4.0, c );
        float sha = occ;
        sha *= smoothstep( -0.1, 0.1, ref.y );
        float fre = 0.1 + 0.9*pow(1.0-co,5.0);

		col  = mate*0.3*vec3(0.8,0.9,1.0)*(0.6+0.4*nor.y)*occ;
		col +=  2.0*0.3*vec3(0.8,0.9,1.0)*(0.6+0.4*nor.y)*sha*fre;
        }

        // sun
        {
        const vec3 lig = sun;
        float dif = clamp( dot( lig, nor ), 0.0, 1.0 );
        float sha = softshadow( pos, lig, 0.001, 64.0, c );
        vec3 hal = normalize( -rd+lig );
        float co = clamp( dot(hal,lig), 0.0, 1.0 );
        float fre = 0.04 + 0.96*pow(1.0-co,5.0);
        float spe = pow(clamp(dot(hal,nor), 0.0, 1.0 ), 32.0 );
        col += mate*3.5*vec3(1.00,0.90,0.70)*dif*sha;
        col +=  7.0*3.5*vec3(1.00,0.90,0.70)*spe*dif*sha*fre;
        }

        // extra fill
        {
        const vec3 lig = vec3( -0.707, 0.000, -0.707 );
		float dif = clamp(0.5+0.5*dot(lig,nor), 0.0, 1.0 );
        col += mate* 1.5*vec3(0.14,0.14,0.14)*dif*occ;
        }

        // fake SSS
        {
        float fre = clamp( 1.+dot(rd,nor), 0.0, 1.0 );
        col += mate* mate*0.6*fre*fre*(0.2+0.8*occ);
        }
    }

	return pow( col, vec3(0.4545) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // anim
    float time = iTime*.15;
    vec4 c = vec4(1.5,1.5,0.7,0.7)*cos( vec4(0.5,3.9,1.4,1.1) + time*vec4(1.2,1.7,1.3,2.5) ) - vec4(0.0,0.0,1.0,0.0);

    // camera
	float r = 1.5+0.15*cos(0.0+0.29*time);
    vec3 ro = vec3(           r*cos(0.3+0.37*time), 
					5.3 + 0.8*r*cos(1.0+0.33*time), 
					          r*cos(2.2+0.31*time) );
    //ro = ro.yxz;
 	vec3 ta = vec3(0.0,0.0,0.0);
    float cr = 0.1*cos(0.1*time);


    // render
    vec3 col = vec3(0.0);
    for( int j=0; j<AA; j++ )
    for( int i=0; i<AA; i++ )
    {
        vec2 p = (-iResolution.xy + 2.0*(fragCoord + vec2(float(i),float(j))/float(AA))) / iResolution.y;

        vec3 cw = normalize(ta-ro);
        vec3 cp = vec3(sin(cr), cos(cr),0.0);
        vec3 cu = normalize(cross(cw,cp));
        vec3 cv = normalize(cross(cu,cw));
        vec3 rd = normalize( p.x*cu + p.y*cv + 2.0*cw );

        col += render( ro, rd, c );
    }
    col /= float(AA*AA);

    vec2 uv = fragCoord.xy / iResolution.xy;
	col *= 0.7 + 0.3*pow(16.0*uv.x*uv.y*(1.0-uv.x)*(1.0-uv.y),0.25);

	fragColor = vec4( col, 1.0 );
}

// some (hopefully correct) observations on using smoothstep and comparing with aaStep


//smooth version of step - https://www.ronja-tutorials.com/2019/11/29/fwidth.html
// produces a harder cut off in conparison with smoothstep, if smoothstep can't be used use this
// usage:
// float aa = aaStep(0.25f, gradient); // harder cut off
// -or-
// float aa = smoothstep(0.24f, 1.0f, gradient); // smoother cut off
//
// gradient *= aa;  // applying cutoff (hardly)
// -or-
// gradient = (gradient - aa * 0.5f) * aa;  // applying cutoff (smoothly)
//
// **** to remember:
// float aa = smoothstep(0.0f, gradient, gradient);  ** pretty much equals gradient** so the line below this does nothing
//  gradient *= aa;
// -however-
// float aa = smoothstep(0.0f, 1.0f, gradient);	**actually smooths and remaps the gradient to the smoothstep function
//  gradient *= aa; // apply smoothstep to original grasdient
// -and-
// *** if the value of second parameter is smaller than first parameter into smoothstep
// there will be an undesired "inverse" cutoff ***
// if the value for the second parameter is larger than 1.0f, the result is no longer normalized in the range 0..1.0
// ie.) smoothstep(0.0f, 1.5f, gradient) requires : smoothstep(0.0f, 1.5f, gradient) / (1.5f*1.5f)  to normalize again
// I dunno about you but negative values are way outside the scope of smoothstep
// #### use smoothstep rather than aaStep, only use aaStep in special cases where a smoother, but harder cutoff is needed (in comparison to step())
// or if "linearerity" is critically important and smoothstep is introducing unsightly waves of discontuity ####

//smooth version of step - https://www.ronja-tutorials.com/2019/11/29/fwidth.html
float aaStep(in float compValue, in float gradient){
  float halfChange = fwidth(gradient) * 0.5f;
  //base the range of the inverse lerp on the change over one pixel
  float lowerEdge = compValue - halfChange;
  float upperEdge = compValue + halfChange;
  //do the inverse interpolation
  return( clamp((gradient - lowerEdge) / (upperEdge - lowerEdge), 0.0f, 1.0f) );
}

vec2 rotate( in vec2 p, in float angle )
{
	#define c_ x
	#define s_ y
	vec2 cossin = vec2(cos(angle), sin(angle));
	return( vec2(p.x * cossin.c_+ p.y * cossin.s, p.x * -cossin.s_+ p.y * cossin.c_) );

	#undef c_
	#undef s_
}

