float dist(float d) {
    float size = 0.01;
    vec2 s = vec2(0.5 + size, 0.5 - size);
    return 1. - min( smoothstep(d, s.x, s.y), 
                     smoothstep(d, s.y, s.x) );
}

float ring(vec2 uv) {
    return dist(sqrt(dot(uv, uv)));
}

vec3 hsl2rgb(vec3 c){
    vec3 rgb = clamp(abs(mod(
        c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0);
    return c.z + c.y * (rgb-0.5)*(1.0-abs(2.0*c.z-1.0));
}

float truchet(vec2 uv) {
    float cellRand = rand(floor(uv));
    vec2 cellUv = fract(uv)-0.5;
    if (cellRand < 0.9) {      
        float a = floor(cellRand*4.)*1.57075;

        if (cellRand < 0.1)
        	a += iTime;

        vec2 sc = vec2(sin(a), cos(a));
        cellUv = mat2(sc.y, -sc.x, sc.x, sc.y) * cellUv;

        if (sqrt(dot(cellUv,cellUv)) > 0.5)
    		return 0.;
        else
        	return ring(cellUv - 0.5) + ring(cellUv + 0.5);

    } 
    return min(1., dist(cellUv.x + 0.5) + dist(cellUv.y + 0.5));
}

