float GetDist(vec3 p  ) {	



    float d;
    float dif1;
    float dif2;

    d=999.9;
    float planeDist = p.y;



    float bxInf = sdBox (p-vec3(0.0,1,4.5),vec3(1.65,0.1,0.5));
    float bxDer = sdBox (p-vec3(-1.6,1.9,4.5),vec3(0.1,1.0,0.5));
    float bxIzq = sdBox (p-vec3(1.6,1.9,4.5),vec3(0.1,1.0,0.5));
    float bxSup = sdBox (p-vec3(-0.0,3.0,4.5),vec3(2.5,0.1,0.5));
    float bxAtras = sdBox (p-vec3(-0.1,2.1,5.0),vec3(1.65,1.0,0.1));

    float bxAtrasVentana = sdBox (p-vec3(-0.5,2.1,5.0),vec3(0.5,0.5,0.4));
    float bxAtrasPuerta = sdBox (p-vec3(0.8,1.8,5.0),vec3(0.45,0.8,0.4));


    float bxSupVentana = sdBox (p-vec3(0.0,3.0,4.5),vec3(0.5,0.21,0.4));



    float  spLuz = sdSphere (p-vec3(0.0), 0.5);
    vec2 res;
    res = vec2(9999, 0);

    //res = opU(res, vec2(planeDist, cPlane));

     if (res.x < planeDist)
      { res.x = res.x;     	} 
    else
        { 
          res.x = planeDist;
        if (planeDist > 0.5 )
          { res=vec2(res.x,CSky); }
        else
         { 
            res=vec2(res.x, CPlane2);
         } 
     } 




    res = opU(res, vec2(bxInf, CInf));
    res = opU(res, vec2(bxDer, CDer));
    res = opU(res, vec2(bxIzq, CIzq));



    dif1 = differenceSDF(bxAtras,bxAtrasVentana);

    dif1= differenceSDF(dif1,bxAtrasPuerta);


    res = opU(res, vec2(dif1, CAtras));



    dif2=differenceSDF(bxSup,bxSupVentana);     
    res = opU(res, vec2(dif2, CSup));
    res = opU(res, vec2(spLuz, CLight));




    d = res.x;
    mObj.dist = res.x;
    mObj.tipo = res.y;

    return d;
}


float RayMarch(vec3 ro, vec3 rd) {
	float dO=0.2;
    //Determines size of shadow
    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        dO += dS;
        if(dO>MAX_DIST || dS<SURF_DIST) break;
    }

    return dO;
}





vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    //Texture of white and black in image
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}



float GetLight(vec3 p) {
    vec3 lightPos = vec3(0, 5, 6);
    //Determine movement of light ex. shadow and light direction and diffusion
    lightPos.xz += vec2(sin(iTime), cos(iTime)*2.);
    vec3 l = normalize(lightPos-p);
    vec3 n = GetNormal(p);

    float dif = clamp(dot(n, l), 0., 1.);
    float d = RayMarch(p+n*SURF_DIST*2., l );
    if(d<length(lightPos-p)) dif *= .1;

    return dif;
}






