    vec3 ro = vec3(0, 2.5, 1.5+1.5*sin(iTime));

    vec3 rLuz=vec3(-0.5, 1.5, -5.5);

    vec3 rd = normalize(vec3(uv.x, uv.y, 1.0));

    float d = RayMarch(ro, rd);
    Obj=mObj;



    vec3 p = (ro + rd * d ); 
    vec3 pluz=(rLuz + rd * d );

    float dif = GetLight(p);
    float dif2 = abs(1.0- GetLight(pluz));

    vec3 colobj;

    colobj=getSphereColor(int( Obj.tipo));



    dif=(dif+dif2)/1.5;

    col = vec3(dif)*colobj;



    fragColor = vec4(col,1.0);
    //Background color is white
}

