#define PI 3.14159265359
#define PHI (1.618033988749895)
#define TAU 6.283185307179586

float sdIcosahedron(vec3 p, float r)
{
	float q = (sqrt(5.)+3.)/2.;

	vec3 n1 = normalize(vec3(q,1,0));
	vec3 n2 = vec3(sqrt(3.)/3.);

	p = abs(p/r);
	float a = dot(p, n1.xyz);
	float b = dot(p, n1.zxy);
	float c = dot(p, n1.yzx);
	float d = dot(p, n2.xyz)-n1.x;
	return max(max(max(a,b),c)-n1.x,d)*r;
}

mat2 rot(float a)
{
    float ca = cos(a);
    float sa = sin(a);

    return mat2(ca,-sa,sa,ca);
}

float sdCube(vec3 p)
{
    vec3 q = abs(p) - 1.;
    return length(max(q, vec3(0.))) + min(max(max(q.x, q.y), q.z), 0.);
}

int m = 0;

float map(vec3 p)
{
    float time = TIME * .2;
	vec3 cp = p;

    p.xy *= rot(time);
    p.yz *= rot(time * .5);
    p.xz *= rot(time * .25);
    float dist  = sdIcosahedron(p, 1.7 );
    if(dist < .01)
        m = 1;
    p = cp;
    p.y = abs(p.y);
    p.xy *= rot(PHI);
    p.yz *= rot(PHI);
    p.xz *= rot(PHI);

    float outer = sdIcosahedron(p , 10.);  
    if(outer < .01 && m != 1)
        m = 2;

    dist = min(dist, -outer);


    return dist;
}

vec3 normal(vec3 p)
{
    float d = map(p);
    vec2 e = vec2(.001,.0);
    return normalize(
    vec3(
    	d - map(p + e.xyy),
    	d - map(p + e.yxy),
    	d - map(p + e.yyx)
        )
    );
}

float ray(inout vec3 cp, vec3 rd, out float st)
{
    float cd = 0.;
    for(st = 0.; st < 1.; st += 1. / 512.)
    {
        cd = map(cp);
        if(abs(cd) < .01)
            break;
        cp += rd * cd;
    }

    return cd;
}


