#define GRIDSIZE 15.0
#define SPEED 1.5
#define MIN_SIZE .6
#define MAX_SIZE 1.5
#define RIPPLE 8.0
#define ANGLE 45.0

float Xor (float a, float b) {
    return a*(1.-b) + b*(1.-a);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    float t = iTime * SPEED;
    vec3 col = vec3(0.);

    float rot = mix(0., 6.28318530718, t*.01);
    float s = sin(rot);
    float c = cos(rot);
    uv *= mat2(c, -s, s, c);

    vec2 gv = fract(uv*GRIDSIZE)-.5;


    float m = 0.;

    for (float y=-1.; y <= 1.; y++) {
        for (float x=-1.; x <= 1.; x++) {
            vec2 offs = vec2(x, y);

            float d = length(gv + offs);
            float r = mix(MIN_SIZE, MAX_SIZE, sin(t-length(uv)*RIPPLE)*.5+.5);

            m = Xor(m, smoothstep(r, r*.9, d));
        }
    }

    //col.rg = gv;
    col += m;

    fragColor = vec4(col,1.0);
}

