#define FK(k) floatBitsToInt(cos(k))^floatBitsToInt(k)
float hash(vec2 k) {
  int x = FK(k.x);int y = FK(k.y);
  return float((x*x-y)*(y*y+x)-x)/2.14e9;
}

float hash3(vec3 k) {
  float h1 = hash(k.xy);
  return hash(vec2(h1, k.z));
}

vec3 hash33(vec3 k) {
  float h1 = hash3(k);
  float h2 = hash3(k*h1);
  float h3 = hash3(k*h2);
  return vec3(h1, h2, h3);
}

float smin( float a, float b, float k ) {
    float h = max( k-abs(a-b), 0.0 )/k;
    return min( a, b ) - h*h*h*k*(1.0/6.0);
}

vec3 sphercoord(vec2 p) {
  float l1 = acos(p.x);
  float l2 = acos(-1.)*p.y;
  return vec3(cos(l1), sin(l1)*sin(l2), sin(l1)*cos(l2));
}

vec3 erot(vec3 p, vec3 ax, float ro) {
  return mix(dot(p,ax)*ax, p, cos(ro)) + sin(ro)*cross(p,ax);
}

float comp(vec3 p, vec3 ro, float t) {
  vec3 ax = sphercoord(ro.xy);
  p.z -= t;
  p = erot(p, ax, ro.z*acos(-1.));
  float scale = 4. + hash(ro.xz)*0.5+0.5;
  p = (fract(p/scale)-0.5)*scale;
  return length(p) - 0.8;
}

float scene(vec3 p) {
  float rad = 3.+p.z+sin(p.y/2.+iTime)+cos(p.x/3.+iTime*0.9);
  float dist = 10000.;
  for (int i = 0; i < 4; i++) {
    vec3 rot = hash33(vec3(float(i+1), cos(float(i)), sin(float(i))));
    float d = comp(p, rot, iTime/2.*(float(i+1)));
    dist = smin(dist, d, 1.);
  }
  return mix(dist, rad, mix(0.3,0.8+sin(iTime)*0.2, 0.1));
}

vec3 norm(vec3 p) {
  mat3 k = mat3(p,p,p)-mat3(0.1);
  return normalize(scene(p) - vec3(scene(k[0]),scene(k[1]),scene(k[2])));
}

float drawdist = 50.;
vec3 trace(vec3 init, vec3 cam, int itcout, inout bool hit) {
  vec3 p = init;
  hit = false;
  float dist;
  for (int i = 0; i < itcout; i++) {
    dist = scene(p)*1.1;
    if (dist*dist < 0.00001) { hit = true; p+= dist*cam; break;}
    if (distance(p, init) > drawdist) { break; }
    p += dist*cam*1.2;
  }
  return p;
}

float refcomp(vec3 p, vec3 cam, float n) {
	float dst = scene(p+cam*n);
    float sharpness = 1.0; //higher values are more sharp
    return smoothstep(-1., 1., (dst*sharpness));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
  uv -= 0.5;
  uv /= vec2(iResolution.y / iResolution.x, 1);


  vec3 cam = normalize(vec3(0.8,uv));
  bool hit = false;
  vec3 col = vec3(1);
  vec3 p; vec3 n;
  vec3 init = vec3(-4,0,sin(iTime*0.37)*1.4);
  vec2 m = iMouse.xy/iResolution.xy;
  if (iMouse.z <= 0.) {
    m = vec2(0.4);
  }
  cam = erot(cam, vec3(0,1,0), -m.y*2.+0.5);
  init = erot(init, vec3(0,1,0), -m.y*2.+0.5);
  cam = erot(cam, vec3(0,0,1), -m.x*6.);
  init = erot(init, vec3(0,0,1), -m.x*6.);
  vec3 origin = init;
  p = trace(init, cam, 400, hit);
  if (hit) {
    n = norm(p);
    float fresnel = pow(1.-abs(dot(cam, n))*0.5,2.);
    col *= fresnel;
    cam = reflect(cam, n);
    hit = false;
    init = p+n*.01;
	float comps = refcomp(init, cam, 5.) + refcomp(init, cam, 4.)*2. + refcomp(init, cam, 2.)*3. + refcomp(init, cam, 1.)*4.;
    comps /= 10.;
	float dst2 = max(scene(p+n*2.)/2.*0.5+0.5,0.);
    col = dst2*vec3(1.)*(pow(comps,2.))*fresnel;
  }
  float fog = pow(smoothstep(drawdist*.5,drawdist,distance(p,origin)),2.);

  fragColor.xyz = mix(col*0.8+0.02, vec3(1),fog);
  fragColor.xyz = pow(smoothstep(vec3(0),vec3(1),sqrt(fragColor.xyz)),vec3(1.7,1.6,1.5));
}

float aaStep(float edge, float gradient)
{
    float halfPix = fwidth(gradient) / 2.0;
    float low = edge - halfPix;
    float hi  = edge + halfPix;
    return clamp((gradient - low) / (hi - low), 0.0, 1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord/iResolution.xy;
    float theta = sin(iTime) * 25.0 * (3.14159 / 180.0);
    uv = vec2(cos(theta) * uv.x - sin(theta) * uv.y,
              sin(theta) * uv.x + cos(theta) * uv.y);

	vec3 col = vec3(aaStep(0.5, uv.x));

    fragColor = vec4(col.rgb,1.0);
}

float D_MIN = 0.001;
float D_MAX = 30.;
int STEP_MAX = 512;

float sMin(float a, float b, float t)
{
    float tt = max(t - abs(a-b), 0.);
    return min(a, b) - tt * tt / t / 4.;
}

vec4 sMin(vec4 a, vec4 b, float t){
    //https://www.iquilezles.org/apps/graphtoy/?f1(x)=4*sin(x)&f2(x)=x/3&f3(x)=0.2&f4(x)=max(f3(x)-abs(f1(x)-f2(x)),0)&f6(x)=1-max(min((0.5%20-%20(f1(x)-f2(x))/2/f3(x)),%201.),%200.)
    float tt = max(t - abs(a.x-b.x), 0.);
    float s = 1.-max(min(0.5-(a.x-b.x)/(2.*t),1.),0.);
    return vec4(min(a.x, b.x) - tt * tt / t / 4., mix(a.yzw, b.yzw, s*s*(3.0-2.0*s)));
}

float sMax(float a, float b, float t){
    float tt = max(t - abs(a-b), 0.);
    return max(a, b) + tt * tt / t / 4.;
}

vec4 sMax(vec4 a, vec4 b, float t){
    float tt = max(t - abs(a.x-b.x), 0.);
    float s = 1.-max(min(0.5-(a.x-b.x)/(2.*t),1.),0.);
    return vec4(max(a.x, b.x) + tt * tt / t / 4., mix(a.yzw, b.yzw, s*s*(3.0-2.0*s)));
}

float sdSphere(vec3 p, float r) {
    return length(p) - r;
}

float sdBox(vec3 p, vec3 s, float r){
    return length(max(abs(p) - s, 0.)) - r;
}

float sdTorus(vec3 p, float r1, float r2){
    return length(vec2(length(p.xz)-r2,p.y)) - r1;
}


vec4 dist(vec3 p){
    float dplan = p.y;
    vec4 plan = vec4(dplan, vec3(0.5,0.5,0.5));

    float dsphere = sdSphere(p-vec3(1.5,1.5,0), 1.);
    vec4 sphere = vec4(dsphere, vec3(0.3, 0.2, 0.8));

    dsphere = -sdSphere(p-vec3(-1.5,1.5,0), 1.);
    vec4 sphere2 = vec4(dsphere, vec3(0.3, 0.8, 0.2));

    float dbox = sdBox(p-vec3(sin(iTime * 0.6) * 3., 0.5, 1), vec3(0.5, 1, 2), 0.);
    vec4 box = vec4(dbox, vec3(0.8, 0.3, 0.2));

    vec4 m = sMin(sMax(box, sphere2, 0.2), sphere, 0.8 + cos(iTime * 1.)*0.0);


    vec3 pt = p;
    pt.y -= 0.04*sin(p.x*11.);
    dbox = abs(sdBox(pt-vec3(0,1,5), vec3(.8,.2,.2),0.) -0.4)-0.1;
    box = vec4(dbox, vec3(.7,.7,.7));

    vec4 m2 = sMax(box, vec4(pt.y-1.-sin(iTime),vec3(0.8,0.2,0.3)),0.1);

    pt = vec3(mod(p.x,16.)-8.,p.y-1.,mod(p.z,16.)-8.);
    float a = pt.y * .4 + iTime * .6;
    mat2 mr = mat2(cos(a),-sin(a),sin(a),cos(a));
    pt.xz *= mr;
    dbox = sdBox(pt, vec3(.125,16.,0.5),0.);
    box = vec4(dbox, vec3(0.2,.7,.5));

    vec4 m3 = sMin(box, plan, 4.);

    m2 = sMin(m2, m3, 0.1);

    pt = p;
    pt.yz *= 1.;

    m2 = sMin(m2, vec4(sdTorus(pt-vec3(0.,0.,0.),.5,6.), .7,.3,.8), 0.2);

    return sMin(m, m2, 0.001);
}

vec4 RayMarch(in vec3 ro, vec3 dir)
{
    float td = 0.;
    vec4 p;
    for (int i=0; i<STEP_MAX; i++){
        vec3 pos = ro + dir * td;
        p = dist(pos);
        if (p.x<D_MIN) break;
        td += p.x * .9;
        if (td>D_MAX) break;
    }
    return vec4(td, p.yzw);
}

vec3 normal(vec3 p){
    vec2 e = vec2(0.001, 0);
    return normalize(vec3(dist(p + e.xyy).x - dist(p - e.xyy).x,
                          dist(p + e.yxy).x - dist(p - e.yxy).x,
                          dist(p + e.yyx).x - dist(p - e.yyx).x ));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){

    vec2 uv = (fragCoord-iResolution.xy*0.5)/iResolution.y;

    vec3 ro = vec3(7.*sin(iTime*0.09),6,-7.*cos(iTime*0.09));
    vec3 o = normalize(vec3(0,1,1)-ro);
    vec3 x = cross(vec3(0,1.,0), o);
    vec3 y = cross(o, x);
    mat3 cd = mat3(x.x,y.x,o.x,x.y,y.y,o.y,x.z,y.z,o.z);
    vec3 rd = normalize(vec3(uv.x,uv.y,0.9)*cd);

    vec4 rm = RayMarch(ro, rd);
    float d = rm.x;
    vec3 pos = ro + d * rd;

    vec3 col = vec3(0);

    if (d<D_MAX)
    {
        col = rm.yzw;
        vec3 n = normal(pos);
        vec3 lp = vec3(2,6,-3);
        vec3 ld = normalize(pos-lp);
        float ll = dot(n, -ld);
        float pl = dot(n, vec3(0, -1, 0));
        float sl = dot(n, vec3(0, 1, 0));
        float c = ll*1.4 + pl*0.8 + sl*0.8;
        //c = pow(c, 0.4545);
        col *= c;
        float ldist = length(pos - lp);
        if (RayMarch(pos + n * 0.4, -ld).x < ldist) col *= 0.2;
    }
    else
    {
        col = vec3(0.5, 0.5, 0.75) * (1. - rd.y * 0.2);
    }

    fragColor = vec4(col,1.0);
}

float Hash(vec2 P) {
	return fract(cos(dot(P,vec2(91.52,-74.27)))*939.24);
}

float Value(vec2 P) {
	vec2 F = floor(P);
	vec2 S = P-F;
	vec4 M = (S*S*(3.-S-S)).xyxy;
    M = M*vec4(-1,-1,1,1)+vec4(1,1,0,0);
	return (Hash(F+vec2(0,0))*M.x+Hash(F+vec2(1,0))*M.z)*M.y+
		   (Hash(F+vec2(0,1))*M.x+Hash(F+vec2(1,1))*M.z)*M.w;
}

float Noise(vec2 P) {
	return 
        .6*Value(P*8.)+
        .4*Value(P*16.)+
        .3*Value(P*32.)+
        .2*Value(P*64.);
}

float mat(vec2 uv, vec2 offset) {
	return Noise(uv*vec2(6., 20.)+offset);
}

float ndot(vec2 a, vec2 b ) { return a.x*b.x - a.y*b.y; }

float sdRhombus( in vec2 p, in vec2 b ) {
    vec2 q = abs(p);
    float h = clamp( (-2.0*ndot(q,b) + ndot(b,b) )/dot(b,b), -1.0, 1.0 );
    float d = length( q - 0.5*b*vec2(1.0-h,1.0+h) );
    return d * sign( q.x*b.y + q.y*b.x - b.x*b.y );
}

float sdCircle( vec2 p, float r ) {
  return length(p) - r;
}

float opSmoothUnion(float d1, float d2, float k) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h);
}

float sdDiamond(vec2 p) {
    return opSmoothUnion(sdRhombus(p, vec2(.2, 1.)), sdCircle(p, .2), .1);
}

vec4 over( in vec4 a, in vec4 b ) {
    return mix(a, b, 1.-a.w);
}

mat2 rot(float a) {
	float c = cos(a);
	float s = sin(a);
    return mat2(c, -s, s, c);
}

vec4 diamonds(vec2 uv, float angle) {
    vec2 p = vec2(uv);
    mat2 r = rot(angle);
    p = fract(p)*2.-1.;
    p *= r;
    float d = sdDiamond(p);
    float a = smoothstep(.01, .0, d);
    float zp = smoothstep(.0, .1, -d);
    float zh = smoothstep(.0, .1, -sdDiamond(p+vec2(.01, .0)));
    float zv = smoothstep(.0, .1, -sdDiamond(p+vec2(.0, .01)));
	return zp+zh+zv==0. 
        ? vec4(0,0,0,a)
        : vec4(normalize( vec3(zp-zh, zp-zv, zp)), a);	
}

vec3 light(vec3 lp, vec3 lc, vec3 n, vec2 uv) {
    vec3 ld = vec3(lp.xy-uv, lp.z-n.z);
    float l = length(ld);
    return lc*pow(max(.0, dot(n, ld/l)), 4.)/l;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = (fragCoord-iResolution.xy*.5)/iResolution.y*2.;

    float n0 = mat(uv, vec2(.0));
    float n1 = mat(uv, vec2(.001, .000));
    float n2 = mat(uv, vec2(.000, .001));

    vec4 c = vec4(.0);
	vec4 d0 = diamonds(uv*3.+.0,  .78539);
	vec4 d1 = diamonds(uv*3.+.5, -.78539);
	float a = max(d0.w, d1.w);
    vec3 n = (d0.xyz + d1.xyz)*a + vec3(n0-n1, n0-n2, n0)*.6*(1.-a*.6);
    float t = iTime;
    mat2 m0 = rot( t*1.4+.4)*sin(t*.4+.2);
    mat2 m1 = rot(-t*1.4+.7)*sin(t*.6+.5);
    mat2 m2 = rot( t*1.4+.9)*cos(t*.8+.9);

    // light colors
    vec3 l0c = vec3(1., .3, 1.);
    vec3 l1c = vec3(.1, .3, 1.);
    vec3 l2c = vec3(.1, 1., 1.);
    // light positions
    vec3 l0v = vec3(vec2(.99, 0.)* m0, 1.5);
    vec3 l1v = vec3(vec2(.70, 0.)* m1, 1.5);
    vec3 l2v = vec3(vec2(.00, 1.)* m2, 1.5);
    // light intensity
    float li0 = pow(distance(uv, l0v.xy), -1.4);
    float li1 = pow(distance(uv, l1v.xy), -1.4);
    float li2 = pow(distance(uv, l2v.xy), -1.4);

    vec3 rc = vec3(.05+n0*n0*n0*n0*.1);
    rc += pow(max(.0, n.z), 2.)*.12;
    rc += light(l0v, l0c, n, uv);
    rc += light(l1v, l1c, n, uv);
    rc += light(l2v, l2c, n, uv);
    c = over(vec4(rc, 1.), c);

    // light dots
    c.rgb += l0c*li0*.004+li0*n0*.008;
    c.rgb += l1c*li1*.004+li1*n0*.008;
    c.rgb += l2c*li2*.004+li2*n0*.008;

    fragColor = c;
}

