float f(in float x){
    return 0.25*(pow(exp(x)+exp(-x),2.0)-pow(exp(x)-exp(-x),2.0)); 	// always equal to 1
}

float grad(in float x){
    const float eps = 1e-2;
    float a = (f(x+eps)-f(x-eps))/(2.0*eps);
    return sqrt(a*a+1.0);
}

