void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	uv = 1.0f-uv;

    float boundary = 1.0f; //boundary is position of edge between fractals
    float fract = 2.0f; //fract is the ratio of fractal n-1/n
    float nx =  ceil(-log2(uv.x)/log2(fract)); //nx is the depth of the fractal (recursively) in x dir.
    float ny = ceil(-log2(uv.y)/log2(fract));
    float n = min(nx,ny);
    boundary = 1.0f;
    for(int i = 0 ; i < int(n); i++)
    {
        boundary /= fract;
    }

    float L = (max( (uv.y-boundary) ,(uv.x - boundary) )*(.85f + .15 * sin(iTime) ))/(boundary-boundary/fract ) +.5f;
    float r = float(int(n)%4) ;
    float g = float(int(n+1.0f)%4);
    float b = float(int(n+2.0f)%4);
    vec3 col = vec3( r/4.0f, (g)/4.0f, (b)/4.0f)/L;

    // Output to screen
    fragColor = vec4(col,1.0);
}

