mat2 rotate (float t) {
    return mat2(cos(t), sin(t), -sin(t), cos(t));
}

float fBm(vec2 st, float noisetype) {

    float frequency = 1.0;
    float amplitude = 0.5;
    float value = 0.0;

    for (float i = 0.; i <6.+7.*sin(iTime*.5)*sin(iTime*.95)+1.; i++) {
        if (noisetype == 0.0)
            value += amplitude * Value2D(frequency * st);
        else
            value += amplitude * SimplexPerlin2D(frequency * st);
        frequency *= 2.0;
        amplitude *= 0.5;
        st = rotate(0.4) * st;

    }

    return value;
}

float warp (vec2 st, float noise) {
    // f(p) = f(p + h(p))
    // h(p): vec2->vec2
    float r = 0.0;
    r = fBm(st, noise);
    vec2 st1 = vec2(0.0);

    st1.x = fBm(st, noise);
    st1.y = fBm(st + vec2(1.0), noise);

    st1.x = fBm(st + st1 + 0.15 * iTime, noise);
    st1.y = fBm(st + st1 + 0.125 * iTime + vec2(100.0, 12323.0), noise);
    return fBm(st + st1, noise);

}

