mat2 rot(float a)
{
	return mat2(cos(a), sin(a), -sin(a), cos(a));
}

float rep(float x, float r)
{
	return mod(x, r) - r / 2.0;
}

float sdCube(vec3 p, vec3 b) 
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float map(vec3 p)
{
    float rp = .75;
    p.x = rep(p.x, rp);
    p.y = rep(p.y, rp);
    p.z = rep(p.z, rp);
    p.yz *= rot(iTime*0.5);
    p.xz *= rot(iTime*0.5);
	return sdCube(p, vec3(0.1));
}

float raymarch(vec3 ro, vec3 rd, float tmin)
{
	float t = tmin;

    for (int i = 0; i < 128; i++) 
    {
    	float d = map(ro + rd * t);
        if (d < 0.0001*t) {
        	break;
        }
        t += d;
    }

    return t;
}

vec3 raydir(vec2 uv, vec3 camPos, vec3 camTarget)
{
    // Calculate camera's "orthonormal basis", i.e. its transform matrix components
    vec3 camForward = normalize(camTarget - camPos);
    vec3 camRight = normalize(cross(vec3(0.0, 1.0, 0.0), camForward));
    vec3 camUp = normalize(cross(camForward, camRight));

    float fPersp = -2.0;
    return normalize(uv.x * camRight + uv.y * camUp + camForward * fPersp);

}


vec3 calcNormal(vec3 pos)
{
    // Center sample
    float c = map(pos);
    // Use offset samples to compute gradient / normal
    vec2 eps_zero = vec2(0.001, 0.0);
    return normalize(vec3( map(pos + eps_zero.xyy), map(pos + eps_zero.yxy), map(pos + eps_zero.yyx) ) - c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // coordinates (from -0.5 to 0.5)
    vec2 uv = fragCoord/iResolution.xy;
    uv = uv * 2. - 1.;
    uv.x *= iResolution.x / iResolution.y;

    // camera
    vec3 ro = vec3(sin(iTime), 0.0, -iTime);
    vec3 ta = vec3(.0);

    // ray
    vec3 rd = raydir(uv, ro, ta);


    // render
    float d = raymarch(ro, rd, 0.0);
    vec3 p = ro + rd * d;
    vec3 norm = calcNormal(p);
    vec3 col = vec3(1.0-exp(d*0.25)) + norm;

	col = pow(col, vec3(2.0));    
    fragColor = vec4(col,1.0);
}

mat2 rot2d(float a )
{
  float c = cos(a);
  float s = sin(a);
  return mat2(c, -s, s, c);
}

float map(vec3  p)
{
  p.xy = p.xy * rot2d(p.z * 0.2);
  return cos(p.x) + 
  sin(p.y + 1.0) + 
  cos(p.z) + 
  (.05 + 0.42 * 
  sin(iTime)) * 
  cos(p.y + 
  p.z * 
  2.0) + 
  0.1 * 
  cos(p.y);
}

vec3 grad(vec3 p)
{
  vec2 eps = vec2(0.001, 0.0);
  return normalize(vec3(map(p + eps.xyy) - map(p - eps.xyy),
                   map(p + eps.yxy) - map(p - eps.yxy),
                   map(p + eps.yyx) - map(p - eps.yyx)));
}

vec3 rm(vec3 ro, vec3 rd, out float st)
{
    vec3 p = ro;
  for (float i = 0.; i < 64.; ++i)
  {
    float d = map(p);
    if (abs(d) < 0.001)
    {
      st = i;
      break;
    }
    p += d * rd * 0.8;
  }
return p;
}

vec3 shade(vec3 p, vec3 ro, float st, vec3 n)
{
  vec3 color = exp(-distance(p, ro)* 0.1) * (n * 1.1 + 0.5) * pow((float(st) / 4.0), 0.5);
  color = mix(vec3(0.1, 0.7, 1.0), color, exp(-distance(p, ro)* 0.1));
  return color;
}

void mainImage(out vec4 fragColor,in vec2 fragCoord)
{
  vec2 uv=vec2(fragCoord.x/iResolution.x,fragCoord.y/iResolution.y);
  uv-=.5;
  uv/=vec2(iResolution.y/iResolution.x,1);
  vec3 ro = vec3(0.0, 0.0, (iTime*.1) * 8.0);
  vec3 rd = normalize(vec3(uv, 0.7 - length(uv)));
  rd.xy *= rot2d((iTime*.1));
  float st = 0.0;
  vec3 p = rm(ro, rd, st);
  vec3 n = grad(p);
  vec3 color = shade(p, ro, st, n);
  vec3 rd2 = reflect(rd, n);
  vec3 ro2 = p + 0.1 * rd2;
  vec3 p2 = rm(ro2, rd2, st);
  vec3 n2 =  grad(p2);
  vec3 color2 =  shade(p2, ro, st, n2);
  color = mix(color, color2, 0.65);
  fragColor = vec4(color, 1.0);
}

float line(vec2 p,vec2 a,vec2 b,float r,float k)
{
    vec2 pa = p - a;
    vec2 ba = b - a;

    float lba = length(ba);
    vec2  nba = normalize(ba);

    float t = dot(pa,nba);

    float tt = t / lba * 2.0 - 1.0;

    tt = cos(tt * 3.1415926 * k);

    vec2 projd = a + t * nba;

    vec2 hd = normalize(p - projd);

    vec2 npa = normalize(pa);

    float cro = sign(nba.x * npa.y - nba.y * npa.x);

    vec2 curp = projd - hd * tt * r*cro;

    float h = length(curp - p);

    return ((t >= 0.0 && t <= lba)) ? smoothstep(r - 4./iResolution.y,r , h) : 1.0;
}

void calcAnchorInfo(vec2 p,vec2 a,float r,float b,float t,out vec2 c,out vec2 d,out float fb,out float h)
{
	vec2 pa = p - a;
    vec2 up = normalize(vec2(0.0,1.0));
    vec2 right = normalize(vec2(1.0,0.0));

    if(abs(t) < 1.1e-3)
    {
        d = a - up * b,
    	c = a + up * b;

        h = abs(pa.x);
        float l = abs(pa.y);

        fb = l - b;
    }
    else
    {
        t = clamp(t,-3.1415 / 2.0,3.1415 / 2.0);
        vec2 up0 = vec2(sin(t),cos(t));
        right = t >= 0.0 ? right : -right;
        t = abs(t);

        float e = b * sin(t) / t;
        d = a - up * b;
        c = d + up0 * 2.0 * e;
        vec2 m = d + up0 * e;
        vec2 pm = p - m;

        vec2 s0 = d + right * (b / t);

        vec2 ps0 = p - s0;

        ps0 = ps0 - 2.0 * max(dot(ps0,up0),0.0) * up0;

        float lps0 = length(ps0);

        h = abs(lps0 - (b / t));
        fb = -ps0.y;
    }
}

float[] offset1 = float[](0.0,0.01,-0.01,0.03,-0.03);
float[] offset2 = float[](0.0,0.05,-0.05,0.07,-0.07);
float[] offset3 = float[](0.06,0.04,0.04,0.03,0.03);
float[] kt = float[](3.3,2.4,2.0,3.5,4.2);

float hair(vec2 p,vec2 c,vec2 d,float r,mat2 rot,int k)
{
    float t1 = 1.0;


    for(int i = 0;i < 5;i ++)
    {
        if(i >= k) break;
        vec2 o1 = rot * vec2(offset1[i],r) + c;
    	vec2 o2 = rot * vec2(offset2[i],r + offset3[i]) + c;

    	t1 *= line(p,o1,o2,0.005,kt[i]);
    }

    return t1;
}

float smileface(vec2 p,vec2 c,vec2 d,float r,float t,float size,float k)
{
    float oldt = clamp(t,-3.1415 / 2.0,3.1415 / 2.0);
    float h = -0.07;
    float ml = 0.01;
    float t2 = oldt * 2.0;
    float cs = cos(t2);
    float ss = sin(t2);
    mat2 rot = mat2(cs,-ss,ss,cs);

    vec2 reye = rot * vec2(r * 0.5,0.0) + c;
    vec2 leye = rot * vec2(-r * 0.5,0.0) + c;

    vec2 rmo = rot * vec2(-r * 0.5,h) + c;
    vec2 lmo = rot * vec2(r * 0.5,h) + c;

    return hair(p,c,d,r,rot,int(mod(k,6.0)*2.0 - 1.0)) * line(p,rmo,lmo,ml,k) * smoothstep(0.0,r*size,length(reye - p)) * smoothstep(0.0,r*size,length(leye - p));
}


float body(vec2 p,vec2 c,vec2 d,float fb,float h,float r,float size)
{
    return smoothstep(r - 4./iResolution.y,r + size,(fb >= 0.0 ? min(length(p - c),length(p - d)) : h));
}

mat2 rot2(float r)
{
    float s = sin(r),c = cos(r);
    return mat2(c,-s,s,c);
}

