#define PI 3.14159265359

float N11(float i) {
	return fract(sin(i + 93741.369) * 31934.123);
}

float N21(vec2 i) {
	return N11(N11(i.x) + i.y);
}

float smoothNoise(vec2 uv, float z) {
    vec2 id = floor(uv * z);
    vec2 gv = fract(uv * z);

    float tl = N21(id);
    float tr = N21(id + vec2(1., 0.));
    float bl = N21(id + vec2(0., 1.));
    float br = N21(id + vec2(1., 1.));

    float t = mix(tl, tr, smoothstep(0., 1., gv.x));
    float b = mix(bl, br, smoothstep(0., 1., gv.x));
    return  mix(t, b, smoothstep(0., 1., gv.y));
}

float graph(vec2 uv, float n) {
    float t = iTime * .01;
    uv -= vec2(.5);
    float a = atan(uv.y, uv.x) / PI * .5 + .5;
    float r = distance(uv, vec2(0.));
    float noise = smoothNoise(vec2(a, r + t) + vec2(N11(n), N11(N11(n))), 10.) * 10. * PI;
    return smoothstep(.9, .8, sin(r * 128. + noise + n) *.5 + .5);	
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy - vec2(.5);
    uv.x *= iResolution.x / iResolution.y;
    uv *= 1.5;

    // Time varying pixel color
	vec3 col = vec3(uv.rg, 0.);
    float m = 0.;

    float z = 2.;
    vec2 id = floor(uv * z);
    vec2 gv = fract(uv * z);

    m = graph(gv, N21(id));
    if (gv.x >= 0.98 || gv.y >=.98) m = 0.;


    // m = smoothNoise(uv, z);

	col = vec3(m);
    // Output to screen
    fragColor = vec4(col,1.0);
}

