float ball(vec2 p) {
    float size = .05;
    float d = distance(vec2(.5), p);
	return smoothstep(size,size - .05, d);
}


float N11(float n) {
	return fract(sin(n * 871.213) * 3134.422);
}

float N21(vec2 uv) {
	return N11(N11(uv.x) + uv.y);
}

float snow(vec2 uv, float t) {
    vec2 org_uv = vec2(uv.x, uv.y);
    float z = 10.;
    uv.y += t * .5;
    vec2 gv = fract(uv*z);
    vec2 id = floor(uv*z); 
	gv.x += (sin(N21(id) * 128. + t) * .4);
    gv.y += (sin(N11(N21(id)) * 128. + t) * .4);
    // float size = graph(org_uv);
    float dots = ball(gv);
    return dots;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	uv.x *= iResolution.x / iResolution.y;
    float t = iTime * .3;

    vec2 gh_uv = uv;

    // Time varying pixel color
    vec3 col = vec3(0.);

    float m = 0.;

    for(float i =0.; i <= 1.; i += 1. / 32.) {
        float z = mix(1., .5 , i);
        vec2 offset = vec2(N11(i), N11(N11(i)));
    	m += snow((uv + offset) * z, t) * .3;
    }


    col = vec3(m);
    col += vec3(.85, .90, 1.) *.8 * mix(.5, 1., uv.y);

    // Output to screen
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //--------------------------------------------------
    // Center cardinal coordinate system with square resolution:
    vec2 uv =  ( fragCoord -0.5 * iResolution.xy )/ iResolution.y;

    //-------------------------------
    // Set up polar coordinate system:
    float r = length(uv)*2.0; // *2 so top/bottom of screen is +-1, not +-1/2
    float t = atan(uv.y,uv.x);
    vec2 polar = vec2(r,t);

    //-------------------------
    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+polar.xyx+vec3(0,2,4));

    //-----------------
    // Output to screen
    fragColor = vec4(col,1.0);
}

// implementing a subset of "A Low-Distortion Map Between Triangle and Square"
// techreport: https://eheitzresearch.wordpress.com/749-2/

#define D 2000.                               // density
#define HUE 0                                 // 1: display Voronoi / colored splats

#define hash2(i)  fract(sin( i * vec2(12.9898, 78.233) ) * 43758.5453)
#define hue(v)  ( .6 + .6 * cos( 6.3*(v)  + vec4(0,23,21,0)  ) )

void mainImage( out vec4 O, vec2 u )
{
    float t = 1.*iTime;           
    bool  blue  = fract(t/12.) > .5 ,         // sample white vs blue noise
          splat = fract(t/12.+.25) > .5;      // display dots vs splats

    vec2  R = iResolution.xy,
          m = iMouse.xy,
          U = ( 2.* u - R ) / R.y, P,
         P0 = vec2(-1), P0U = P0-U,           // triangle 3 vertices
         P1 = vec2(.5,0) +.5*cos(t+vec2(0,11)), 
         P2 = length(m)<20. ? vec2(0,.5) : (2.*m-R)/R.y;

    mat2  M = mat2( P1-P0, P2-P0 );           // triangle frame
    float s = abs(determinant(M))/2.,         // triangle area
          d = 1e5, v,k, N = D*s, l = sqrt(N);

    for( float i = 1.; i <= N; i++) {
        P = hash2(i);                         // uniform hash on canonical square
        if (blue) P = vec2(fract(i/l),i/N) + (P-.5)/l; // blue noise case
        if (P.y > P.x) P.y -= P.x *= .5;      // square-triangle iso-mapping
        else           P.x -= P.y *= .5;
      //int c = int(P.y > P.x); P[c] -= P[1-c] *= .5; // more expensive
      //if (P.x+P.y > 1.) P = 1.-P;           // simpler but not bijective (required for bluenoise or stratified)

      //P = P0 + P.x*(P1-P0) + P.y*(P2-P0);   // barycentric coordinates
      //d = min(d, dot(P-U,P-U) );            // get sample closest to cur pixel
      //                                      Optimized version:
        P = P0U + M*P;                        // barycentric coordinates
#if !HUE
        if (!splat) d = min(d, dot(P,P) );    // get sample closest to cur pixel
        else    O += max( 0., 1. -.3*sqrt(D)*length(P) ) / 16.; // splats
#else
        if (!splat) { v = dot(P,P); if (v<d) d=v,k=i; }         // closest id (for Voronoi)
        else    O += max( 0., 1. -.3*sqrt(D)*length(P) ) / 16.  // splats (colored)
                    *hue(i*331.7)*1.5; 
#endif
    }

    if (!splat) O += smoothstep(3./R.y,0.,sqrt(d)-.002); // draw dot
#if HUE
    if (!splat && d<2./D) O = hue(hash2(k+.5).x);        // draw Voronoi
#else
    O = pow(O, vec4(1./2.2));                            // to sRGB
#endif
}

#define UI0 1597334673U
#define UI1 3812015801U
#define UI2 uvec2(UI0, UI1)
#define UI3 uvec3(UI0, UI1, 2798796415U)
#define UIF (1.0 / float(0xffffffffU))

#define SCALE 12.
#define PURPLE (vec3(92., 25., 226.)/255.)

const vec3[3] colors = vec3[](
						vec3(92., 197., 187.)/255., // cyan
    					vec3(240., 221., 55.)/255., // yellow
    					vec3(253., 87., 59.)/255.); // red

// Hash by Dave_Hoskins
float hash12(vec2 p)
{
	uvec2 q = uvec2(ivec2(p)) * UI2;
	uint n = (q.x ^ q.y) * UI0;
	return float(n) * UIF;
}

// Distance function by iq
float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,vec2(0))) + min(max(d.x,d.y),0.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.y;
    // first set of grid uv
    vec2 auv = uv * SCALE;
    vec2 _auv = fract(auv);
    // second set of grid uv offset by first grid uv center
    vec2 buv = uv * SCALE - .5;
    vec2 _buv = fract(buv);
    // time factor
    float t = iTime;

    vec3 col = vec3(0.);

    // random color index for each cell in the grid
    float ah = hash12(floor(auv + 647.));
    // rounded box for first grid uv
    float abox = smoothstep(.1, .05, sdBox(_auv - .5, vec2(.305)) - .12)
		* (.75 + .25 * sin(t + 588. * ah)) * 1.1 + .1;
    // box color oscillating between light and dark
    vec3 aboxCol = colors[int(3. * hash12(floor(auv) + 378. + t * .4))];
    // random number for each second grid cell
    float bh = hash12(floor(buv + 879.));
    // rounded box for the second offset grid
    float bbox = smoothstep(.1, .05, sdBox(_buv - .5, vec2(.305)) - .12)
        * (.75 + .25 * sin(t + 261. * bh)) * 1.1 + .1;
    // oscillate the color, but give it a darker shade than the first grid,
    // which in turn offsets the color in the first grid
    vec3 bboxCol = colors[int(3. * hash12(floor(buv) + 117. - t * .8))];

    // mix grid box colors based on their respective alpha
    col = mix(col, vec3(abox) * aboxCol, abox);
    col = mix(col, vec3(bbox) * bboxCol, .5 * bbox);
    col = mix(col * 1.25, PURPLE, 1. - (abox + bbox) * .5); // purple bg

    fragColor = vec4(col, 1.0);
}

#define CR00 -0.5
#define CR01 1.5
#define CR02 -1.5
#define CR03 0.5
#define CR10 1.0
#define CR11 -2.5
#define CR12 2.0
#define CR13 -0.5
#define CR20 -0.5
#define CR21 0.0
#define CR22 0.5
#define CR23 0.0
#define CR30 0.0
#define CR31 1.0
#define CR32 0.0
#define CR33 0.0

#define PALE_BLUE vec3(0.25,0.25,0.35)
#define MEDIUM_BLUE vec3(0.10,0.10,0.30)
#define DARK_BLUE vec3(0.05,0.05,0.26)
#define DARKER_BLUE vec3(0.03,0.03,0.20)

#define Ambient vec3(0.1,0.1,0.1)

#define NearClipPlaneDist 2.0

#define AA 2

float hash31(vec3 p)
{
    p  = fract( p*0.3183099+.1 );
	p *= 17.0;
    return fract( p.x*p.y*p.z*(p.x+p.y+p.z) );
}

float noise3d( in vec3 x )
{
    vec3 i = floor(x);
    vec3 f = fract(x);
	f = f*f*f*(6.0*f*f - 15.0*f + 10.0);
    return mix(mix(mix( hash31(i+vec3(0,0,0)), 
                        hash31(i+vec3(1,0,0)),f.x),
                   mix( hash31(i+vec3(0,1,0)), 
                        hash31(i+vec3(1,1,0)),f.x),f.y),
               mix(mix( hash31(i+vec3(0,0,1)), 
                        hash31(i+vec3(1,0,1)),f.x),
                   mix( hash31(i+vec3(0,1,1)), 
                        hash31(i+vec3(1,1,1)),f.x),f.y),f.z);
}

float marble(vec3 p){
    float m = 0.0;
    float f = 1.0;
    for(float i = 0.0; i < 4.0; i += 1.0){
        m += noise3d(p*f)/f;
        f*=2.17;
    }
    return m;
}

//æ ¹æ®må¨colors[1]å°colors[n-1]ä¹é´è¿è¡æ ·æ¡çº¿æå¼
vec3 marbleColor(float m){
    m = clamp(0.0,1.0,m);
    float nspan = 10.0;
    vec3 colors[13];
    colors[0]=PALE_BLUE;
    colors[1]=PALE_BLUE;
    colors[2]=MEDIUM_BLUE;
    colors[3]=MEDIUM_BLUE;
    colors[4]=MEDIUM_BLUE;
    colors[5]=PALE_BLUE;
    colors[6]=PALE_BLUE;
    colors[7]=DARK_BLUE;
    colors[8]=DARK_BLUE;
    colors[9]=DARKER_BLUE;
    colors[10]=DARKER_BLUE;
    colors[11]=PALE_BLUE;
    colors[12]=DARKER_BLUE;
    colors[12]=DARKER_BLUE;

    m = clamp(0.0,1.0,m)*nspan;
    float span = floor(m);
    if(span>=nspan){
        span = nspan - 1.0;
    }
    m -= span;
    int p0 = int(span);
    int p1 = int(span+1.0);
    int p2 = int(span+2.0);
    int p3 = int(span+3.0);
    vec3 c3 = CR00*colors[p0] + CR01*colors[p1] + CR02*colors[p2] + CR03*colors[p3];
    vec3 c2 = CR10*colors[p0] + CR11*colors[p1] + CR12*colors[p2] + CR13*colors[p3];
    vec3 c1 = CR20*colors[p0] + CR21*colors[p1] + CR22*colors[p2] + CR23*colors[p3];
    vec3 c0 = CR30*colors[p0] + CR31*colors[p1] + CR32*colors[p2] + CR33*colors[p3];
    return ((c3*m+c2)*m+c1)*m+c0;
}

