vec2 map( in vec3 pos )
{
    vec2 res = vec2(1e10, 0.0);
    res = opU(res, vec2(sdBox(pos, vec3(1.5,0.1,1.0)),1));
    //res = opU(res, vec2(sdSphere(pos + vec3(0,-1,0), 1.0),1));
    return res;
