vec2 castRay( in vec3 ro, in vec3 rd )
{
    vec2 res = vec2(-1.0,-1.0);

    float tmin = 1.0;
    float tmax = 20.0;

    float t = tmin;
    for( int i = 0; i < 70 && t < tmax; i++ )
    {
        vec2 h = map( ro+rd*t );
        if( abs(h.x)<(0.0001*t) )
        { 
            res = vec2(t,h.y); 
            break;
        }
        t += h.x;
    }

    return res;
}

vec3 render(in vec3 ro, in vec3 rd)
{
    vec2 res = castRay(ro,rd);
    float t = res.x;
	float m = res.y; 
    vec3 pos = ro + t * rd;
    vec3 normal = vec3(0,0,0);
    vec3 col = vec3(0,0,0); 
    if(m <= -1.0)//skyBox
    {
        col = Ambient;
    } else {
        float m = marble(pos*2.0);
        m = 0.5*m;
        col = marbleColor(m);
    }
    return col;
}

mat3 getViewMatrix(in vec3 cameraPos, in vec3 targetPos)
{
    vec3 w = normalize(targetPos - cameraPos);
    vec3 up = vec3(0,1,0);
    vec3 u = normalize(cross(w, up));
    vec3 v = normalize(cross(u,w));
    return mat3(u,v,w);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 mousePos = iMouse.xy / iResolution.xy - vec2(0.5,0.5);
    float time = iTime * 0.1;
    vec3 cameraPos = vec3(3.0 * cos(time + 6.0*mousePos.x), 2.0 + 2.0*mousePos.y, 3.0 * sin(time + + 6.0*mousePos.x));

    vec3 lookAtPos = vec3(0,0,0);
    mat3 viewMatrix = getViewMatrix(cameraPos, lookAtPos);

    vec3 sum = vec3(0.0);
    for(int i = 0;i<AA;i++){
        for(int j = 0;j<AA;j++){
             vec2 diff = vec2(float(i),float(j))/float(AA)-0.5;
             vec2 pixelPosInCameraCoor = (-iResolution.xy + 2.0*(fragCoord+diff))/iResolution.y;
             vec3 rayDir = viewMatrix * normalize(vec3(pixelPosInCameraCoor.xy, NearClipPlaneDist));
             vec3 col = render(cameraPos, rayDir);
             col = pow( col, vec3(0.4545) );
             sum += col;
        }
    }
    sum /= float(AA*AA);
    // Output to screen
    fragColor = vec4(sum, 1);
}

/*
Shader coded live on Recondustream twitch (https://www.twitch.tv/recondustream)
This twitch channel is a caritative initiative to support the current french strike.
The shader was made using Bonzomatic.
You can find the original shader here: http://lezanu.fr/LiveCode/Recondustream_Nusan.glsl
This version is tweaked to better fit to avalaible textures on shadertoy
*/

float time=0.0;

#define rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))
#define rep(v,s) (fract((v)/s-0.5)-0.5)*s

float box(vec2 p, vec2 s) {
  p=abs(p)-s;
  return length(max(vec2(0),p)) + min(0.0,max(p.x, p.y));
}

float box(vec3 p, vec3 s) {
  p=abs(p)-s;
  return length(max(vec3(0),p)) + min(0.0,max(p.x, max(p.y,p.z)));
}

float sph(vec3 p, float r) {
  return length(p)-r;
}

float cyl(vec2 p, float r) {
  return length(p)-r;
}

float prog=0.0;
float voiture(vec3 p) {

  vec3 p2=p;
  vec3 p3=p;

  float d = box(p, vec3(1.5,2.5,4));

  p.y += 2.5;
  d = min(d, max(cyl(p.xy, 1.5), abs(p.z)-4.0));

  p.y = abs(p.y-1.5)-1.5;
  d = min(d, box(p, vec3(1.6,0.1,4.1)));

  p2.x = abs(p.x)-1.6;
  p2.z = rep(p.z, 1.6);
  p2.y += 1.4;
  d = max(d, -box(p2, vec3(0.2,0.7,0.7)));

  p3.z = abs(p3.z)-4.0;
  p3.y += 1.0;
  d = max(d, -box(p3, vec3(0.6,1.2,0.2)));


  return d;
}

float roues(vec3 p) {


  p.y -= 3.0;
  p.x = abs(p.x)-1.8;
  p.z = min(3.0+p.z,max(p.z-3.0,rep(p.z, 1.5)));
  float d = abs(cyl(p.yz, 0.65))-0.1;
  d = max(d, abs(p.x)-0.2);

  p.yz *= rot(time * 4.0);

  p.y=abs(p.y);
  p.z=-abs(p.z);
  p.yz *= rot(0.7);
  p.y=abs(p.y);
  p.z=-abs(p.z);
  p.yz *= rot(0.4);
  d = min(d, box(p, vec3(0.05,0.05,0.7)));


  return d;
}

float rails(vec3 p) {

  p.y += 0.2;
  p.x=abs(p.x)-1.6;
  float d=box(p.xy, vec2(0.1,0.2));

  p.z = rep(p.z, 1.5);
  d=min(d, box(p, vec3(2.0,0.1, 0.3)));

  return d;
}

vec3 chemin(vec3 p) {
  vec3 off=vec3(0);
  off.x += sin(p.z * 0.04)*10.0;
  off.x += sin(p.z * 0.023)*22.0;
  off.y += sin(p.z * 0.03)*10.0;
  return off;
}

