  p.y += 4.2;
  float tt=time*0.5;
  float t2 = fract(tt);
  t2 = smoothstep(0.0,1.0,t2);
  t2 = smoothstep(0.0,1.0,t2);
  t2 = pow(t2, 10.0);
  p.z += (floor(tt) + t2) * 30.0 + time*3.0; 
  prog = p.z;
  p.z = rep(p.z, 9.5);




  voit=voiture(p);
  float d = voit;

  d=min(d, roues(p));

  d=min(d, rails(p2));

  sol=(-p2.y - h + 0.7)*0.7;
  d=min(d, sol);

  wat=-p3.y+5.0;
  d=min(d, wat);

  return d;
}

vec3 camera(vec2 uv, vec3 s, vec3 t, float fov) {

  vec3 cz=normalize(t-s);
  vec3 cx=normalize(cross(cz, vec3(0,1,0)));
  vec3 cy=normalize(cross(cx, cz));

  return normalize(uv.x*cx - uv.y*cy + fov*cz);
}

vec3 norm(vec3 p) {
  vec2 off = vec2(0.01,0.0);
  return normalize(map(p)-vec3(map(p-off.xyy), map(p-off.yxy), map(p-off.yyx)));
}

float shadow(vec3 p, vec3 r) {
  float shad=1.0;
  for(int i=0; i<30; ++i) {
    float d=map(p);
    shad=min(shad, d*10.0);
    p+=d*r;
  }
  return clamp(shad,0.0,1.0);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
  vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
  uv -= 0.5;
  uv /= vec2(iResolution.y / iResolution.x, 1);

  time=mod(iTime,300.0);

  // camera position
  vec3 s=vec3(0,0,-30);
  s.yz *= rot(0.5);
  s.xz *= rot(0.7+time*0.2);

  // camera target
  vec3 t=vec3(0,-7,10);
  t.xz *= rot(time*0.3);

  s.z += time * 8.0;
  t.z += time * 8.0;

  s -= chemin(s);
  t -= chemin(t);

  //float fov = 0.5 + pow(smoothstep(-1.0,1.0,sin(time*10.0)),10.0)*0.2;
  float fov = 0.7;
  vec3 r = camera(uv, s, t, fov);

  vec3 p=s;
  float at=0.0;
  float dd=0.0;
  for(int i=0; i<100; ++i) {
    float d=map(p) * 0.7;
    if(d<0.001) {
      if(wat>0.01) {
        break;
      }
      d = 0.1;
      r.y = -r.y;
      r.x += (texture(iChannel0, p.xz*0.008 + time*0.03).x-0.4)*0.5;
      r=normalize(r);
    }
    if(dd>200.0) {
      break;
    }

    p+=r*d;
    dd+=d;
    at += 0.1/(0.1+abs(d));
  }

  float issol=step(sol,0.01);
  float isvoit=step(voit,0.01);
  float iswat=step(wat,0.01);
  float pp=floor(prog/9.5-0.5);
  vec3 n=norm(p);

  float fog = 1.0-clamp(dd/200.0,0.0,1.0);

  vec3 col=vec3(0);

  //col += at * 0.03 * vec3(1,0.5,0.3);

  vec3 l=normalize(-vec3(1,2,3));

  float shad=shadow(p + n * 0.2, l);

  float ao = clamp(map(p+n*0.2)/0.2,0.0,1.0) * (clamp(map(p+n*2.0)/2.0,0.0,1.0)*0.5+0.5);

  vec3 diff=vec3(0.7);
  diff=mix(diff, vec3(0.5,1.0,0.5), issol);
  vec3 cc=vec3(1,0.5,0.2);
  float g=floor(pp);
  cc.xy *= rot(g);
  cc.xz *= rot(g*0.7);
  cc=abs(cc);
  diff=mix(diff, cc, isvoit);
  col += max(0.0, dot(n,l)) * diff * shad * ao;

  vec3 sky = mix(vec3(0.5,0.6,1)*0.8, vec3(1.0,0.5,0.2)*10.0 , pow(max(0.0,dot(l,r)), 3.0));
  col += (-n.y*0.5+0.5) * diff * 1.2 * ao;

  col *= fog;

  col += pow(1.0-fog,3.0) * 1.1 * sky;

  col *= 1.2-length(uv);

  col = smoothstep(0.0,1.0,col*1.2);
  col = pow(col, vec3(0.4545));

  col *= 1.0;
  fragColor = vec4(col, 1);
}

