float PrSphDf (vec3 p, float r);
float PrRoundCylDf (vec3 p, float r, float rt, float h);
vec2 PixToHex (vec2 p);
vec2 HexToPix (vec2 h);
vec3 HsvToRgb (vec3 c);
mat3 StdVuMat (float el, float az);
float Minv3 (vec3 p);
float SmoothMin (float a, float b, float r);
float SmoothBump (float lo, float hi, float w, float x);
vec2 Rot2D (vec2 q, float a);
float Hashfv2 (vec2 p);
float Fbm2 (vec2 p);
vec3 VaryNf (vec3 p, vec3 n, float f);

vec3 ltDir;
vec2 gId, slcLim;
float dstFar, tCur, tLoc, rBase, rTube, sLoopV[2], rLoop, nArm, armUp, armId,
   hgSize, bbHt, bbRad, bRot, szFac, hVal;
int idObj;
const int idArm = 1, idArmE = 2, idCylL = 3, idCylU = 4, idBub = 5;
const float pi = 3.14159, sqrt3 = 1.7320508;

#define DMIN(id) if (d < dMin) { dMin = d;  idObj = id; }

void SetObjConf ()
{
  float tCyc;
  hVal = Hashfv2 (37. * gId + 11.11);
  tCyc = 6. + 4. * hVal;
  bRot = 2. * pi * hVal;
  tLoc = tCur + tCyc * hVal;
  nArm = 5. + floor (4. * hVal);
  szFac = (6. + 4. * hVal) / 10.;
  armUp = floor (mod (tLoc / tCyc, nArm));
  sLoopV[0] = 0.35 + 20. * pow (1. - SmoothBump (0.25, 0.75, 0.22, mod (tLoc / tCyc, 1.)), 8.);
  sLoopV[1] = 0.35 + 20.;
  slcLim = - sin (pi / nArm + vec2 (0.5 * pi, 0.));
  bbHt = 1. + mod (3. * tCur + 0.5 * hVal * dstFar, 0.5 * dstFar);
  bbRad = 0.03 * (1. + hVal);
}

float ObjDf (vec3 p)
{
  vec3 q;
  vec2 arcLim;
  float dMin, d, db, s, r, aLoop, dLoop;
  dMin = dstFar;
  if (hVal > 0.03) {
    dMin /= szFac;
    p.xz -= HexToPix (gId * hgSize);
    p /= szFac;
    p.y -= rTube + 0.15;
    p.xz = Rot2D (p.xz, bRot);
    q = p;
    q.xz = Rot2D (q.xz, (2. * pi / nArm) * armUp);
    db = dot (vec2 (abs (q.z), - q.x), slcLim);
    dLoop = rLoop * sLoopV[0];
    aLoop = 0.25 * pi / sLoopV[0];
    arcLim = sin (0.25 * pi / sLoopV[0] + vec2 (0., 0.5 * pi));
    q = p;
    q.xz = Rot2D (q.xz, (2. * pi / nArm) * armUp);
    q.xy = Rot2D (vec2 (rBase - q.x, q.y - dLoop), aLoop - 0.5 * pi);
    s = mod ((32. / aLoop) * atan (q.y, - q.x) / pi + 0.5 - 2. * tLoc, 1.);
    r = length (vec2 (length (q.xy) - dLoop, q.z)) - rTube;
    d = max (r - 0.03 * SmoothBump (0.25, 0.75, 0.15, s), - r - 0.15);
    d = max (d, dot (vec2 (q.x, abs (q.y)), arcLim));
    if (d < dMin) armId = armUp;
    DMIN (idArm);
    q.xy = Rot2D (q.xy, aLoop) - vec2 (- dLoop, 0.27);
    d = max (PrRoundCylDf (q.xzy, rTube + 0.03, 0.1, 0.3), rTube - 0.15 - length (q.xz));
    if (d < dMin) armId = armUp;
    DMIN (idArmE);
    dLoop = rLoop * sLoopV[1];
    aLoop = 0.25 * pi / sLoopV[1];
    arcLim = sin (0.25 * pi / sLoopV[1] + vec2 (0., 0.5 * pi));
    q = p;
    if (mod (nArm, 2.) != 0.) q.xz = Rot2D (q.xz, pi / nArm);
    q.xz = Rot2D (q.xz, 2. * pi * (floor (nArm * atan (q.z, - q.x) / (2. * pi) + 0.5) / nArm));
    q.xy = Rot2D (vec2 (rBase + q.x, q.y - dLoop), aLoop - 0.5 * pi);
    s = mod ((32. / aLoop) * atan (q.y, - q.x) / pi + 0.5 + 0.5 * tLoc, 1.);
    r = length (vec2 (length (q.xy) - dLoop, q.z)) - rTube;
    d = max (r - 0.03 * SmoothBump (0.25, 0.75, 0.15, s), - r - 0.15);
    d = max (max (d, dot (vec2 (q.x, abs (q.y)), arcLim)), db);
    if (d < dMin) armId = -1.;
    DMIN (idArm);
    q.xy = Rot2D (q.xy, aLoop) - vec2 (- dLoop, 0.27);
    d = max (max (PrRoundCylDf (q.xzy, rTube + 0.03, 0.1, 0.3),
       rTube - 0.15 - length (q.xz)), db);
    if (d < dMin) armId = -1.;
    DMIN (idArmE);
    q = p;
    d = PrRoundCylDf (q.xzy, rBase + 0.05, 0.15, rTube + 0.1);
    DMIN (idCylL);
    q = p;
    q.y -= 3. * rTube;
    d = SmoothMin (d, PrRoundCylDf (q.xzy, (0.7 - 0.2 * (q.y / (3. * rTube))) * rBase,
       0.1, 3. * rTube), 0.1);
    DMIN (idCylU);
    q = p;
    q.y -= bbHt;
    d = PrSphDf (q, bbRad);
    DMIN (idBub);
    dMin *= 0.8 * szFac;
  }
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  vec3 vri, vf, hv, p;
  vec2 edN[3], pM, gIdP;
  float dHit, d, s, eps;
  eps = 0.0005;
  edN[0] = vec2 (1., 0.);
  edN[1] = 0.5 * vec2 (1., sqrt3);
  edN[2] = 0.5 * vec2 (1., - sqrt3);
  for (int k = 0; k < 3; k ++) edN[k] *= sign (dot (edN[k], rd.xz));
  vri = hgSize / vec3 (dot (rd.xz, edN[0]), dot (rd.xz, edN[1]), dot (rd.xz, edN[2]));
  vf = 0.5 * sqrt3 - vec3 (dot (ro.xz, edN[0]), dot (ro.xz, edN[1]),
     dot (ro.xz, edN[2])) / hgSize;
  pM = HexToPix (PixToHex (ro.xz / hgSize));
  gIdP = vec2 (-99.);
  dHit = 0.;
  for (int j = 0; j < 160; j ++) {
    hv = (vf + vec3 (dot (pM, edN[0]), dot (pM, edN[1]), dot (pM, edN[2]))) * vri;
    s = Minv3 (hv);
    p = ro + dHit * rd;
    gId = PixToHex (p.xz / hgSize);
    if (gId.x != gIdP.x || gId.y != gIdP.y) {
      gIdP = gId;
      SetObjConf ();
    }
    d = ObjDf (p);
    if (dHit + d < s) {
      dHit += d;
    } else {
      dHit = s + eps;
      pM += sqrt3 * ((s == hv.x) ? edN[0] : ((s == hv.y) ? edN[1] : edN[2]));
    }
    if (d < eps || dHit > dstFar || p.y < 0.) break;
  }
  if (d >= eps) dHit = dstFar;
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.0002, -0.0002);
  v = vec4 (- ObjDf (p + e.xxx), ObjDf (p + e.xyy), ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  vec3 p;
  vec2 gIdP;
  float sh, d, h;
  sh = 1.;
  d = 0.03;
  gIdP = vec2 (-99.);
  for (int j = 0; j < 30; j ++) {
    p = ro + d * rd;
    gId = PixToHex (p.xz / hgSize);
    if (gId.x != gIdP.x || gId.y != gIdP.y) {
      gIdP = gId;
      SetObjConf ();
    }
    h = ObjDf (p);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += clamp (h, 0.05, 0.5);
    if (sh < 0.05) break;
  }
  return 0.7 + 0.3 * sh;
}

float RippleHt (vec2 p)
{
  vec2 q;
  float s1, s2;
  q = Rot2D (p, -0.02 * pi);
  s1 = abs (sin (4. * pi * abs (q.x + 1.5 * Fbm2 (0.5 * q))));
  s1 = (1. - s1) * (s1 + sqrt (1. - s1 * s1));
  q = Rot2D (p, 0.01 * pi);
  s2 = abs (sin (3.1 * pi * abs (q.x + 1.9 * Fbm2 (0.3 * q))));
  s2 = (1. - s2) * (s2 + sqrt (1. - s2 * s2));
  return mix (s1, s2, 0.1 + 0.8 * smoothstep (0.3, 0.7, Fbm2 (2. * p)));
}

vec4 RippleNorm (vec2 p, vec3 vn, float f)
{
  vec2 e = vec2 (0.002, 0.);
  float h;
  h = RippleHt (p);
  vn.xy = Rot2D (vn.xy, f * (RippleHt (p + e) - h));
  vn.zy = Rot2D (vn.zy, f * (RippleHt (p + e.yx) - h));
  return vec4 (vn, h);
}

vec3 BgCol (vec3 rd)
{
  float t, gd, b;
  t = 2. * tCur;
  b = dot (vec2 (atan (rd.x, rd.z), 0.5 * pi - acos (rd.y)), vec2 (2., sin (rd.x)));
  gd = clamp (sin (5. * b + t), 0., 1.) * clamp (sin (3.5 * b - t), 0., 1.) +
     clamp (sin (21. * b - t), 0., 1.) * clamp (sin (17. * b + t), 0., 1.);
  return mix (vec3 (0.1, 0.7, 0.6), vec3 (0.3, 0.5, 1.), 0.5 + 0.5 * rd.y) *
     (0.24 + 0.44 * (rd.y + 1.) * (rd.y + 1.)) * (1. + 0.1 * gd);
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4, vn4;
  vec3 p, col, c, bgCol, vn;
  float dstObj, s, sh;
  bool isBg, isLit;
  rTube = 0.3;
  rBase = 1.8;
  rLoop = 5.;
  dstObj = ObjRay (ro, rd);
  isBg = false;
  isLit = false;
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    p = ro;
    gId = PixToHex (p.xz / hgSize);
    SetObjConf ();
    p.xz -= HexToPix (gId * hgSize);
    c = HsvToRgb (vec3 (mod (37.3 * hVal, 1.), 0.2, 1.));
    if (idObj == idArm) {
      col4 = vec4 (0.9, 0.9, 1., 0.);
    } else if (idObj == idArmE) {
      if (armId == armUp) col4 = vec4 (0.7 + 0.3 * sin (5. * tLoc), 0., 0., -1.);
      else col4 = vec4 (c, 0.);
    } else if (idObj == idCylL) {
      col4 = vec4 (0.8 * c, 0.);
    } else if (idObj == idCylU) {
      col4 = vec4 (c, 0.);
      s = length (p.xz) - rTube - 0.2;
      if (s < 0.) {
        if (s < -0.1) col4 = vec4 (vec3 (0.6 + 0.4 * sin (5. * tLoc), 0., 0.), -1.);
        else col4.rgb *= 0.7;
      }
    } else if (idObj == idBub) {
      col4 = vec4 (mix (vec3 (1.2), BgCol (reflect (rd, vn)), 0.5), -1.);
    }
    if (col4.a >= 0.) isLit = true;
  } else if (rd.y < 0. && (- ro.y / rd.y) < dstFar) {
    dstObj = - ro.y / rd.y;
    ro += dstObj * rd;
    col4 = mix (vec4 (0.3, 0.4, 0.3, 0.), vec4 (0.2, 0.3, 0.3, 0.),
       0.3 + 0.4 * smoothstep (0.4, 0.6, Fbm2 (4. * ro.xz))) * (1. +
       0.2 * smoothstep (0.3, 0.7, Fbm2 (0.5 * ro.xz)));
    vn4 = RippleNorm (0.1 * ro.xz, vec3 (0., 1., 0.), 6.);
    vn = VaryNf (2. * ro, vn4.xyz, 4. - 3.5 * smoothstep (0.2, 0.5, dstObj / dstFar));
    col4 *= 0.9 + 0.1 * smoothstep (0.1, 0.3, vn4.w);
    isLit = true;
  } else isBg = true;
  bgCol = BgCol (rd);
  if (isBg) {
    col = bgCol;
  } else {
    if (isLit) {
      sh = min (ObjSShadow (ro, ltDir), 1. - 0.2 * smoothstep (0.4, 0.6,
         Fbm2 (0.1 * ro.xz + 0.2 * tCur)));
      col = col4.rgb * (0.3 * bgCol + 0.7 * sh * max (dot (vn, ltDir), 0.));
    } else {
      col = col4.rgb * (0.3 + 0.7 * max (- dot (vn, rd), 0.));
    }
    col = mix (col, bgCol, smoothstep (0.3, 1., dstObj / dstFar));
  }
  col = pow (clamp (col, 0., 1.), vec3 (1.1, 1.1, 0.9));
  return clamp (col, 0., 1.);
}

#define AA  0   // optional antialiasing

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv;
  float el, az, zmFac, sr;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  hgSize = 12.;
  az = 0.;
  el = -0.1 * pi;
  if (mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    el += 0.3 * pi * mPtr.y;
  } else {
    az += 0.4 * pi * (2. * mod (floor (0.015 * tCur), 2.) - 1.) *
       SmoothBump (0.3, 0.7, 0.15, mod (0.015 * tCur, 1.));
    el -= 0.05 * pi * sin (0.017 * pi * tCur);
  }
  vuMat = StdVuMat (el, az);
  ro = vec3 (0., 20., 5. + 1.5 * tCur);
  zmFac = 3.;
  dstFar = 150.;
  ltDir = vuMat * normalize (vec3 (1., 2., -1.));
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = 0.; a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), zmFac));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (col, 1.);
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

float PrRoundCylDf (vec3 p, float r, float rt, float h)
{
  float dxy, dz;
  dxy = length (p.xy) - r;
  dz = abs (p.z) - h;
  return min (min (max (dxy + rt, dz), max (dxy, dz + rt)), length (vec2 (dxy, dz) + rt) - rt);
}

vec2 PixToHex (vec2 p)
{
  vec3 c, r, dr;
  c.xz = vec2 ((1./sqrt3) * p.x - (1./3.) * p.y, (2./3.) * p.y);
  c.y = - c.x - c.z;
  r = floor (c + 0.5);
  dr = abs (r - c);
  r -= step (dr.yzx, dr) * step (dr.zxy, dr) * dot (r, vec3 (1.));
  return r.xz;
}

vec2 HexToPix (vec2 h)
{
  return vec2 (sqrt3 * (h.x + 0.5 * h.y), (3./2.) * h.y);
}

vec3 HsvToRgb (vec3 c)
{
  return c.z * mix (vec3 (1.), clamp (abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.) - 1., 0., 1.), c.y);
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

float Minv3 (vec3 p)
{
  return min (p.x, min (p.y, p.z));
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b, a, h) - r * h * (1. - h);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

const float cHashM = 43758.54;

float Hashfv2 (vec2 p)
{
  return fract (sin (dot (p, vec2 (37., 39.))) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int i = 0; i < 3; i ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  vec2 e;
  e = vec2 (0.1, 0.);
  g = vec3 (Fbmn (p + e.xyy, n), Fbmn (p + e.yxy, n), Fbmn (p + e.yyx, n)) - Fbmn (p, n);
  return normalize (n + f * (g - n * dot (n, g)));
}

// Ecosphere - the procedural creation of a planet like sphere.
//
// Patrik Sandahl, 2019 - patrik.sandahl@gmail.com
//
// 3D noise algorithm by Ian McEwan, Ashima Arts.

#define EPSILON 0.001
#define DEFAULT_ZOOM 1.0

#define PI 3.141592653589

#define MAX_OCTAVES 9.0

// Material for nothing. I.e. the empty space.
#define MAT_NOTHING 0

// Material for the colored sphere.
#define MAT_COLOR_SPHERE 1

// Material for the sea water.
#define MAT_WATER_SPHERE 2

// World seed - skew the input for the noise.
#define WORLD_SEED 47.159

// Initial scene - base sphere goes from dark to light and
// gradually morphs into the naked ecosphere.
#define SCENE0 0

// Second scene - the colored ecosphere.
#define SCENE1 1

// Third scene - sea level rises.
#define SCENE2 2

// Forth scene - one day and night cycle.
#define SCENE3 3

// A ray object with origin and direction.
struct Ray {
    vec3 origin;
    vec3 direction;
};

// Force normalization of direction when creating a ray.
Ray ray(vec3 origin, vec3 direction);

// Calculate a point at distance d along the ray.
vec3 point(Ray r, float d);

// Generate a primary camera ray given:
// eye - the camera position.
// at - the position where the camera is looking.
// up - the world up position.
// focalLength - the zoom value for the camera. A greater value zoom in.
// uv - the uv coordinate for which the ray is produced. It is assumed
// that uv zero is in the middle of the projection.
// The camera is assuming an OpenGL like coordinate system.
Ray cameraRay(vec3 eye, vec3 at, vec3 up, float focalLength, vec2 uv);

// An intersection object with distance along the ray, an displacement
// offset for the object and the material id for the object.
struct Intersection {
    float dist;
    float offset;
    int material;
};

// Produce the closest intersection from the scene given the position.
Intersection intersectScene(int sceneId, vec3 pos);

// March the ray for an intersection with the scene.
Intersection march(int sceneId, Ray r, float far);

// Calculate a normal through central difference. It's very expensive
// though. Consider a noise function with analytical derivatives as
// future improvement.
vec3 calcNormal(int sceneId, vec3 pos);

// Calculate a 2D rotations matrix.
mat2 calcRotate2d(float theta);

// Distance function for the base sphere.
float baseSphere(vec3 pos);

// Distance function for a shere. The position - relative origin - and
// the radius for the sphere.
float sphere(vec3 pos, float radius);

// Get the sun's position.
vec3 sunPosition(int sceneId);

// Normalize the height offset from ~[-1 : 1] to ~[0 : 1].
float normalizeOffset(float offset);

// Get the terrain color the given height offset.
vec3 terrainColor(float offset);

// Estimate the needed number of octaves from screen with.
int estimateOctaves(float width);

// Noise functions.
float fbm(vec3 pos, int numOctaves);
float snoise(vec3 v);

// Entry point for generating the image.
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Normalized pixel coordinate where origo is in the
    // middle and compensation is made for aspect ratio.
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    // Make a primary ray for the uv position.
    vec3 eye = vec3(0.0, 0.0, 25.0);
    vec3 at = vec3(0.0);
    vec3 up = normalize(vec3(0.5, 1.0, 0.0)); // Tilt the view a little.

    Ray primaryRay = cameraRay(eye, at, up, DEFAULT_ZOOM, uv);

    // Determine scene id from time.   
    int sceneId = SCENE0;
    if (iTime >= 24.0) {
      	sceneId = SCENE3;
    } else if (iTime >= 16.0) {
        sceneId = SCENE2;
    } else if (iTime >= 12.0) {
        sceneId = SCENE1;
    }

    // Perform the ray marching, keep the far plane near ;-)
    Intersection intersection = march(sceneId, primaryRay, 45.0);

    // Create a default color for the current pixel.
    vec3 color = vec3(0.0);

    switch (intersection.material) {
        // A sphere colored using its height offset.
        case MAT_COLOR_SPHERE:
        {
         	vec3 pos = point(primaryRay, intersection.dist);
    		vec3 lightDirection = normalize(sunPosition(sceneId) - pos);
    		vec3 normal = calcNormal(sceneId, pos);                
    		float light = max(0.0, dot(normal, lightDirection));            

            vec3 graySphere = vec3(normalizeOffset(intersection.offset));
            vec3 colorSphere = terrainColor(intersection.offset);
            color = mix(graySphere, colorSphere, smoothstep(12.0, 16.0, iTime));

        	color = color * light;
        }
       	break;

        // A sphere with ocean color.
        case MAT_WATER_SPHERE:
        {
            vec3 pos = point(primaryRay, intersection.dist);
    		vec3 lightDirection = normalize(sunPosition(sceneId) - pos);
    		vec3 normal = calcNormal(sceneId, pos);                
    		float light = max(0.0, dot(normal, lightDirection));

            vec3 ocean = vec3(0.0, 67.0 / 255.0, 123.0 / 255.0);
            color = ocean * light;
        }
        break;

        default:
        	// Just rendering the black void with default color.
        	break;
    }

    // Output to screen.
    fragColor = vec4(color, 1.0);
}

Ray ray(vec3 origin, vec3 direction)
{
    return Ray(origin, normalize(direction));
}

vec3 point(Ray r, float d)
{
    return r.origin + r.direction * d;
}

Ray cameraRay(vec3 eye, vec3 at, vec3 up, float focalLength, vec2 uv)
{
    vec3 z = normalize(at - eye);
    vec3 x = normalize(cross(z, up));
    vec3 y = cross(x, z);

    vec3 center = eye + z * focalLength;
    vec3 xy = center + uv.x * x + uv.y * y;

    return ray(eye, xy - eye);
}

Intersection intersectScene(int sceneId, vec3 pos)
{
    // The sphere shall be rotated in all scenes.
    pos.xz = calcRotate2d(iTime * 0.05) * pos.xz;

    // And there always be a base sphere.
    float sphereDist = baseSphere(pos - vec3(0.0));

    // There shall always be height offset.
    float offset = fbm((pos + WORLD_SEED) * 0.13, estimateOctaves(iResolution.y));

    if (sceneId == SCENE0) {

        // Mix between the base sphere and the ecosphere.
        float factor = smoothstep(4.0, 10.0, iTime);
        float dist = mix(sphereDist, sphereDist - offset, factor);

        return Intersection(dist, offset, MAT_COLOR_SPHERE);      
    }  else if (sceneId == SCENE1) {

        // Just the ecosphere.
        return Intersection(sphereDist - offset, offset, MAT_COLOR_SPHERE);      
    } else {

        // The ecosphere and sea.
        float seaDist = sphere(pos - vec3(0.0), mix(9.00, 9.88, smoothstep(16.0, 24.0, iTime)));        
        if (seaDist < sphereDist - offset) {
            return Intersection(seaDist, 0.0, MAT_WATER_SPHERE);
        } else {
            return Intersection(sphereDist - offset, offset, MAT_COLOR_SPHERE);
        }
    }
}

Intersection march(int sceneId, Ray r, float far)
{
    float rayDistance = 0.0;
    Intersection intersection = Intersection(0.0, 0.0, MAT_NOTHING);

    for (int i = 0; i < 100; ++i) {       
        intersection = intersectScene(sceneId, point(r, rayDistance));

        rayDistance += intersection.dist;
        if (intersection.dist < EPSILON) break;
        if (rayDistance > far) {
            intersection.material = MAT_NOTHING;
            break;
        }
    }

    intersection.dist = rayDistance;
    return intersection;
}

vec3 calcNormal(int sceneId, vec3 pos)
{
    float d = intersectScene(sceneId, pos).dist;
    vec2 e = vec2(EPSILON, 0.0);

    vec3 n = d - vec3(
        intersectScene(sceneId, pos - e.xyy).dist,
        intersectScene(sceneId, pos - e.yxy).dist,
        intersectScene(sceneId, pos - e.yyx).dist
    );

    return normalize(n);
}

mat2 calcRotate2d(float theta)
{
    return mat2(vec2(cos(theta), -sin(theta)), vec2(sin(theta), cos(theta)));
}

float baseSphere(vec3 pos)
{
    return sphere(pos, 10.0);
}

float sphere(vec3 pos, float radius)
{
    return length(pos) - radius;
}

vec3 sunPosition(int sceneId)
{
    // The base position for the sun.
    vec3 basePosition = vec3(100.0, 0.0, 100.0);

    if (sceneId == SCENE0) {
        // Let the sun travel one half orbit from back of sphere to base.
        float theta = mix(-PI, 0.0, smoothstep(0.0, 10.0, iTime));
        basePosition.xz = calcRotate2d(theta) * basePosition.xz;
    } else if (sceneId == SCENE3) {
        // Let the sun orbit. 20 seconds for a full circle.
        float theta = mix(0.0, PI * 2.0, smoothstep(24.0, 34.0, iTime));
        basePosition.xz = calcRotate2d(theta) * basePosition.xz;
    }

    return basePosition;
}

float normalizeOffset(float offset)
{
    return offset * 0.75 + 0.5;
}

vec3 terrainColor(float offset)
{
    offset = normalizeOffset(offset);

    vec3 clay = vec3(127.0 / 255.0, 95.0 / 255.0, 63.0 / 255.0);
    vec3 fern = vec3(79.0 / 255.0, 121.0 / 255.0, 66.0 / 255.0);
    vec3 forest = vec3(11.0 / 255.0, 102.0 / 255.0, 35.0 / 255.0);
    vec3 granite = vec3(97.0 / 255.0, 97.0 / 255.0, 97.0 / 255.0);
    vec3 snow = vec3(1.0, 250.0 / 255.0, 250.0 / 255.0);

    vec3 color = mix(clay, fern, smoothstep(0.0, 0.4, offset));
    color = mix(color, forest, smoothstep(0.4, 0.55, offset));
    color = mix(color, granite, smoothstep(0.55, 0.65, offset));
    color = mix(color, snow, smoothstep(0.65, 1.0, offset));

    return vec3(color);
}

int estimateOctaves(float height)
{
    // Ad hoc heuristics for the selection of octaves. Made from what's looking good.
    if (height <= 450.0) {
        return 7;
    } else if (height <= 675.0) {
        return 8;
    } else {
        return 9;
    }
}

float fbm(vec3 pos, int numOctaves) {
	float v = 0.0;
	float a = 0.5;
	vec3 shift = vec3(100);
	for (int i = 0; i < numOctaves; ++i) {
		v += a * snoise(pos);
		pos = pos * 2.0 + shift;
		a *= 0.5;
	}
	return v;
}

//	Simplex 3D Noise 
//	by Ian McEwan, Ashima Arts
//
vec4 permute(vec4 x) {return mod(((x*34.0)+1.0)*x, 289.0);}
vec4 taylorInvSqrt(vec4 r) {return 1.79284291400159 - 0.85373472095314 * r;}

float snoise(vec3 v)
{ 
  	const vec2 C = vec2(1.0/6.0, 1.0/3.0) ;
  	const vec4 D = vec4(0.0, 0.5, 1.0, 2.0);

	// First corner
  	vec3 i = floor(v + dot(v, C.yyy) );
  	vec3 x0 = v - i + dot(i, C.xxx) ;

	// Other corners
  	vec3 g = step(x0.yzx, x0.xyz);
  	vec3 l = 1.0 - g;
  	vec3 i1 = min( g.xyz, l.zxy );
  	vec3 i2 = max( g.xyz, l.zxy );

  	//  x0 = x0 - 0. + 0.0 * C 
  	vec3 x1 = x0 - i1 + 1.0 * C.xxx;
  	vec3 x2 = x0 - i2 + 2.0 * C.xxx;
  	vec3 x3 = x0 - 1. + 3.0 * C.xxx;

	// Permutations
  	i = mod(i, 289.0 ); 
  	vec4 p = permute( permute( permute( 
    	              i.z + vec4(0.0, i1.z, i2.z, 1.0 ))
        	   		  + i.y + vec4(0.0, i1.y, i2.y, 1.0 )) 
                      + i.x + vec4(0.0, i1.x, i2.x, 1.0 ));

	// Gradients
	// ( N*N points uniformly over a square, mapped onto an octahedron.)
  	float n_ = 1.0/7.0; // N=7
  	vec3  ns = n_ * D.wyz - D.xzx;

  	vec4 j = p - 49.0 * floor(p * ns.z *ns.z);  //  mod(p,N*N)

  	vec4 x_ = floor(j * ns.z);
  	vec4 y_ = floor(j - 7.0 * x_ );    // mod(j,N)

  	vec4 x = x_ *ns.x + ns.yyyy;
  	vec4 y = y_ *ns.x + ns.yyyy;
  	vec4 h = 1.0 - abs(x) - abs(y);

  	vec4 b0 = vec4( x.xy, y.xy );
  	vec4 b1 = vec4( x.zw, y.zw );

  	vec4 s0 = floor(b0)*2.0 + 1.0;
  	vec4 s1 = floor(b1)*2.0 + 1.0;
  	vec4 sh = -step(h, vec4(0.0));

  	vec4 a0 = b0.xzyw + s0.xzyw*sh.xxyy ;
  	vec4 a1 = b1.xzyw + s1.xzyw*sh.zzww ;

  	vec3 p0 = vec3(a0.xy,h.x);
  	vec3 p1 = vec3(a0.zw,h.y);
  	vec3 p2 = vec3(a1.xy,h.z);
  	vec3 p3 = vec3(a1.zw,h.w);

	//Normalise gradients
  	vec4 norm = taylorInvSqrt(vec4(dot(p0,p0), dot(p1,p1), dot(p2, p2), dot(p3,p3)));
  	p0 *= norm.x;
  	p1 *= norm.y;
  	p2 *= norm.z;
  	p3 *= norm.w;

	// Mix final noise value
  	vec4 m = max(0.6 - vec4(dot(x0,x0), dot(x1,x1), dot(x2,x2), dot(x3,x3)), 0.0);
  	m = m * m;
  	return 42.0 * dot( m*m, vec4( dot(p0,x0), dot(p1,x1), 
                                  dot(p2,x2), dot(p3,x3) ) );
}

// Ref: https://www.shadertoy.com/view/XtVfzd

const float eps = 1e-3;
const float pi = 3.1415926535;
const vec3 z_up = vec3(0.0, 0.0, 1.0);

float rand(vec2 p) { 
    return fract(sin(dot(p, vec2(12.9898, 4.1414))) * 43758.5453);
}

float rand(float n){return fract(sin(n) * 43758.5453123);}

float noise(float p){
	float fl = floor(p);
  	float fc = fract(p);
	return mix(rand(fl), rand(fl + 1.0), fc);
}

float noise(vec2 n) {
	const vec2 d = vec2(0.0, 1.0);
  	vec2 b = floor(n), f = smoothstep(vec2(0.0), vec2(1.0), fract(n));
	return mix(mix(rand(b), rand(b + d.yx), f.x), mix(rand(b + d.xy), rand(b + d.yy), f.x), f.y);
}

float mod2(float p, float a) {
    return p - a * floor(p / a);
}

vec2 mod2(vec2 p, vec2 a) {
    return vec2(mod2(p.x, a.x), mod2(p.y, a.y));
}

float sdf_circle(vec2 p, vec2 o, float r) {
    return length(p - o) - r;
}

float sdf_ring(vec2 p, float r, float lineWidth) {
    float outer = sdf_circle(p, vec2(0, 0), r + 0.5 * lineWidth);
    float inner = sdf_circle(p, vec2(0, 0), r - 0.5 * lineWidth);

    return max(-inner, outer);
}

float sdf(vec2 p) {
    return min(sdf_ring(p, 0.75, 0.175), sdf_ring(p, 0.35, 0.175));
}

float sake_displacement(vec2 p, vec2 p0) {
    float dist = length(p - p0);
    vec2 dir = normalize(p - p0);

    float k = 5.0;
    float omega = 20.0;
    float basewave = 0.5 * sin(k * dist - mod2(omega * iTime, 2.0 * pi)) + 0.5;
    float k2 = 22.0;
    float omega2 = 44.0;
    float subwave = 0.5 * sin(k2 * dist - mod2(omega2 * iTime, 2.0 * pi)) + 0.5;
    float k3 = 45.0;
    float omega3 = 90.0;
    float subwave2 = 0.5 * sin(k3 * dist - mod2(omega3 * iTime, 2.0 * pi)) + 0.5;

    basewave = 2.0 * pow(basewave, 4.0) + 0.3 * pow(subwave, 2.0) + 0.09 * subwave2;

    float amp = min(1.0 / sqrt(eps + dist), 1.0);
    amp *= basewave;

    float outer_r = 0.82;
    amp += 100.0 * step(outer_r, length(p)) * (length(p) - outer_r) * (length(p) - outer_r);

    amp = 1.0 - exp(-amp);

    amp *= step(length(p0), 0.85);

    return -0.1 * amp;
}

vec3 sake_normal(vec2 p, vec2 p0) {
    float dfx = sake_displacement(p + vec2(1.0, 0.0) * eps, p0) - sake_displacement(p - vec2(1.0, 0.0) * eps, p0);
    float dfy = sake_displacement(p + vec2(0.0, 1.0) * eps, p0) - sake_displacement(p - vec2(0.0, 1.0) * eps, p0);
    return normalize(vec3(dfx, dfy, 2.0 * eps));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0 * fragCoord.xy - iResolution.xy) / iResolution.xy;
    vec2 pos = (2.0 * fragCoord.xy - iResolution.xy) / min(iResolution.x, iResolution.y);

    vec2 displaced_pos = pos;
    float sake_depth = 0.0;
    vec3 normal;
    {
        vec2 mouse_pos = (2.0 * iMouse.xy - iResolution.xy) / min(iResolution.x, iResolution.y);

        if (iMouse.x == 0.0 && iMouse.y == 0.0) {
            mouse_pos = vec2(-0.5, -0.5) + 0.1 * vec2(2.0 * noise(iTime) - 1.0, 2.0 * noise(iTime + 11.52) - 1.0);
        }

        const float max_sake_depth = 0.4;
        sake_depth = mod2(0.05 * iTime, max_sake_depth) * step(length(mouse_pos), 0.85);

        normal = sake_normal(pos, mouse_pos);   
        vec3 refract_dir = refract(-z_up, normal, 1.4);

        vec2 displacement = refract_dir.xy / refract_dir.z * sake_depth;

        displaced_pos += displacement;
    }

    vec3 ring_color = vec3(0.1, 0.1, 0.6);
    vec3 ring_edge_color = vec3(0.3, 0.6, 0.9);
    ring_color = mix(ring_edge_color, ring_color, smoothstep(-0.02, 0.02, -sdf(displaced_pos)));

    vec3 cup_color = vec3(0.98, 0.98, 0.98);  
    vec3 color = mix(ring_color, cup_color, smoothstep(0.0, 0.02, sdf(displaced_pos)));

    //æ±åçå½¢é«åº¦ï¼å°ç¢è§ä¸ºåçå½¢ï¼
    float h = sqrt(max(0.0, 1.0 - dot(pos, pos)));
    vec3 N = normalize(vec3(-pos, h));
    color *= mix(0.8, 1.0, dot(N, vec3(0.0, 0.0, 1.0)));

    //æ¸éé¢è²
    const float max_sake_h = 0.8;
    float sake_h = h - (1.0 - max_sake_h);
    color *= mix(vec3(1.0), vec3(0.8, 0.8, 0.0), 1.0 - exp(-0.3 * sake_h)); 

    // reflection
    vec3 light_dir = normalize(vec3(3.0, 2.0, 1.0));
    vec3 ref_dir = reflect(-z_up, normal);
    vec3 ref_color = 3.0 * vec3(1.0, 1.0, 1.0) * smoothstep(0.8, 0.9, dot(ref_dir, light_dir));
    ref_color += 0.5 * noise(2.0 * ref_dir.xy);
    ref_color *= 1.0 - exp(-2.0 * sake_depth);

    color += 1.0 * ref_color;

    //ç¢æ²¿
    color = mix(color, cup_color * 0.6, smoothstep(0.86, 0.87, length(pos)));
    //âç¢çååº¦â
    color = mix(color, cup_color * 0.1, smoothstep(0.91, 0.92, length(pos)));
    //åç´ç¹åæºè¡°åè¿ä¼¼æ¨¡æ
    color *= 1.0 - 0.5 * length(uv) * length(uv);

    fragColor = vec4(color, 1.0);
}

// Reusing bits of https://shadertoy.com/view/llsfRj
// ( watch there and upstream for contiuous lines ).

// --- direct transforms
vec2 Mobius(vec2 p, vec2 z1, vec2 z2)
{
	z1 = p - z1; p -= z2;
	return mat2(z1, z1.y,-z1.x) * p / dot(p, p);
}

vec2 spiralZoom(vec2 p, vec2 offs, float n, float spiral, float zoom, vec2 phase)
{
	p -= offs;
	float a = atan(p.y, p.x)/6.283  + iTime/32.,
	      d = length(p);
	return mat2(n,1, spiral,-zoom) * vec2(a, log(d)) + phase;
}

// --- demo configuration 
vec2 Z1 = vec2(-.875, -.525),
     Z2 = vec2( .375, -.125);

vec2 MobiusSpiral(vec2 q)      // total direct transform
{                              // ( for reverse, see https://shadertoy.com/view/llsfRj )
    q = Mobius(q, Z1, Z2);
    return spiralZoom(q, vec2(-.5), 5., 3.14159*.2, .5, vec2(-1, 1)*iTime*.25);
}

// #define hash(p) fract(sin((p)*mat2(127.1,311.7, 269.5,183.3)) *43758.5453123)

void mainImage(out vec4 O, vec2 u) // --- Draw Moebius Spiral field
{
#define scale (R.y/15.) // 30.                           // number of cells
	vec2 R = iResolution.xy,
         U = (u -.5*R) / R.y,
      // ofs = hash(floor(scale*U)),  // U -= ofs/scale; // jittering
         V = MobiusSpiral(U);
    O = .7+.3*vec4(.5+.5*sin(3.*6.28*V),0,1);        // display continuous field
    U = 2.*fract(U*scale+.5)-1.; 

 #define f(v) fract(v+.5)-.5                         // suppress the wrapping glitch
    mat2 M = inverse( mat2(f(dFdx(V)),f(dFdy(V))) ); // measure compression, for LOD
    M /= length(vec4(M));   // / max(360.,R.y);      // normalize
  //float v = 2.*length(U*M) - .5;                   // draw ellipse
    float v = abs(dot(U,M[1]));                      // draw main direction
    v = smoothstep(2.*scale/R.y, 0., v);
    O *= vec4( pow(v, 1./2.2 ) );                    // to sRGB
}

// References
// https://www.shadertoy.com/view/XdfczH
// https://www.shadertoy.com/view/wtfSWH
