#define pi 3.14159265358979323846264338327950288419716

vec3 moveOnSphereThetaPhi(float radius, float theta, float phi) {
    //theta = (mod(lon,360.0) / 360.0)*pi * 2.0;
    //phi = (mod(lat,360.0) / 360.0)*pi * 2.0;
    theta = theta * pi;
    phi = phi * pi;
    float tsin = sin(theta);
    return radius * vec3(tsin*cos(phi),tsin*sin(phi),cos(theta));
}

vec3 moveOnSphereLonLat(float radius, float lon, float lat) {
    //vec3 pos = vec3(0.0,0.0,0.0);
    //float lat = asin(pos.z / radius);
    //float lon = atan(pos.y,pos.x);

    float x = cos(lat) * cos(lon);
    float y = cos(lat) * sin(lon);
    float z = sin(lat);

    vec3 move = radius * vec3(x,y,z);

    return move;
}

float circle(vec2 fragCoord, float radius)
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 cUv = uv;
    cUv.x -= 0.5;
    cUv.y -= 0.5;
    cUv.y *= iResolution.y/iResolution.x;

    float circleOuter = smoothstep(0.101,0.103,length(cUv*0.5)/radius);
    float circleInner = smoothstep(0.099,0.097,length(cUv*0.5)/radius);
    float circle = circleInner + circleOuter;

    float c = 1.0 - circle;

    return c;
}

float DistLine(vec3 ro, vec3 rd, vec3 p) {
	return length(cross(p-ro, rd))/length(rd);
}

float DrawPoint(vec3 ro, vec3 rd, vec3 p) {
	float d = DistLine(ro, rd, p);
    d = smoothstep(.06, .05, d);
    float z = p.z > 0.0 ? 0.125 : 1.0;

    return d*z;
}


float DrawLine(vec3 ro, vec3 rd, vec3 a, in vec3 b)
{
    vec3 ab =normalize(b-a),ao = a-ro;
	float d0 = dot(rd, ab), d1 = dot(rd, ao), d2 = dot(ab, ao);
	float len = (d0*d1-d2)/(1.0-d0*d0);
    len= clamp(len,0.0,length(b-a));
    vec3 p = a+ab*len;
    float z = p.z > 0.0 ? 0.0 : 1.0;

 	//float d = DistLine(ro, rd, p);
    //d = smoothstep(.06, .05, d);
    //float z = p.z > 0.0 ? 0.125 : 1.0;

    return length(cross(p-ro, rd))/(1.-p.z);
}


vec3 lookat(vec3 p, vec3 eye, vec3 target, vec3 up)
{
	vec3 w = normalize(target-eye), u = normalize(cross(w,up));
	return vec3(dot(p,u), dot(p,cross(u,w)), dot(p,w));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime;

    vec2 uv = fragCoord/iResolution.xy;
    vec2 muv = uv;

	muv -= 0.5;
    muv.x *= iResolution.x/iResolution.y;

    vec3 target = vec3(0.0, 0.0, 0.0);
    vec3 ro = vec3(3. * cos(t)*0.0, 2. * sin(t)*0.0, -3.); // camera position

    float zoom = 1.0;

    vec3 up = vec3(0., 1., 0.);
    vec3 f = normalize(target - ro);
    vec3 r = cross(up, f);
    vec3 u = cross(f, r);

    vec3 z = ro + f * zoom;
    vec3 i = z + muv.x*r + muv.y*u;
    vec3 rd = normalize(i - ro); // camera direction
    vec3 rd2 = lookat(rd,ro,target,up);
    //rd=vec3(muv, 1.0);

    vec3 p = vec3(0);

    vec3 mover = vec3(0);

    vec3 o = moveOnSphereLonLat(1.,t,t*2.);
    vec3 du = o+normalize(o)*.2;
    vec3 df = moveOnSphereLonLat(1.,(t+.1)*1.0,(t+.1)*2.);
    vec3 dr = o+cross(du,df);

    float lu = .01/DrawLine(ro, rd, o, du);
    float lf = .01/DrawLine(ro, rd, o, df);
    float lr = .01/DrawLine(ro, rd, o, dr);

    mover += vec3(DrawPoint(ro, rd, o));
    mover += mix(mover,vec3(0.,0.,1.),lu);
    mover += vec3(1.,0.,0)*lf;
    mover += vec3(0.,1.,0.)*lr;

    for(int i=0; i<50; i++) {
        float j = float(i)*.04;
    	mover += DrawPoint(ro, rd,  moveOnSphereLonLat(1.,(t-j),(t-j)*2.));
    }

    vec3 col = vec3(0);
    col = vec3(circle(fragCoord,0.9))*.35 + mover;

	fragColor = vec4(col,1.);
}

float smin( float a, float b, float k ) {
float h = clamp( 0.5+0.5*(b-a)/k, 0., 1. );
return mix( b, a, h ) - k*h*(1.0-h);
}

float sdBox(vec3 p, vec3 s) {
p = abs(p)-s;
return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}

vec2 path(float z){
float x = sin(z) + 3.0 * cos(z * 0.5) - 1.5 * sin(z * 1.12345);
float y = cos(z) + 1.5 * sin(z * 0.3) + .2 * cos(z * 1.12345);
return vec2(x,y)*0.5 ;
}

vec2 GetDist (vec3 p) 
{
vec2 o = path(p.z) / 5.0;
p = vec3(p.x,p.y,p.z)-vec3(o.x,o.y,0.); 
float r = 3.14159*sin(p.z*0.15)+(iTime*0.25*1.);
mat2 R = mat2(cos(r), sin(r), -sin(r), cos(r));
p.xy *= R ; 

p.xy *= (sin(p.z*0.2+iTime*0.3)*1.2+0.1)/1.5;

p = fract(p) * 2. - 1.; 
vec2 box = vec2(sdBox(p-vec3(0,0.0,0), vec3(0.3)),1);
vec2 box2 = vec2(sdBox(p-vec3(0,0.0,0), vec3(0.1,1.1,0.1)),0);
vec2 box3 = vec2(sdBox(p-vec3(0,0.0,0), vec3(1.1, 0.1,0.1)),2);
vec2 box4 = vec2(sdBox(p-vec3(0,0.0,0), vec3(0.1, 0.1,1.1)),3);
vec2 d = (box.x<box2.x)?box:box2;
d=(d.x<box3.x)?d:box3;
d=(d.x<box4.x)?d:box4;
d.x =  smin(d.x,box.x,0.2);
d.x *= 0.4;        
return  d;
}

vec2 RayMarch (vec3 ro, vec3 rd) 
{
vec2 h, t=vec2( 0.);
for (int i=0; i<64; i++) 
{
h = GetDist(ro + t.x * rd);
if(h.x<.001||t.x>100.) break;
t.x+=h.x;t.y=h.y;
}
if(t.x>100.) t.x=0.;
return t;
}

vec3 GetNormal(vec3 p){
vec2 d = GetDist(p);
vec2 e = vec2(0.001,0);
vec3 n = d.x - vec3(
GetDist(p-e.xyy).x,
GetDist(p-e.yxy).x,
GetDist(p-e.yyx).x);
return normalize(n);
}

float GetLight(vec3 p)
{
vec2 a = path(iTime * 1.0)*1.0;
vec3 o = vec3(a / 5.0,iTime-0.5);
vec3 lightPos =  o;
vec3 l = normalize(lightPos-p);
vec3 n = GetNormal(p);
float dif = clamp(dot(n, l)*.5+.5, 0., 1.);
vec2 d = RayMarch(p+n*.001*20., l);
if (d.x<length(lightPos-p)) dif *= 0.5;
return dif;       
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
vec2 uv = (fragCoord -.5*iResolution.xy) / iResolution.y;
vec3 col = vec3(0);
vec2 a = path(iTime * 1.0)*1.0;
vec3 o = vec3(a / 5.0,iTime);
vec3 rd = normalize(vec3(uv.x,uv.y-.2,0.5));
float the = iTime*0.3;
vec2 d = RayMarch (o,rd);
float t =d.x *1.;   
if(t>0.){
vec3 p = o + rd *t;
vec3 baseColor = vec3(0.,0.,0.5);
if(d.y==0.) baseColor=vec3(sin(p.z+iTime*2.),1.,0);
//if(d.y==0.) baseColor=vec3(sin(p.z+iTime*1.)-0.4,1.,0.5);
if(d.y==1.) baseColor=vec3(0,cos(p.z+iTime*4.),1.);
if(d.y==2.) baseColor=vec3(sin(p.z+iTime*1.)-0.4,1.,0.5);
if(d.y==3.) baseColor=vec3(1,.1,sin(iTime));
float dif = GetLight (p); 
col = vec3(dif);       
col+=baseColor;
float fog = 1. / (2. + t * t * 0.25);
col *= vec3(fog);   
vec3 sky = vec3(1., 1., 1.);
col = mix(sky, col, 1./(d.x*d.x/1./1.*.05+1.1)); 
}
fragColor = vec4(col,1.0);
}

