float getnoise(int octaves, float persistence, float freq, vec3 coords) {

    float amp= 1.; 
    float maxamp = 0.;
    float sum = 0.;

    for (int i=0; i < octaves; ++i) {

        sum += amp * snoise(coords*freq); 
        freq *= 2.;
        maxamp += amp;
        amp *= persistence;
    }

    return (sum / maxamp) * .5 + .5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = mod(iTime, t2);
    t = t / t2; // Normalized time

    vec4 col = vec4(0.0);
	vec2 q = fragCoord.xy / iResolution.xy;
	vec2 p = ( 2.0 * fragCoord.xy - iResolution.xy ) / min( iResolution.y, iResolution.x );
    vec2 mo = (2.0 * iMouse.xy - iResolution.xy) / min(iResolution.x, iResolution.y);
    p += vec2(0.0, -0.1);

    //float ay = TAU * mod(iTime, 8.0) / 8.0;
    //ay = 45.0 * 0.01745;
    float ay = 0.0, ax = 0.0, az = 0.0;
    if (iMouse.z > 0.0) {
        ay = 3.0 * mo.x;
        ax = 3.0 * mo.y;
    }
    mat3 mY = mat3(
         cos(ay), 0.0,  sin(ay),
         0.0,     1.0,      0.0,
        -sin(ay), 0.0,  cos(ay)
    );

    mat3 mX = mat3(
        1.0,      0.0,     0.0,
        0.0,  cos(ax), sin(ax),
        0.0, -sin(ax), cos(ax)
    );
    mat3 m = mX * mY;

    vec3 v = vec3(p, 1.0);
    v = m * v;
    float v_xy = length(v.xy);
    float z = v.z / v_xy;

    // The focal_depth controls how "deep" the tunnel looks. Lower values
	// provide more depth.
	float focal_depth = 0.15;
    #ifdef WHITEOUT
    focal_depth = mix(0.15, 0.015, smoothstep(0.65, 0.9, t));
    #endif

    vec2 polar;
    //float p_len = length(p);
    float p_len = length(v.xy);
    //polar.y = focal_depth / p_len + iTime * speed;
    polar.y = z * focal_depth + iTime * speed;
    //polar.y = z;
    float a = atan(v.y, v.x);
    //float a = atan(q.y, q.x);
    // atan returns a value in the range -PI to PI, let's normalize
    // that into the range [0..1]
    a = 0.5 + 0.5 * a / (1.0 * PI);
    a -= iTime * rotation_speed;
    float x = fract(a);
    // Remove the seam by reflecting the u coordinate around 0.5:
    if (x >= 0.5) x = 1.0 - x;
    polar.x = x;

    // Colorize blue
    float val = 0.45 + 0.55 * fBm3(
        vec3(vec2(2.0, 0.5) * polar, 0.15 * iTime));
    //float val = getnoise(8, 0.65, 1.0, vec3(polar, 0));
    val = clamp(val, 0.0, 1.0);
    col.rgb = vec3(0.15, 0.4, 0.9) * vec3(val);

    // Add white spots
    vec3 white = 0.35 * vec3(smoothstep(0.55, 1.0, val));
    col.rgb += white;
    col.rgb = clamp(col.rgb, 0.0, 1.0);

    float w_total = 0.0, w_out = 0.0;
    #ifdef WHITEOUT
    // Fade in and out from white every t2 seconds
    float w_in = 0.0;
    w_in = abs(1.0 - 1.0 * smoothstep(0.0, 0.25, t));
    w_out = abs(1.0 * smoothstep(0.8, 1.0, t));
    w_total = max(w_in, w_out);
    #endif


    // Add the white disk at the center
    float disk_size = max(0.025, 1.5 * w_out);
    //disk_size = 0.001;
    float disk_col = exp(-(p_len - disk_size) * 4.0);
    //col.rgb += mix(col.xyz, vec3(1,1,1), disk_col);
    col.rgb += clamp(vec3(disk_col), 0.0, 1.0);


    #ifdef WHITEOUT
    col.rgb = mix(col.rgb, vec3(1.0), w_total);
    #endif

    fragColor = vec4(col.rgb,1);
}

const float EPSILON = 0.001;
const float MAX_DIST = 100.0;

float remap(float low1, float high1, float low2, float high2, float value){
    return low2 + (value - low1) * (high2 - low2) / (high1 - low1);
}


mat3 rotateY(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(c, 0, s),
        vec3(0, 1, 0),
        vec3(-s, 0, c)
    );
}



float SphereSDF(vec3 p, float size){
	return length(p) - size;
}

float CubeSDF(vec3 p, vec3 size){
    vec3 d = abs(p) - size;

    float insideDistance = min( max(max(d.x, d.y),d.z), 0.0);

    float outsideDistance = length( max(d, 0.0) );

    return insideDistance + outsideDistance;
}

float TorusSDF( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

mat2 Rot (float a)
		{
    		float s = sin(a);
			float c = cos(a);
            return mat2(c,-s,s,c);

		}


mat2 R;
float T;



float SceneSDF(vec3 p){

    p.xz *= (Rot (cos(iTime)*1.));

    float gap = 2.;
    p.x = mod(p.x + gap,2.0 * gap) - gap;
    p.y = mod(p.y + gap,2.0 * gap) - gap;
    p.z = mod(p.z + gap, 2.0 * gap) - gap;

    p *= vec3(1.,1.0,1.);

    float sphere = SphereSDF( p, .7);

    float cube = CubeSDF(p, vec3 (0.5));

	float torus = TorusSDF(p, vec2(1.,.04));

    float cub = max(-sphere,cube);
     cub = min(cub,torus);

    return cub;
}

vec3 estimateNormal(vec3 p) {
    return normalize(vec3(
        SceneSDF(vec3(p.x + EPSILON, p.y, p.z)) - SceneSDF(vec3(p.x - EPSILON, p.y, p.z)),
        SceneSDF(vec3(p.x, p.y + EPSILON, p.z)) - SceneSDF(vec3(p.x, p.y - EPSILON, p.z)),
        SceneSDF(vec3(p.x, p.y, p.z  + EPSILON)) - SceneSDF(vec3(p.x, p.y, p.z - EPSILON))
    ));
}

vec3 RayDirection(float fieldOfView, vec2 fragCoord, vec2 size){
	vec2 xy = fragCoord - size / 2.0;
    float z = size.y / tan(radians(fieldOfView) / 2.);
    return normalize(vec3(xy, -z));
}

vec4 ShortestDistanceToSurface(vec3 eye, vec3 marchingDirection, float start, float end){
	float depth = start;
	vec3 pos = eye;
    for(int i = 0; i < 95; i++){
        vec3 pos = eye + depth * marchingDirection;
    	float dist = SceneSDF(pos);
        if( dist <= EPSILON){
            return vec4(pos,depth);
        }
        depth += dist;
        if(depth >= end){
            pos = eye + depth * marchingDirection;
        	return vec4(pos,end);
        }
    }
    return vec4(pos,end);
}

mat4 viewMatrixRIGHT() {
    return mat4(
        vec4(1, 0, 0, 0),
        vec4(0, 1, 0, 0),
        vec4(0,0, 1, 0),
        vec4(0, 0, 0, 1)
    );
}

mat4 viewMatrixDOWN() {
    return mat4(
        vec4(0, 0, 1, 0),
        vec4(1,0, 0, 0),
        vec4(0, 1, 0, 0),
        vec4(0, 0, 1, 0)
    );
}

mat4 viewMatrixLEFT() {
    return mat4(
         vec4(1, 0, 0, 0),
        vec4(0, 1, 0, 0),
        vec4(0,0, -1, 0),
        vec4(0, 0, 0, -1)
    );
}

mat4 viewMatrixUP() {
    return mat4(
          vec4(0,1, 0, 0),
        vec4(0, 0, 1,0),
        vec4(-1,0, 0,0),
        vec4(0, 0,0,1)

    );
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime * 0.3;
    vec2 res = iResolution.xy;
    vec2 frag = fragCoord.xy;
    vec2 uv = frag / res;

    vec3 rayDir;
    vec3 worldDir;    
    vec3 eye;
    vec4 shortDist = vec4(0);

    float movement;
    movement = iTime *.1;


    // the screen is cut up into different POV points here:
    if( uv.x < 0.25){

        frag = fragCoord.xy;
        res = vec2(res.x *  0.25, res.y);
        rayDir = RayDirection(100.0, frag, res );
		worldDir = ( viewMatrixRIGHT() * vec4(rayDir,0.) ).xyz;
       eye = vec3(0.0 + 0.0 * cos(t),
                   movement*5. +0.0 * cos(t),
                   0. + 0.0 * sin(t));


        shortDist = ShortestDistanceToSurface(eye, worldDir, 0.0, 100.0);
    }


    else if(  uv.x > 0.25 && uv.x < 0.5 ){
        frag = fragCoord.xy;
        res = vec2(res.x * .75, res.y);
        rayDir = RayDirection(100.0, frag, res);
		worldDir = ( viewMatrixDOWN() * vec4(rayDir,0) ).xyz;
        eye = vec3(0. + 0.0 * cos(t),
                (movement *5.)-3.5 + 0.0 * cos(t),
                   0.0 + 0.0 * sin(t));
        shortDist = ShortestDistanceToSurface(eye, worldDir, 0.0, 100.0);
    }

       else if(  uv.x > 0.5 && uv.x < .75 ){
        frag = fragCoord.xy;
        res = vec2(res.x * 1.25, res.y);
        rayDir = RayDirection(100.0, frag, res);
		worldDir = ( viewMatrixLEFT() * vec4(rayDir,0) ).xyz;
        eye = vec3(0. + 0.0 * cos(t),
                movement *5. + 0.0 * cos(t),
                   0.0 + 0.0 * sin(t));
        shortDist = ShortestDistanceToSurface(eye, worldDir, 0.0, 100.0);
    }

     else if(  uv.x > 0.75 && uv.x < 1. ){
        frag = fragCoord.xy ;
        res = vec2(res.x *1.75, res.y);
        rayDir = RayDirection(100.0, frag, res);
		worldDir = ( viewMatrixUP() * vec4(rayDir,0) ).xyz;
        eye = vec3(0. + 0.0 * cos(t),
                movement *5. + 0.0 * cos(t),
                 0. + 0.0 * sin(t));
        shortDist = ShortestDistanceToSurface(eye, worldDir, 0.0, 100.0);
    }

    vec3 pos = shortDist.xyz;
    float dist = shortDist.w;

    vec3 ambientColor = vec3(0.5, 0.3, 0.3);
    vec3 color = vec3(130.0, 84.0, 72.0) / 255.0;

    vec3 lightPos = vec3(4.0 * sin(iTime),
                         6.0,
                         4.0 * cos(iTime));


    vec3 lightDirection = normalize(-lightPos);

    vec3 p = eye + dist * worldDir;
    vec3 normal = estimateNormal(p);
    float diffuse = dot(normal, -lightDirection);
    diffuse = max(diffuse, 0.0);

    vec3 finalColor = vec3(0);

    if( dist < MAX_DIST){
        finalColor = color;

        finalColor = finalColor * (diffuse + ambientColor);

        float distToEye = length(eye - pos);
        finalColor = finalColor * remap(70.0, 20.0, 0.0, 1.0, distToEye);
    }

	fragColor = vec4(finalColor,1);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv *= vec2(2000.0, 2000.0/iResolution.x*iResolution.y);

    float step = iTime * 100.0;
    float blue = uv.x + uv.y + 1.25 * step;
    blue = mod(blue, 256.0) / 256.0;

    float green = uv.y/2.0 + (1.0 + sin(step*3.0/8.0/60.0)) * uv.x / 2.0;
    green = mod(green, 256.0) / 256.0;

    float red = 480.0 + (1.0+cos(step*0.75/90.0))*uv.x/2.0 - uv.y/2.0;
    red = mod(red, 256.0) / 256.0;

    // Time varying pixel color
    vec3 col = vec3(red, green, blue);

    // Output to screen
    fragColor = vec4(col,1.0);
}

