    vec3 cloud_color = mix(vec3(1.0), sky_color, blend_factor);
    fragColor.rgb = mix(fragColor.rgb, cloud_color, cloud_blend_factor);
    */

    // Crappy tonemapping to make it look more realistic:
    //fragColor.rgb = pow(fragColor.rgb, vec3(1.0 + fragColor.rgb));

    //fragColor.rgb = vec3(accumulated_cloud_density);

    // TODO: How does shadertoy treat gamma?
    //fragColor.rgb = to_gamma_space(fragColor.rgb);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
	//vec3 col = vec3(.5f + (fragCoord.x - iResolution.x/2.0f )/float(iResolution.x),
    //                .5f + (fragCoord.y-iResolution.y/2.0f)/float(iResolution.y) ,
    //                .2f);

    float boundary = 1.0f;
    float n = 0.0f;
    float fract = 1.5f;
    n =  ceil(-log2(uv.x)/log2(fract));
    boundary = 1.0f;
    for(int i = 0 ; i < int(n); i++)
    {
        boundary /= fract;
    }

    float L = ((uv.x - boundary)*(.85f + .15 * sin(iTime) ))/(boundary-boundary/fract ) +.5f;
    float r = float(int(n)%4) ;
    float g = float(int(n+1.0f)%4);
    float b = float(int(n+2.0f)%4);
    vec3 col = vec3( r/4.0f, (g)/4.0f, (b)/4.0f)/L;

    // Output to screen
    fragColor = vec4(col,1.0);
}

