const vec3 msat = vec3(.3,.3,.3);
const float msdiv = .12;
vec3 saturate( vec3 v ) {
  // https://www.shadertoy.com/view/Wtt3R8
  float sv = dot(v,msat);
  vec3 hs = v/sv*msdiv;
  float ls = (1.-exp(-sv))*.9;
  float s = 1.-exp(-sv*sv/10.);
  vec3 hs2 = mix(hs,vec3(1),s);
  return ls*hs2/mix(msdiv,1.,s);
}

vec3 image( sampler2D chan, vec2 uv, vec3 gain )
{
  vec3 v = 1.*pow(texture(chan, uv).rgb,vec3(2.2));
  v+= .1*pow(textureLod(chan, uv, 5.).rgb,vec3(2.2));
  v*=gain;
  return v;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 uv = fragCoord/iResolution.xy*vec2(2,2);
  vec3 v = vec3(0);
  float gain = exp2(iMouse.x/100.);
  if (iMouse.x<2.) gain = (4.+2.*cos(iTime))*(1.+pow(.6+.2*sin(iTime*5.)+.1*sin(iTime*7.),2.));
  if      (uv.y>6./3.5) v = (vec3(1.,.2,.04)*fract(uv.x)*gain);
  else if (uv.y>5./3.5) v = image(iChannel0, uv, gain*vec3(1));
  else if (uv.y>4./3.5) v = image(iChannel1, uv, gain*vec3(1));
  else if (uv.y>3./3.5) v = image(iChannel2, uv, gain*vec3(1));
  else if (uv.y>2./3.5) v = image(iChannel1, uv, gain*vec3(0.4,1,0.01));
  else if (uv.y>1./3.5) v = image(iChannel3, uv, gain*vec3(0.04,0.04,2));
  else                  v = image(iChannel3, uv, gain*vec3(1));
  if (uv.x>=1.) v = saturate(v);

#ifdef SHOWCLIPPING
  float clipdet = float(any(greaterThan(v,vec3(1.))));
  if (fwidth(clipdet)>0.) { fragColor = vec4(1.-vec3(greaterThan(v,vec3(1))),0); return; }
  if (clipdet>0. && (int(dot(fragCoord/2.,vec2(1)))%2)==0) v = vec3(0);
#endif
  if (uv.x<1.) v = mix(1.,.6,uv.x)*clamp(v,0.,1.);
  fragColor = pow(vec4(v*255./256.,1.),vec4(1./2.2));
}

precision highp float;


// Convention:
// vec2s ending with 'c'
// are in circular coords
// ex: pc = vec2(angle of p, length of p)



#define time (iTime / 3.0)

#define PI2 6.28
#define PI 3.1416


// Point to polar coord
vec2 p_to_pc(vec2 p){
    return vec2(atan(p.y, p.x), length(p));
}

// Polar coord to point
vec2 pc_to_p(vec2 pc){
    return vec2(pc.y * cos(pc.x), pc.y * sin(pc.x));
}

// I use these fields to create and tweak the snowflakes
vec2 fieldA(vec2 pc){
    // Modify angle and distance
    pc.y += 0.02 * floor(cos(pc.x * 6.0 ) * 5.0);
    pc.y += 0.01 * floor(10.0 * cos(pc.x * 30.0));
    pc.y += 0.5 * cos(pc.y * 10.0);

    // Take back to position
    return pc;
}


// Different values of f happen to give quite different
// snowflake shapes. Interesting...
vec2 fieldB(vec2 pc, float f){
    // Modify angle and distance
    pc.y += 0.1 * cos(pc.y * 100.0 + 10.0);
    pc.y += 0.1 * cos(pc.y * 20.0 + f);
    pc.y += 0.04 * cos(pc.y * 10.0 + 10.0);

    return pc;
} 

vec4 snow_flake(vec2 p, float f){

    vec4 col = vec4(0.0);

	vec2 pc = p_to_pc(p * 10.0);


    pc = fieldA(fieldB(pc, f));

    p = pc_to_p(pc);

    float d = length(p);

    if(d < 0.3){
        col.rgba += vec4(1.0);
    }

    return col;
}

vec4 snow(vec2 p){

    vec4 col = vec4(0.0);

	p.y += 1.5 * time;

    p = fract(p + 0.05) - 0.5;

    p *= 1.0;


    p.x += 0.01 * cos(p.y * 3.0 + p.x * 3.0 + time * PI2);


    // Just me pseudo randomly adding snowflakes
    // Could be optimized by not calling the function 8 times
    // (using some field distortion )
    col += snow_flake(p, 1.0);
    col += snow_flake(p + vec2(0.2, -0.1), 4.0);
    col += snow_flake(p * 2.0 + vec2(-0.4, -0.5), 5.0);
    col += snow_flake(p * 1.0 + vec2(-0.2, 0.4), 9.0);
    col += 2.0 * snow_flake(p * 1.0 + vec2(0.4, -0.4), 5.0);
    col += snow_flake(p * 1.0 + vec2(-1.2, 1.2), 9.0);
    col += snow_flake(p * 1.0 + vec2(2.4, -1.2), 5.0);
    col += snow_flake(p * 1.0 + vec2(-1.2, 1.1), 9.0);


    return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){

	vec2 uv = fragCoord.xy / iResolution.x;
    float x = uv.x;
    float y = uv.y;

    vec2 p = vec2(x, y) - vec2(0.5);

    vec4 col = vec4(0.0, 0.0, 0.0, 1.0);

    col += 0.3 * snow(p * 2.0);
    col += 0.2 * snow(p * 4.0 + vec2(time, 10.0));
    col += 0.1 * snow(p * 8.0);

    col.a = 0.1;

    fragColor = col;
}

// restituisce le coordinate di p modulari tra -interval/2 e +interval/2 
vec2 onRep(vec2 p, float interval) {
	return mod(p, interval) - interval * 0.5;
}

float barDist(vec2 p, float interval, float width) {
	return length(max(abs(onRep(p, interval)) - width, 0.0));
}

float cubeDist(vec3 p, float interval, float width) {
	return length(max(abs(mod(p, interval) - interval * 0.5) - width, 0.0));
}

float sphereDist(vec3 p, float interval, float width) {
	return length(0.5 * interval - abs(mod(p + vec3(0, 0, 3.0 * iTime), interval) - interval * 0.5)) - width;
}

vec3 rotate(vec3 p, float angle, vec3 axis){
	vec3 a = normalize(axis);
	float s = sin(angle);
	float c = cos(angle);
	float r = 1.0 - c;
	mat3 m = mat3(
		a.x * a.x * r + c,
		a.y * a.x * r + a.z * s,
		a.z * a.x * r - a.y * s,
		a.x * a.y * r - a.z * s,
		a.y * a.y * r + c,
		a.z * a.y * r + a.x * s,
		a.x * a.z * r + a.y * s,
		a.y * a.z * r - a.x * s,
		a.z * a.z * r + c
	);
	return m * p;
}

// distance estimator
float sceneDist(vec3 p) {
	float bar_x = barDist(p.yz, 0.5, 0.0125);
	float bar_y = barDist(p.xz, 0.5, 0.0125);
	float bar_z = barDist(p.xy, 0.5, 0.0125);
	return min(min(min(bar_x, bar_y), bar_z), min(cubeDist(p, 0.5, 0.0375), sphereDist(p, 1.0, 0.05)));
}

vec3 lerp(vec3 a, vec3 b, float f) {
	return a + f * (b - a);
}

// ----------------------------------------------------------------------------------------------------------------------------

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
//    vec2 uv = fragCoord/iResolution.xy;
	vec2 p = ( fragCoord.xy * 2. - iResolution.xy ) / min(iResolution.x, iResolution.y);
	vec3 cameraPos = vec3(0.25 * iTime + 0.5, 0.125 * iTime, 0.25 * iTime + 0.125);

	vec2 a = 0.8 * p;
	vec3 rayDirection = normalize(vec3(sin(a.x) * cos(a.y), sin(a.y), cos(a.x) * cos(a.y)));

	rayDirection = rotate(rayDirection, 0.5 * iTime, vec3(0., 0., 1.));
	rayDirection = rotate(rayDirection, 0.5 * iTime, vec3(0., 1., 0.));

	float depth = 0.0;

	vec3 col = vec3(0.8, 0.9, 1.0);

	// ray marching
	for (int i = 0; i < 99; i++) {
		vec3 rayPos = cameraPos + rayDirection * depth;
		float dist = sceneDist(rayPos);
		if (dist < 0.0001) {
			col = vec3(1.0, 0.9, 0.7) * (0.2 + 0.01 * float(i));
			col = lerp(col, vec3(0.8, 0.9, 1.0), 0.01 * float(i));
			break;
		}
		depth += dist;
	}

	// effetto vignette
	vec2 sv = sin(fragCoord.xy / iResolution.xy * 3.141592);
	col = col * (0.5 + 0.5 * sv.x * sv.y);

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define IT 32.
#define SD .02
#define MD 20.

float smin(float a, float b, float k) {
    float h = clamp(.5+.5*(b-a)/k, 0., 1.);
    return mix(b,a,h)-k*h*(1.-h);
}

vec3 h33(vec3 p) {
    p = fract(p*365.8193+95.);
	p *= 17.;
    return fract(vec3(p.x*p.y,p.y*p.z,p.z*p.x)+(p.x+p.y+p.z));
}

float voronoi(vec3 p) {
    vec3 c = floor(p);
    float d = 10.;
    vec3 s;
    for (s.x = -1.; s.x < 2.; s.x++)
        for (s.y = -1.; s.y < 2.; s.y++)
            for (s.z = -1.; s.z < 2.; s.z++)
            	d = smin(d,length(p-c-.5-s+sin(h33(c+s)*(iTime*.5+10.))*.5),.1);
    return d;
}


float dist(vec3 p) {
    return .7-voronoi(p*.5)+2.-min(length(p),2.);
}

vec3 raymarch(vec3 ro, vec3 rd) {
    vec3 p = ro;
    float td;
    for (float i; i < IT; i++) {
        float d = dist(p);
        td += d;
        p += rd*d;
        if (d < SD || td > MD) break;
    }
    return p;
}

vec3 normal(vec3 p) {
    vec2 o = vec2(SD,0);
    return normalize(vec3(
    dist(p+o.xyy)-dist(p-o.xyy),
    dist(p+o.yxy)-dist(p-o.yxy),
    dist(p+o.yyx)-dist(p-o.yyx)));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec3 ro = vec3(0);
    vec3 rd = normalize(vec3(uv,1));
    vec3 p = raymarch(ro,rd);
    float l = max(0.,dot(normal(p),normalize(vec3(1,3,-2))));
    vec3 col = dist(p) < SD * 2. ? mix(vec3(1.),vec3(0.3,0.1,0.6),pow(length(p-ro)/MD,.2)) * l : vec3(0);

    fragColor = vec4(col,1.);
}

// antialiasing, adapted from https://shadertoyunofficial.wordpress.com/2019/01/02/programming-tricks-in-shadertoy-glsl/
#define S(v, v0, r) smoothstep(6.0 / R.y, -6.0 / R.y, length(v - (v0)) - (r))

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // normalise the coordinates
    vec2 R = iResolution.xy, U = ((2.0 * fragCoord.xy) - R) / min(R.x, R.y);
    // grid
    vec2 grid = (fract(U * 2.0) * 2.0) - 1.0;
    // 2 phase offset square waves
    vec2 offset = step(fract(vec2(iTime, iTime + 1.0) / 2.0), vec2(0.5));
    // translation
    grid -= fract(iTime) * sign(grid.yx) * offset;
    // render dots
    vec3 c = vec3(S(fract(grid), vec2(0.5), 0.3));
    fragColor = vec4(c, 0.0);
}

#define Rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))
#define antialiasing 3./min(iResolution.y,iResolution.x)

float dBox2d(vec2 p, vec2 b) {
    return max(abs(p.x) - b.x, abs(p.y) - b.y);
}

float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,vec2(0))) + min(max(d.x,d.y),0.0);
}

float sdLine( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

float sdTriangle( in vec2 p0, in vec2 p1, in vec2 p2, in vec2 p )
{
    vec2 e0 = p1 - p0;
    vec2 e1 = p2 - p1;
    vec2 e2 = p0 - p2;

    vec2 v0 = p - p0;
    vec2 v1 = p - p1;
    vec2 v2 = p - p2;

    vec2 pq0 = v0 - e0*clamp( dot(v0,e0)/dot(e0,e0), 0.0, 1.0 );
    vec2 pq1 = v1 - e1*clamp( dot(v1,e1)/dot(e1,e1), 0.0, 1.0 );
    vec2 pq2 = v2 - e2*clamp( dot(v2,e2)/dot(e2,e2), 0.0, 1.0 );

    float s = sign( e0.x*e2.y - e0.y*e2.x );
    vec2 d = min( min( vec2( dot( pq0, pq0 ), s*(v0.x*e0.y-v0.y*e0.x) ),
                       vec2( dot( pq1, pq1 ), s*(v1.x*e1.y-v1.y*e1.x) )),
                       vec2( dot( pq2, pq2 ), s*(v2.x*e2.y-v2.y*e2.x) ));

    return -sqrt(d.x)*sign(d.y);
}

float sdEllipse( in vec2 p, in vec2 ab )
{
    p = abs(p); if( p.x > p.y ) {p=p.yx;ab=ab.yx;}
    float l = ab.y*ab.y - ab.x*ab.x;
    float m = ab.x*p.x/l;      float m2 = m*m; 
    float n = ab.y*p.y/l;      float n2 = n*n; 
    float c = (m2+n2-1.0)/3.0; float c3 = c*c*c;
    float q = c3 + m2*n2*2.0;
    float d = c3 + m2*n2;
    float g = m + m*n2;
    float co;
    if( d<0.0 )
    {
        float h = acos(q/c3)/3.0;
        float s = cos(h);
        float t = sin(h)*sqrt(3.0);
        float rx = sqrt( -c*(s + t + 2.0) + m2 );
        float ry = sqrt( -c*(s - t + 2.0) + m2 );
        co = (ry+sign(l)*rx+abs(g)/(rx*ry)- m)/2.0;
    }
    else
    {
        float h = 2.0*m*n*sqrt( d );
        float s = sign(q+h)*pow(abs(q+h), 1.0/3.0);
        float u = sign(q-h)*pow(abs(q-h), 1.0/3.0);
        float rx = -s - u - c*4.0 + 2.0*m2;
        float ry = (s - u)*sqrt(3.0);
        float rm = sqrt( rx*rx + ry*ry );
        co = (ry/sqrt(rm-rx)+2.0*g/rm-m)/2.0;
    }
    vec2 r = ab * vec2(co, sqrt(1.0-co*co));
    return length(r-p) * sign(p.y-r.y);
}

float recycleLogo(vec2 p) {
    vec2 pref = p;
    vec2 scale = vec2(3.0);

    p *= scale;
    mat2 rot1 = Rot(radians(30.0));
    mat2 rot2 = Rot(radians(-30.0));
    mat2 rot3 = Rot(radians(150.0));
    float recycleRectA1 = dBox2d((p+vec2(0.1,0.0))*rot1,vec2(0.08,0.15));
    float recycleRectA2 = dBox2d((p+vec2(-0.1,0.0))*rot2,vec2(0.08,0.15));
    float arrowA = sdTriangle(vec2(0.15,0.0),vec2(-0.15,0.0),vec2(0.0,0.11),(p+vec2(-0.18,0.13))*rot3);
    float recycleRectA = min(arrowA,max((p.y-0.1),min(recycleRectA1,recycleRectA2)));

    p += vec2(0.28,0.5);
    p *= Rot(radians(-120.0));
    float recycleRectB1 = dBox2d((p+vec2(0.1,0.0))*rot1,vec2(0.08,0.15));
    float recycleRectB2 = dBox2d((p+vec2(-0.1,0.0))*rot2,vec2(0.08,0.15));
    float arrowB = sdTriangle(vec2(0.15,0.0),vec2(-0.15,0.0),vec2(0.0,0.11),(p+vec2(-0.18,0.13))*rot3);
    float recycleRectB = min(arrowB,max((p.y-0.1),min(recycleRectB1,recycleRectB2)));
    p = pref;

    p *= scale;
    p += vec2(-0.28,0.5);
    p *= Rot(radians(120.0));
    float recycleRectC1 = dBox2d((p+vec2(0.1,0.0))*rot1,vec2(0.08,0.15));
    float recycleRectC2 = dBox2d((p+vec2(-0.1,0.0))*rot2,vec2(0.08,0.15));
    float arrowC = sdTriangle(vec2(0.15,0.0),vec2(-0.15,0.0),vec2(0.0,0.11),(p+vec2(-0.18,0.13))*rot3);
    float recycleRectC = min(arrowC,max((p.y-0.1),min(recycleRectC1,recycleRectC2)));
    p = pref;

    return min(min(recycleRectA,recycleRectB),recycleRectC);
}

float plasticLogo(vec2 p) {
    vec2 pref = p;
    vec2 scale = vec2(2.4);

    p *= scale;

    mat2 rot1 = Rot(radians(180.0));
    float rectA1 = dBox2d((p+vec2(0.25,0.0)),vec2(0.05,0.1));
    float rectA2 = dBox2d((p+vec2(0.0,-0.05)),vec2(0.2,0.05));
    float rectA3 = dBox2d((p+vec2(-0.25,-0.03)),vec2(0.05,0.07));
    float arrowA = sdTriangle(vec2(0.09,0.0),vec2(-0.09,0.0),vec2(0.0,0.1),(p+vec2(-0.25,0.03))*rot1);
    float prectA = min(arrowA,min(min(rectA1,rectA2),rectA3));

    float rectB1 = dBox2d((p+vec2(0.25,0.4)),vec2(0.05,0.15));
    float rectB2 = dBox2d((p+vec2(0.0,0.5)),vec2(0.2,0.05));
    float rectB3 = dBox2d((p+vec2(-0.25,0.48)),vec2(0.05,0.07));
    float arrowB = sdTriangle(vec2(0.09,0.0),vec2(-0.09,0.0),vec2(0.0,0.1),(p+vec2(0.25,0.25)));
    float prectB = min(arrowB,min(min(rectB1,rectB2),rectB3));

    mat2 rot2 = Rot(radians(30.0));
    float r1 = length(p+vec2(-0.05,0.2))-0.03;
    float r2 = length(p+vec2(-0.05,0.2))-0.07;
    float r = max(-r1,r2);
    float b =  dBox2d((p+vec2(0.0,0.25)),vec2(0.08,0.025));
    float b1 =  dBox2d((p+vec2(-0.04,0.31))*rot2,vec2(0.025,0.08));
    float charA = min(min(r,b),b1);

    float b2 =  dBox2d((p+vec2(-0.25,0.18)),vec2(0.06,0.02));
    float b3 =  dBox2d((p+vec2(-0.29,0.31))*rot2,vec2(0.025,0.08));
    float b4 =  dBox2d((p+vec2(-0.26,0.25)),vec2(0.09,0.025));
    float charB = max(p.x-0.34,min(min(b2,b3),b4));

    return min(min(min(prectA,prectB),charA),charB);
}

float paperLogo(vec2 p) {
    vec2 pref = p;
    vec2 scale = vec2(1.6);

    p *= scale;

    p *= Rot(radians(45.0)+iTime*0.5);
    mat2 rot1 = Rot(radians(90.0));
    float e = sdEllipse(p,vec2(0.27,0.32));
    float e2 = sdEllipse(p,vec2(0.22,0.27));
    float b =  dBox2d((p),vec2(0.06,0.5));
    float arrowA = sdTriangle(vec2(0.09,0.0),vec2(-0.09,0.0),vec2(0.0,0.1),(p+vec2(0.08,-0.28))*rot1);
    float arrowB = sdTriangle(vec2(0.09,0.0),vec2(-0.09,0.0),vec2(0.0,0.1),(p+vec2(-0.08,0.28))*rot1*-1.0);
    float res = min(min(max(-b,max(-e2,e)),arrowA),arrowB);
    p = pref;

    p *= scale;
    vec2 pos = vec2(0.02,0.0);
    float b1 = dBox2d((p+vec2(0.07,-0.095)+pos)*Rot(radians(35.0)),vec2(0.015,0.05));
    float b2 = dBox2d((p+vec2(0.03,-0.04)+pos)*Rot(radians(35.0)),vec2(0.015,0.05));
    float b3 = dBox2d((p+vec2(0.07,-0.02)+pos)*Rot(radians(-30.0)),vec2(0.015,0.05));
    float b4 = dBox2d((p+vec2(0.055,0.02)+pos),vec2(0.06,0.015));
    float b5 = dBox2d((p+vec2(0.055,0.07)+pos),vec2(0.015,0.06));
    float b6 = dBox2d((p+vec2(0.11,0.09)+pos)*Rot(radians(20.0)),vec2(0.012,0.03));
    float b7 = dBox2d((p+vec2(0.00,0.09)+pos)*Rot(radians(-20.0)),vec2(0.012,0.03));
    res = min(res,min(b1,min(b2,min(b3,min(b4,min(b5,min(b6,b7)))))));

    float b8 = dBox2d((p+vec2(-0.10,-0.12)+pos)*Rot(radians(70.0)),vec2(0.012,0.06));
    float b9 = dBox2d((p+vec2(-0.05,0.01)+pos),vec2(0.012,0.11));
    float b10 = dBox2d((p+vec2(-0.10,0.0)+pos),vec2(0.06,0.012));
    float b11 = dBox2d((p+vec2(-0.13,-0.01)+pos),vec2(0.012,0.13));
    float b12 = dBox2d((p+vec2(-0.06,0.11)+pos)*Rot(radians(-5.0)),vec2(0.038,0.012));
    float b13 = dBox2d((p+vec2(-0.15,0.10)+pos)*Rot(radians(-45.0)),vec2(0.03,0.013));

    res = min(res,min(b8,min(b9,min(b10,min(b11,min(b12,b13))))));

    return res;
}

float pvcLogo(vec2 p) {
