float corner(vec2 p){
  bool t = sign(p.y)+sign(p.x) == 2.;
  return t ? length(p) : max(p.x,p.y);
}

float cylinf(vec3 p, vec2 dim){
  vec2 crds = vec2(length(p.xy), p.z);
  return corner(crds-dim);
}

float cyl(vec3 p, vec2 dim) {
  vec2 crds = vec2(length(p.xy), abs(p.z));
  return corner(crds-dim);
}

#define FK(k) floatBitsToInt(cos(k))^floatBitsToInt(k)
float hash(vec2 p) {
  int x = FK(p.x);int y = FK(p.y);
  return float((x*x-y)*(y*y+x)+x)/2.14e9;
}

float PAW = acos(-1.)*1.5;
float blackles_correction = 2./1.5;

vec2 piston(vec3 p, vec2 id, float scale) {
  vec2 center = id/scale;

  vec2 offset = vec2(hash(id*vec2(0.5,0.69)),hash(id*vec2(0.85,0.19)));
  center += offset;

  float off1 = hash(id)*PAW*blackles_correction;
  float off2 = hash(id+vec2(off1,cos(off1)))*PAW*blackles_correction;
  float off3 = hash(id+vec2(off2,cos(off2)))*PAW*blackles_correction;
  float height = sin(iTime + off1) + sin(iTime*1.9 + off2)*0.9 + sin(iTime*2.9 + off3)*0.7;

  float rod = cylinf(p-vec3(center,0), vec2(0.6,height));
  float scop = cylinf(p-vec3(center,0.), vec2(1.,(10.+height)/2.-10.));
  float best = min(rod, scop);
  float cap = cyl(p-vec3(center,height), vec2(1.2,0.1));
  if (cap < best) {
    return vec2(cap-0.05,1);
  }
  return vec2(best-0.1,0);
}

float mat = 0.;
vec2 bestid = vec2(0);
float scene(vec3 p) {
  float scale =0.2;

  vec2 id = floor(p.xy*scale);
  float dist = 10000.;
  for (int i = -1; i < 2; i++) {
    for (int j = -1; j < 2; j++) {
      vec2 currid = vec2(id)+vec2(i,j);
      vec2 pi = piston(p, currid, scale);
      if (pi.x < dist) {
        dist = pi.x;
        mat = pi.y;
        bestid = currid;
      }
    }
  }
  return dist;
}

vec3 norm(vec3 p) {
  mat3 k = mat3(p,p,p) - mat3(0.001);
  return normalize(scene(p) - vec3(scene(k[0]),scene(k[1]),scene(k[2])));
}

void erot(inout vec3 p, vec3 ax, float ro) {
  p = mix(dot(ax,p)*ax,p,cos(ro)) + sin(ro)*cross(ax,p);
}

float samp(vec3 p, vec3 i, float dist) {
  return tanh(2.*scene(p + i*dist)/dist)*0.5+0.5;
}

vec3 planeinterp(vec3 a, vec3 b, vec3 c, vec3 d, vec2 k) {
  a*=a;
  b*=b;
  c*=c;
  d*=d;
  return sqrt(mix(mix(a, b, k.x), mix(c, d, k.x),k.y));
}

vec3 prettycol(vec2 k) {
  return planeinterp(vec3(1,0,0), vec3(1,0.1,0.5), vec3(0.1,0.6,0.9), vec3(0.5,0.5,1), k*0.5+0.5);
}

vec3 shade(vec3 p, vec3 cam) {
  vec2 localbest = bestid;
  vec3 col = vec3(0.8);
  vec2 colr = vec2(hash(localbest*vec2(0.75,0.19)),hash(localbest*vec2(0.35,0.59)));
  if (mat == 1.) {
    col = mix(vec3(0.8),prettycol(colr),0.6);
  } else {
    col *= 0.9+colr.x*0.2;
  }
  vec3 n = norm(p);
  vec3 i = reflect(cam, n);
  float frensel = 1.-pow(abs(dot(n, cam)),2.)*0.5;
  float s = (samp(p, i, 0.5)+samp(p, i, 2.)+samp(p, i, 4.))/3.;
  return col*frensel*s;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
  uv -= 0.5;
  uv /= vec2(iResolution.y / iResolution.x, 1);

  vec3 cam = normalize(vec3(1.4, uv));
  vec3 init = vec3(-9,0,5);
  float ang1 = 0.6;
  float ang2 = 0.6;
  erot(cam, vec3(0,1,0), ang1);
  erot(init, vec3(0,1,0), ang1);
  init += vec3(iTime*5.,0,0);
  erot(cam, vec3(0,0,1), ang2);
  erot(init, vec3(0,0,1), ang2);

  vec3 p = init;
  bool hit = false;
  for (int i = 0; i < 100; i++) {
    float dist = scene(p);
    if (abs(dist) < 0.001) { hit = true; break; }
    if (distance(init, p) > 100.) break;
    p += dist*cam;
  }
  fragColor.xyz = hit ? shade(p,cam) : vec3(0.5);
  fragColor.xyz = smoothstep(vec3(0.1,0.1,0.05),vec3(0.9),fragColor.xyz) + hash(uv*iTime)*0.025;
}

// constants

#define PI (3.14159265)
#define EARTH_RADIUS (6370997.0)

const float kOuterRadius = EARTH_RADIUS * 1.025;
const float kOuterRadius2 = kOuterRadius * kOuterRadius;
const float kInnerRadius = EARTH_RADIUS;
const float kInnerRadius2 = kInnerRadius * kInnerRadius;

const float kScale = 1.0 / (kOuterRadius - kInnerRadius);
const float kScaleDepth = 0.25;
const float kScaleOverScaleDepth = kScale / kScaleDepth;
const float kCameraHeight = 0.0001;

const float kRAYLEIGH = 0.005;
const float kMIE = 0.01;

const float kR4PI = kRAYLEIGH * 4.0 * PI;

// Wavelength factors for scattering
const vec3 k_lambda_variance = vec3(0.0, 0., 0.);
const vec3 k_lambda = vec3(0.65, .57, 0.475) - k_lambda_variance;

const float kM4PI = kMIE * 4.0 * PI;

const float mie_g = -0.99;
const float mie_g2 = mie_g * mie_g;

float saturate(float x) {
    return clamp(x, 0.0, 1.0);
}

vec3 saturate(vec3 x) {
    return clamp(x, vec3(0.0), vec3(1.0));
}

float rayleigh_phase(float cos2) {
    return 0.75 * (1.0 + cos2);
}

float mie_phase(float c, float cos2) {
    float temp = 1.0 + mie_g2 - 2.0 * mie_g * c;
    temp = smoothstep(0.0, 0.01, temp) * temp;
    //temp = pow(temp, 1.5);
    temp = max(temp, 0.0001);
    return 1.5 * ((1.0 - mie_g2) / (2.0 + mie_g2)) * (1.0 + cos2) / temp;
}

float scale(float inCos) {
    float x = 1.0 - inCos;
    return 0.25 * exp(-0.00287 + x*(0.459 + x*(3.83 + x*(-6.80 + x*5.25))));
}

vec3 renderSky(in vec3 viewDir, in vec3 lightDir) {

    //lightDir = normalize(vec3(0., 0., 1.0));
    viewDir = normalize(viewDir);
    //viewDir = lightDir;

    float height = kInnerRadius + kCameraHeight;
    vec3 cameraPos = vec3(0.0, height, 0.0);

    float depth = exp(kScaleOverScaleDepth * (-kCameraHeight));
    // angle between eye ray and camera height
    float startAngle = dot(viewDir, cameraPos) / height;
    float startAngleScale = scale(startAngle);
    float startOffset = depth * startAngleScale;

	// Calculate the length of the "atmosphere"
	float far = sqrt(kOuterRadius2 + kInnerRadius2 * viewDir.y * viewDir.y - kInnerRadius2) - kInnerRadius * viewDir.y;

	vec3 pos = cameraPos + far * viewDir;

    // Initialize the scattering loop variables
	float sampleLength = far / 2.0;
	float scaledLength = sampleLength * kScale;
	vec3 sampleRay = viewDir * sampleLength;
	vec3 samplePoint = cameraPos + sampleRay * 0.5;

    vec3 invLambda = pow(k_lambda, vec3(-4.0));
    vec3 front = vec3(0.0);

    float brightness = 20.0;// = lightDir.y <= 0.0 ? 1.0 : 20.0;
    const float range = 0.1;
    /*
    if(lightDir.y >= 0.0) {
        brightness = 20.0;
    	if(lightDir.y <= range)
        	brightness = mix(10.0, 20.0, saturate(lightDir.y / range));
    }
    else {
        brightness = 1.0;
        if(lightDir.y >= -range)
            brightness = mix(10.0, 1.0, saturate(-lightDir.y / range));
    }

    lightDir.y = abs(lightDir.y);
    */
    for (int i = 0; i < 1; ++i) {
        float height = length(samplePoint);
		float depth = exp(kScaleOverScaleDepth * (kInnerRadius - height));
        float lightAngle = dot(lightDir, samplePoint) / height;
		float cameraAngle = dot(viewDir, samplePoint) / height;
		float scatter = (startOffset + depth*(scale(lightAngle) - scale(cameraAngle)));
        vec3 atten = exp(-clamp(scatter, 0.0, 50.0) * (invLambda * kR4PI + kM4PI));

        front += atten * (depth * scaledLength);
        samplePoint += sampleRay;
    }

    vec3 c1 = front * invLambda * kRAYLEIGH * brightness;

    vec3 c2 = front * kMIE * brightness;

    float eyeCos = -dot(viewDir, lightDir);
    float eyeCos2 = eyeCos * eyeCos;

    float rayleigh = rayleigh_phase(eyeCos2);
    float mie = mie_phase(eyeCos, eyeCos2);

    // mie = 0

    vec3 col = step(0.0, viewDir.y) * sqrt(rayleigh * c1 + mie * c2);

    return col;
}

vec3 rotateX(in vec3 v, in float theta) {
    float c = cos(theta);
    float s = sin(theta);

    mat3 m = mat3(1., 0., 0.,
                0., c, s,
                0., -s, c);

    return m * v;
}

float sunAngle = 4.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //screen coords
	vec2 q = fragCoord.xy/iResolution.xy;
	vec2 v = -1.0+2.0*q;
	v.x *= iResolution.x/iResolution.y;

	//camera ray
	vec3 dir = normalize(vec3(v.x, v.y+.5, -1.5));

    //dir = cross(dir, vec3(1.0, 0.0, 0.0));
    //dir = vec3(0.0, 0.8, 1.);

    sunAngle = -iTime * .3;

    //dir = rotateX(dir, PI * 1.9);

    vec3 sun = normalize(rotateX(vec3(0.0, 0.0, 1.0), sunAngle));

    vec3 sky = renderSky(dir, sun);
    fragColor = vec4(sky, 1.0);
}

