#define MAX_STEPS 100
#define MAX_DIST 100.0
#define SURF_DIST 0.01

//球形
float sdSphere(vec3 point, vec3 offset, float radius) {
	return length(point - offset) - radius;
}

//平面
float sdPlane(vec3 point, vec3 offset, vec3 normal) {
	return dot(point - offset, normal.xyz);
}

//计算射线上的点到物体的最小距离
float GetDist(vec3 point) {

    float sphereDistance = sdSphere(point, vec3(0, 1, 6), 1.0);
    float planeDistance = sdPlane(point, vec3(0, 0, 6), normalize(vec3(0, 1, -0.4)));

    float minDistance = min(sphereDistance, planeDistance);

    return minDistance;
}

//使用光线步进
float RayMarch(vec3 rayOrigin, vec3 rayDrection) {
	float distanceFromOrigin = 0.0;

    for(int i = 0; i < MAX_STEPS; i++) {
    	vec3 point = rayOrigin + rayDrection * distanceFromOrigin;
        float minDistance = GetDist(point);
        distanceFromOrigin += minDistance;

        if(distanceFromOrigin > MAX_DIST || minDistance < SURF_DIST) 
            break;
    }

    return distanceFromOrigin;
}

//运用偏移得到法向量
vec3 GetNormal(vec3 hitPoint) {
	float hitDistance = GetDist(hitPoint);
    vec2 epsilon = vec2(.01, 0);

    vec3 hitNormal = hitDistance - vec3(
        	GetDist(hitPoint - epsilon.xyy),
        	GetDist(hitPoint - epsilon.yxy),
        	GetDist(hitPoint - epsilon.yyx)
    	);

    return normalize(hitNormal);
}

//漫反射和其阴影
float GetDiffuseLight(vec3 hitPoint) {
	vec3 lightPos = vec3(0, 5, 6);
    lightPos.xz += vec2(sin(iTime), cos(iTime)) * 10.0;

    vec3 lightDirection = normalize(lightPos - hitPoint);
    vec3 hitNormal = GetNormal(hitPoint);

    float diffuseLight = dot(hitNormal, lightDirection);
    diffuseLight = clamp(diffuseLight, 0.0, 1.0);

    float shadowTolightDist = RayMarch(hitPoint + hitNormal * SURF_DIST * 2.0, lightDirection);
    if(shadowTolightDist < length(lightPos - hitPoint)) 
        diffuseLight *= .1;

    return diffuseLight;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord - .5 * iResolution.xy) / iResolution.y;

    vec3 rayOrigin = vec3(0, 3, -3);
    vec3 rayDirection = normalize(vec3(uv.x, uv.y, 1));

    float dist = RayMarch(rayOrigin, rayDirection);
    vec3 hitPoint = rayOrigin + dist * rayDirection;
    float diffuseLight = GetDiffuseLight(hitPoint);

    vec3 col = vec3(diffuseLight);

    fragColor = vec4(col,1.0);
}

float opSmoothUnion( float d1, float d2, float k )
{
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h);
}

float sdSphere( vec3 p, float s )
{
  return length(p)-s;
} 

float map(vec3 p)
{
	float d = 2.0;
	for (int i = 0; i < 16; i++)
	{
		float fi = float(i);
		float time = iTime * (fract(fi * 412.531 + 0.513) - 0.5) * 2.0;
		d = opSmoothUnion(
            sdSphere(p + sin(time + fi * vec3(52.5126, 64.62744, 632.25)) * vec3(2.0, 2.0, 0.8), mix(0.5, 1.0, fract(fi * 412.531 + 0.5124))),
			d,
			0.4
		);
	}
	return d;
}

vec3 calcNormal( in vec3 p )
{
    const float h = 1e-5; // or some other value
    const vec2 k = vec2(1,-1);
    return normalize( k.xyy*map( p + k.xyy*h ) + 
                      k.yyx*map( p + k.yyx*h ) + 
                      k.yxy*map( p + k.yxy*h ) + 
                      k.xxx*map( p + k.xxx*h ) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    // screen size is 6m x 6m
	vec3 rayOri = vec3((uv - 0.5) * vec2(iResolution.x/iResolution.y, 1.0) * 6.0, 3.0);
	vec3 rayDir = vec3(0.0, 0.0, -1.0);

	float depth = 0.0;
	vec3 p;

	for(int i = 0; i < 64; i++) {
		p = rayOri + rayDir * depth;
		float dist = map(p);
        depth += dist;
		if (dist < 1e-6) {
			break;
		}
	}

    depth = min(6.0, depth);
	vec3 n = calcNormal(p);
    float b = max(0.0, dot(n, vec3(0.577)));
    vec3 col = (0.5 + 0.5 * cos((b + iTime * 3.0) + uv.xyx * 2.0 + vec3(0,2,4))) * (0.85 + b * 0.35);
    col *= exp( -depth * 0.15 );

    // maximum thickness is 2m in alpha channel
    fragColor = vec4(col, 1.0 - (depth - 0.5) / 2.0);
}

