#define R_BIG 0.44
#define R_MIDDLE 0.2
#define R_SMALL 0.05
#define MID_CENTER vec2(0.0, R_MIDDLE)
#define EPS 0.001
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    uv *= mat2(cos(iTime), sin(iTime), -sin(iTime), cos(iTime)); // rotate
    uv.y *= 1. + sin(iTime*2.0)*0.2; // morph - comment out for normal looking yy

    vec2 uuv = vec2(uv.x, abs(uv.y));
    float left = step(0.0, -uv.x);
    float up = step(0.0, uv.y);
    float bigDist = length(uv);
    float smallDist = length(uuv - MID_CENTER);

    float big = smoothstep(R_BIG, R_BIG - EPS, bigDist);
    float small = smoothstep(R_SMALL, R_SMALL - EPS, smallDist);
    float middle = smoothstep(R_MIDDLE, R_MIDDLE - EPS, smallDist);

    float light = smoothstep(R_MIDDLE * 2. + EPS, R_MIDDLE * 2., bigDist);
    light *= left;
    light -= (middle - 2. * small) * up;
    light += middle * (1.0 - up) * (1.0 - left); // (1.0 - left) because left part is already white
    light -= small * (1.0 - up);

    // generate final color
    vec3 col = vec3(1.0 - big + light);

    // Output to screen
    fragColor = vec4(col,1.0);
}

