void mainImage(out vec4 O, in vec2 U)
{
    vec2 R = iResolution.xy;
    U = (1. - 2. * iMouse.x / R.x) * (U - .5 * R) / min(R.x, R.y);
    float m = 6.*texture(iChannel0, vec2(0.05)).x;
    vec3 ht = smoothstep(0., 2., .25 - dot(U, U)) * vec3(U, m-4.),
         n = 100. * normalize(ht - vec3(0, -.15 * fract(.00085 * iTime), .65)), 
         p = n;
    for (float i = 0.; i <= 25.; i++)
    {
        p = 20. * n + vec3(cos(.325 * iTime - i - p.x) + cos(.325 * iTime + i - p.y), 
                           sin(i - p.y) + cos(i + p.x), 3);
        p.xy = n.xy + (.5 - cos(i) * p.xy + sin(i) * vec2(p.y, -p.x));
    }
    float tx = 6. * sqrt(dot(vec3(0, 6, 4), -p));
    O = vec4(pow(sin(vec3(0, 1, 1.57) - tx) * .35 + .5, vec3(1.5)), 1);
}

// Fork of "Isosurface Heart" by klk. https://shadertoy.com/view/XtVSRh

// Many Thanks to IQ for wonderful idea of signed distance field!

#define PI 3.1415926535897932384626433832795

#define float3 vec3
#define float2 vec2
#define float4 vec4
#define float3x3 mat3

const float MAX_RAY_LENGTH=10000.0;

void RayPlane(float3 tp0, float3 dp1, float3 dp2, float3 rp0, float3 rd, out float t, out float3 uv, out float3 n)
{
	float3 dp0=rp0-tp0;

	float3 dett =cross(dp1,dp2);
	float3 detuv=cross(dp0,rd);

	float det=(-1.0)/dot(dett,rd);

	float u=(dot(detuv,dp2))*det;
	float v=(dot(detuv,dp1))*det;
	t=(dot(dett ,dp0))*det;
	if(t<0.0)
	{
		t=MAX_RAY_LENGTH;
		return;
	}

	uv=float3(u,v,0.0);
	n=normalize(dett);
}

float Arrows(float2 p, float t)
{
	float2 p1=float2(p.x+p.y,p.x-p.y);
	float2 f1xy=abs(fract(p1/sqrt(8.0))-0.5)-0.25;
	f1xy=clamp(f1xy*t+0.5,0.0,1.0);
	float f1=mix(f1xy.x,1.0-f1xy.x,f1xy.y);

	float2 fxy=float2(p.x-sqrt(0.125),p.y);
	fxy=abs(fract((fxy*sqrt(2.0)+0.5)/2.0)-0.5)-0.25;
	fxy=clamp(fxy*t/sqrt(2.0)+0.5,0.0,1.0);
	float f=mix(fxy.x,1.0-f1,fxy.y);

	return f;
}

float Checker(float2 p, float t)
{
	float2 fxy=float2(p.x,p.y);
	fxy=abs(fract((fxy+0.5)/2.0)-0.5)-0.25;
	fxy=clamp(fxy*t+0.5,0.0,1.0);
	float f=mix(fxy.x,1.0-fxy.x,fxy.y);

	return f;
}

float PlaneTexture(float2 p, float t)
{
	return Arrows(p,t);
}

// Trace non-SDF objects
void Trace(float3 rp0, float3 rd, out float t, out float3 pos, out float3 n)
{
	float t1=MAX_RAY_LENGTH;
	float3 col1;
	float3 n1;
	RayPlane(float3(0.0,-10.0,0.0),float3(-1.0,0.0,0.0),float3(0.0,0,1.0),rp0, rd, t1, col1, n1);
	pos=rp0+rd*t1;
	t=t1;
}

// Smooth combine functions from IQ
float smin(float a, float b, float k)
{
	float h=clamp(0.5+0.5*(b-a)/k, 0.0, 1.0);
	return mix(b, a, h)-k*h*(1.0-h);
}

float smax( float a, float b, float k)
{
	return -smin(-a,-b,k);
}

float smin( float a, float b)
{
	return smin(a,b,0.1);
}

float smax( float a, float b)
{
	return smax(a,b,0.1);
}

float sq(float x){return x*x;}

float Torus(float x, float y, float z, float R, float r)
{
	return sqrt(sq(sqrt(sq(x)+sq(z))-R)+sq(y))-r;
}

float Torus(vec3 p, float R, float r)
{
	return sqrt(sq(sqrt(sq(p.x)+sq(p.z))-R)+sq(p.y))-r;
}


float Lid(float x, float y, float z)
{
	float v=sqrt(sq(x)+sq(y-0.55)+sq(z))-1.4;
	v=smin(v,Torus(y-2.,x,z,.2,.08),.1);
	v=smax(v,-sqrt(sq(x)+sq(y-0.55)+sq(z))+1.3);
	v=smax(v,sqrt(sq(x)+sq(y-2.5)+sq(z))-1.3);

	v=smax(v,-sqrt(sq(x-.25)+sq(z-.35))+0.05,.05);
	v=smin(v,Torus(x,(y-1.45)*.75,z,.72,.065),.2);
	return v;
}

float Nose(float x, float y, float z)
{
	z-=sin((y+0.8)*3.6)*.15;

	float v=sqrt(sq(x)+sq(z));

	v=abs(v-.3+sin(y*1.6+.5)*0.18)-.05;
	v=smax(v,-y-1.);
	v=smax(v,y-0.85,.075);

	return v;
}

float Teapot(float3 p)
{
	float x=p.x;
	float y=p.y;
	float z=p.z;

	float v=0.0;
	v=sqrt(x*x+z*z)-1.2-sin(y*1.5+2.0)*.4;
	v=smax(v,abs(y)-1.,0.3);



	float v1=sqrt(x*x*4.+sq(y+z*.1)*1.6+sq(z+1.2))-1.0;
	v1=smax(v1,-sqrt(sq(z+1.2)+sq(y+z*.12+.015)*1.8)+.8,.3);

	v=smin(v,Torus(y*1.2+.2+z*.3,x*.75,z+1.25+y*.2,.8,.1),.25);
	v=smin(v,sqrt(sq(x)+sq(y-1.1)+sq(z+1.8))-.05,.32);

	float v3=Nose(x,(y+z)*sqrt(.5)-1.6,(z-y)*sqrt(.5)-1.1);

	v=smin(v,v3,0.2);

	v=smax(v,smin(sin(y*1.4+2.0)*0.5+.95-sqrt(x*x+z*z),y+.8, .2));
	v=smax(v,-sqrt(sq(x)+sq(y+.15)+sq(z-1.5))+.12);

	v=smin(v,Torus(x,y-0.95,z,0.9,.075));
	v=smin(v,Torus(x,y+1.05,z,1.15,.05),0.15);


	float v2=Lid(x,y+.5,z);
	v=min(v,v2);

	return v;
}

float plate0(float3 p)
{
	float v=(length(p.xz)*.8-p.y)/sqrt(1.64);
	v=smin(v,(length(p.xz)*.3-p.y+.7)/sqrt(1.09));
	v=smax(v,-p.y+.8,.1);
	return v;
}

float Plate(float3 p)
{
	float v;
	float vi=plate0(p);
	float vo=plate0(p+float3(0,-.1,0));
	v=smax(vi,-vo);
	v=smax(v,(length(p.xz)*2.+p.y)/sqrt(3.)-3.);
	v=smin(v,Torus(p.x,p.y-.7,p.z,0.8,.025),0.2);
	return v;
}

float Value(float3 p)
{
	float v;
	v=Teapot(p);
	//v=Plate(p);
	return v;
}

struct Ray
{
	vec3 p;
	vec3 d;
};

bool RayMarch(
	const in Ray r, 
	const float startT, const float endT, 
	const float stp, 
	const int N,
	out float t, out float v, out int i)
{
	float t0=startT;
	t=t0;
	v=Value(r.p+r.d*t);

	if(v<0.)
		return true;

	i=0;
	for(int j=0;j<1;j+=0)
	{
		t+=max(v*.85, stp);
		float v1=Value(r.p+r.d*t);
		if(v1<0.)
		{
			// Linear interpolation between two last steps
			t=t0+(t-t0)*v/(v-v1);
			v=Value(r.p+r.d*t);
			return true;
		}
		if(t>endT)
			return false;
		i++;
		if(i>N)
			return false;
		v=v1;
		t0=t;
	}
	return false;
}

float3 CalcNormal(float3 p, float n0)
{
	float3 n;
	float d=0.001;
	n.x=Value(p+float3(d,0.0,0.0))-n0;
	n.y=Value(p+float3(0.0,d,0.0))-n0;
	n.z=Value(p+float3(0.0,0.0,d))-n0;

	n=normalize(n);
	return n;
}

struct Sphere
{
	vec3 p;
	float r;
};

bool RaySphere(in Ray r, in Sphere s, out float t0, out float t1)
{
	float3 l=s.p-r.p;
	float tc=dot(l,r.d);
	if(tc<0.0)
	{
		return false;
	};

	float d2=s.r*s.r+tc*tc-dot(l,l);

	if(d2<0.0)
	{
		return false;
	};

	float thc=sqrt(d2);
	t0=tc-thc;
	t1=tc+thc;
	return true;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float3 campos=float3(-12.0,3.0,0.0);
	float3 look_at=float3(0.0,0.5,0.0);
	float3 up=float3(0,1,0);
	float3 forward;
	float3 right;

	float3 light=float3(0,10,10);

	float T=iTime*0.45;

	light.x=cos(T)*10.0;
	light.z=sin(T)*10.0;
	light.y=5.0;

	float mposx=iMouse.x;
	float mposy=iMouse.y;
	if(iMouse.z<0.0)mposx=-iMouse.z;
	if(iMouse.w<0.0)mposy=-iMouse.w;

	float a1=(0.6+(mposy/iResolution.y-0.5)*0.7)*PI;
	float a2=mposx/iResolution.x*PI*2.0-PI/3.0;

	if(iMouse.y<10.0)
	{
		a1=PI*0.55;
		a2=PI+0.3;
	}

	campos.y=cos(a1)*campos.x;
	float camx=sin(a1)*campos.x;
	campos.x=cos(a2)*camx;
	campos.z=sin(a2)*camx;

	forward=normalize(look_at-campos);
	right=normalize(cross(up,forward));
	up=normalize(cross(forward,right));

	float2 scr = fragCoord.xy /iResolution.xy;
	scr=2.0*scr-1.0;

	float2 scr2ray=scr;
	scr2ray.x*=(iResolution.x/iResolution.y);
	float2 uv=scr2ray;
	float3 ray=normalize(forward+(up*uv.y+right*uv.x)*0.2);

	float3 col=float3(0.0,0.5,0.0);
	float3 n;
	float t;

	float3 fogcol=mix(float3(0.87,0.8,0.83),float3(0.3,0.6,1.0),clamp(1.0-(1.0-ray.y)*(1.0-ray.y),0.,1.));
	fragColor.rgb=fogcol;
	float3 tpos;
	Trace(campos, ray, t, tpos, n);
	col=mix(float3(0.97,0.95,0.83),float3(0.1,0.15,0.4), smoothstep(0.0,1.0,PlaneTexture(tpos.xz*0.2,36000.0/t/t)));
	float3 tolight=normalize(light);

	// Debug visualization of SDF values 
	if(false)
	{
		float t1=MAX_RAY_LENGTH;
		float3 col1;
		float3 colp=vec3(0);
		float3 n1;
		RayPlane(float3(0.,0.,0.),float3(0.0,1.0,0.0),float3(1.0,0,0.0),campos, ray, t1, col1, n1);
		float3 pos=campos+ray*t1;
		if(t1<t)
		{
			t=t1;
			float v=Value(pos);
			colp.r=v>0.?fract(v):0.5;
			colp.b=v<0.?fract(v):0.5;
			colp.g=.5-abs(clamp(fract(v*10.),0.,1.)-0.5);
		}
		col.rgb+=colp.rgb*0.5;
		fragColor.rgb=col;
	}

	if(t<MAX_RAY_LENGTH)
	{
		col=mix(fogcol,col,exp(-t*0.005));
		fragColor.rgb=col;
	}

	{
		float ts0, ts1;
		float3 start=campos;
		float n0;
		Sphere bound=Sphere(vec3(.0,.3,.2),2.5);

		// Try bounding sphere first
		bool hit=RaySphere(Ray(start,ray), bound, ts0, ts1);
		//if(hit)fragColor.rgb*=0.95;
		int nt=-1;

		float tp;

		if(hit)
		{
			hit=RayMarch(Ray(start,ray),ts0,ts1,.025,180,tp,n0,nt);
		}

		if(hit)
		{
			if(tp<t)
			{
				t=tp;
				float3 p=start+ray*tp;
				float3 n=CalcNormal(p,n0);
				if(nt<0)
				{
					n=normalize(p-bound.p);
				}

				float3 halfn=normalize(tolight-ray);

				float lamb=pow(clamp(dot(n,tolight),0.0,1.0),1.0)*0.9+0.1;
				float3 refray=reflect(ray,n);

				float spec1=clamp(dot(halfn,n),0.0,1.0);
				float spec2=clamp(dot(tolight,refray),0.0,1.0);

				float3 reffog=mix(
					float3(0.87,0.8,0.83),
					float3(0.3,0.6,1.0),
					clamp(1.0-(1.0-refray.y)*(1.0-refray.y),0.,1.));

				float3 n1;

				col=lamb*float3(0.78,0.79,0.8);
				float3 rpos;
				float3 rcol;
				float tr;
				Trace(p, reflect(ray,n), tr, rpos, n1);
				float fresn=clamp(1.0-dot(ray,-n),0.0,1.0);
				if(tp<MAX_RAY_LENGTH)
				{
					rcol=mix(float3(0.87,0.85,0.83),float3(0.1,0.12,0.4),
						smoothstep(0.0,1.0,PlaneTexture(rpos.xz*0.2,600.0/tr/tr)));
					rcol=mix(reffog,rcol,exp(-tr*0.02));
				}
				else
				{
					rcol=reffog;
				}
				{
					col=mix(col,rcol,pow(fresn,1.2)*0.50);
					col+=rcol*(pow(fresn,2.6)*0.2+0.1);
					col=mix(col,float3(1,1,1),pow(spec2,40.0)*.4);
					col=mix(col,float3(1,1,1),.8*pow(spec2,180.0));
				}
				fragColor.rgb=col;
			}
		}

		// Color coded steps count
		if(false)
		{
			if(nt>8)
				fragColor.g+=.2;
			if(nt>16)
				fragColor.r+=.4;
			if(nt>32)
				fragColor.g-=.2;
			if(nt>48)
				fragColor.rb+=vec2(-.4,.4);
		}
	}
	fragColor.a=1.;
}

/* "Gears using involute SDF" by Stanislav Pidhorskyi - 2020
 * License Creative Commons Attribution 4.0 International License.
 * Contact: stpidhorskyi@mix.wvu.edu
 */


float Union(float a, float b)
{
	return min(a, b);
}

float SmoothUnion(float a, float b, float k)
{
    float h = clamp(0.5 + 0.5*(b - a) / k, 0.0, 1.0);
    return mix(b, a, h) - k * h * (1.0 - h);
}

float Substraction(float a, float b)
{
    return max(a,-b);
}

float Intersection(float a, float b)
{
    return max(a,b);
}

float SmoothIntersection(float a, float b, float k)
{
    return Substraction(Intersection(a, b), SmoothUnion(Substraction(a, b), Substraction(b, a), k));
}

float Circle(vec2 p, float r)
{
	return length(p) - r;
}

float Circle(float rho, float r)
{
	return rho - r;
}

float Gear(vec2 p, float D, float N, float psi, float alpha, float dir)
{
    float R = D / 2.0;
    /* The Pitch Circle Diameter is the diameter of a circle which by a pure rolling action would transmit 
     * the same motion as the actual gear wheel. It should be noted that in the case of wheels which connect
     * non-parallel shafts, the pitch circle diameter is different for each cross section of the wheel
     * normal to the axis of rotation.
     */

    float rho = length(p);

    float Pd = N / D; // The Diametral Pitch, p_d is the number of teeth per unit length of diameter

    float P = 3.1416 / Pd; // Circular Pitch
    /* The Circular Pitch, p is the length of arc round the pitch circle between corresponding points 
     * on adjacent teeth.
     */

    float a = 1.0 / Pd; // Addendum
    /* The Addendum is the radial length of a tooth from the pitch circle to the tip of the tooth.
     */

    float Do = D + 2.0 * a; // Outside Diameter
    float Ro = Do / 2.0; 

    float h = 2.2 / Pd;

    float innerR = Ro - h - 0.4;

    // Early exit
    if (innerR - rho > 0.0)
        return innerR - rho;

    // Early exit
    if (Ro - rho < -0.2)
        return rho - Ro;

    float Db = D * cos(psi); // Base Diameter 
    float Rb = Db / 2.0; 

    float fi = atan(p.y, p.x) * dir + alpha;

    float alphaStride = P / R;

    float invAlpha = acos(Rb / R);
    float invPhi = tan(invAlpha) - invAlpha;

    float shift = alphaStride / 2.0 - 2.0 * invPhi;

    float fia = mod(fi + shift / 2.0, alphaStride) - shift / 2.0;
    float fib = mod(-fi - shift + shift / 2.0, alphaStride) - shift / 2.0;

    float dista = -1.0e6;
    float distb = -1.0e6;

    if (Rb < rho)
    {
        float acos_rbRho = acos(Rb/rho);

        float thetaa = fia + acos_rbRho;
        float thetab = fib + acos_rbRho;

        float ta = sqrt(rho * rho - Rb * Rb);

        // https://math.stackexchange.com/questions/1266689/distance-from-a-point-to-the-involute-of-a-circle
        dista = ta - Rb * thetaa;
        distb = ta - Rb * thetab;
    }

    float gearOuter = Circle(rho, Ro);
    float gearBase = Circle(rho, Rb);
    float gearLowBase = Circle(rho, Ro - h);
    float crownBase = Circle(rho, innerR);

    float cogs = Intersection(dista, distb);

    float baseWalls = Intersection(fia - (alphaStride - shift), fib - (alphaStride - shift));

    cogs = Intersection(baseWalls, cogs);
    cogs = SmoothIntersection(gearOuter, cogs, 0.01);
    cogs = SmoothUnion(gearLowBase, cogs, Rb - Ro + h);

    cogs = Substraction(cogs, crownBase);
    return cogs;
}

float Alpha(float d)
{
    d *= iResolution.y / 2.0;
	return clamp(-d + 0.7, 0.0, 1.0);
}


float AO(float d, float height)
{
    return 1.0 - atan(height, d) / 3.14159;
}

float SDF(vec2 p)
{
    float N = pow(2.0, mod(floor(iTime / 15.0), 4.0)) * 4.0 + 9.0; // Number of Teeth
    float psi = 3.096e-5 * N * N -6.557e-3 * N + 0.551;// Pressure Angle
	/* The Pressure Angleor Angle of Obliquity  Psi is the angle 
 	 * between the common normal LM and the tangent at the pitch point
 	 */
    float D = 2.8;

    float gearA = Gear(p + vec2(1.5, 0.0), D, N, psi, iTime / 4.0, 1.0);
    float gearB = Gear(p + vec2(1.5 - D, 0.0), D, N, psi, iTime / 4.0, -1.0);
    float dist = Union(gearA, gearB);
    return dist;
}

vec2 normal(vec2 p, float dist)
{
	float dx = SDF(p + vec2(0.0, 1.0) / iResolution.yy) - dist;
	float dy = SDF(p + vec2(1.0, 0.0) / iResolution.yy) - dist;
    return normalize(vec2(dx, dy));
}

vec3 Render(vec2 p)
{
	// gradient
	vec3 color = vec3(0.55, 0.5, 0.45) * (1.3 - length(p / 2.0 / iResolution.xy * iResolution.yy));

	color = mix(color, vec3(0.1, 0.1, 0.2), 0.2 * Alpha(0.5 * SDF((p - vec2(1.0, 0.0)) / 2.0)));

    float dist = SDF(p);

	color *= AO(dist, 0.2);

	color = mix(color, vec3(0.1, 0.1, 0.1), Alpha(dist));

    #ifdef ADD_SPECULAR
    if (dist < 0.0)
    {
    	vec2 n = normal(p, dist);
        vec3 nn = normalize(vec3(n, -dist * 200.1));

        float s = dot(nn, normalize(vec3(1.0, 1.0, 1.0)));

        s = pow(max(s, 0.0), 20.0);

    	color += vec3(s) * clamp(-dist * iResolution.y / 4.0, 0.0, 1.0);
    }
    #endif

    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	
    vec2 uv = (fragCoord.xy  + vec2(0.5)) / iResolution.yy;
    vec2 p = uv.xy * vec2(2.0) - vec2(1.0);
	fragColor = vec4(pow(Render(p), vec3(1.0 / 2.2)), 1.0);
}

//by @etiennejcb
//3d simplex noise from https://www.shadertoy.com/view/XsX3zB

const float PI = 3.1415926535897932384626433832795;

/* discontinuous pseudorandom uniformly distributed in [-0.5, +0.5]^3 */
vec3 random3(vec3 c) {
	float j = 4096.0*sin(dot(c,vec3(17.0, 59.4, 15.0)));
	vec3 r;
	r.z = fract(512.0*j);
	j *= .125;
	r.x = fract(512.0*j);
	j *= .125;
	r.y = fract(512.0*j);
	return r-0.5;
}

/* skew constants for 3d simplex functions */
const float F3 =  0.3333333;
const float G3 =  0.1666667;

/* 3d simplex noise */
float simplex3d(vec3 p) {
	 /* 1. find current tetrahedron T and it's four vertices */
	 /* s, s+i1, s+i2, s+1.0 - absolute skewed (integer) coordinates of T vertices */
	 /* x, x1, x2, x3 - unskewed coordinates of p relative to each of T vertices*/

	 /* calculate s and x */
	 vec3 s = floor(p + dot(p, vec3(F3)));
	 vec3 x = p - s + dot(s, vec3(G3));

	 /* calculate i1 and i2 */
	 vec3 e = step(vec3(0.0), x - x.yzx);
	 vec3 i1 = e*(1.0 - e.zxy);
	 vec3 i2 = 1.0 - e.zxy*(1.0 - e);

	 /* x1, x2, x3 */
	 vec3 x1 = x - i1 + G3;
	 vec3 x2 = x - i2 + 2.0*G3;
	 vec3 x3 = x - 1.0 + 3.0*G3;

	 /* 2. find four surflets and store them in d */
	 vec4 w, d;

	 /* calculate surflet weights */
	 w.x = dot(x, x);
	 w.y = dot(x1, x1);
	 w.z = dot(x2, x2);
	 w.w = dot(x3, x3);

	 /* w fades from 0.6 at the center of the surflet to 0.0 at the margin */
	 w = max(0.6 - w, 0.0);

	 /* calculate surflet components */
	 d.x = dot(random3(s), x);
	 d.y = dot(random3(s + i1), x1);
	 d.z = dot(random3(s + i2), x2);
	 d.w = dot(random3(s + 1.0), x3);

	 /* multiply d by w^4 */
	 w *= w;
	 w *= w;
	 d *= w;

	 /* 3. return the sum of the four surflets */
	 return dot(d, vec4(52.0));
}


float transformValue(float v){
    v = 0.5 + 0.5*v;
    v = pow(v+0.35,7.0);
    return v;
}

//from bookofshaders
vec3 rgb2hsb( in vec3 c ){
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz),
                 vec4(c.gb, K.xy),
                 step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r),
                 vec4(c.r, p.yzx),
                 step(p.x, c.r));
    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)),
                d / (q.x + e),
                q.x);
}

//  Function from Iñigo Quiles
//  https://www.shadertoy.com/view/MsS3Wc
vec3 hsb2rgb( in vec3 c ){
    vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),
                             6.0)-3.0)-1.0,
                     0.0,
                     1.0 );
    rgb = rgb*rgb*(3.0-2.0*rgb);
    return c.z * mix(vec3(1.0), rgb, c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = fragCoord.xy/iResolution.x;
	float offset = 0.9*simplex3d(vec3(9.*p+vec2(0.,-0.3*iTime),0.2*iTime))*clamp((2.0-5.*length(p-vec2(0.5,0.25))),0.,2.) + 10.*length(p-vec2(0.5,0.25));
    //float offset2 = simplex3d(vec3(123)+vec3(10.*p+vec2(-1.*iTime,0),0.5*iTime));

    float change = 9.0;

    float scl = 5.;

    float value = simplex3d(vec3(change*(iTime-offset),scl*p.x,scl*p.y));
	float value2 = simplex3d(vec3(1.1*change*(iTime-offset),123.+scl*p.x,scl*p.y));
    float value3 = simplex3d(vec3(1.5*change*(iTime-offset),1234.+scl*p.x,scl*p.y));
    //float value4 = simplex3d(vec3(1.5*change*(iTime-offset),3234.+scl*p.x,scl*p.y));

	value = transformValue(value);
    value2 = transformValue(value2);
    value3 = transformValue(value3);

    vec3 color0 = vec3(value+value2,value2+0.*value3,value3+value);

    vec3 rgb = clamp(color0,0.,1.);

    vec3 hsb = rgb2hsb(rgb);

    hsb.x += 0.3*sin(13.*length(p-vec2(0.5,0.25))-1.*iTime)+0.15*iTime;

    hsb.x = mix(hsb.x,0.5,0.7);

    hsb.y += 0.2*sin(14.*length(p-vec2(0.5,0.25))-1.1*iTime+0.5)-0.4;

    rgb = hsb2rgb(hsb);

	fragColor = vec4(rgb,1.0);
	return;
}

//Carter S. Jan 2020
//Basic Raytrace of a Moving Sphere with Reflections (Basic Shadows).
//normally these checks would be done recursively but for educational purpses it is often
//easier to see it done iteratively
//We are starting to get to the point where more elegant ways of checking intersections are needed
//not just for performance but for ease of extending the code (Thank goodness this is just for learning)

#define PI 3.14159265359

struct Ray {
	vec3 origin; // Origin of ray (not to be confused with world origin)
	vec3 direction; // Direction Normalized vector
};

struct Sphere {
	vec3 center;  // Center of the Sphere
	float radius; // Radius of the Sphere
};

struct Plane {
	vec3 normal; //Normal vector
    vec3 point; //Some point such that for all points p in the plane dot((p-point),normal) == 0
};

//Simplest light source
struct PointLight {
    vec3 position; //3d point in space
    vec4 color; //RGBA color
};


//Move the Sphere over time
void AnimateSphere(inout Sphere sphere) {
    //Size of the animation loop
	float loopRadius = 4.0;
    //initial sphere position
    float loopOffset = sphere.center.z;
    //time it takes to complete a loop
    float loopTime = 8.0;

    //Based on parametric equations of a circle
    sphere.center.x = sin((mod(iTime, loopTime))/loopTime*2.0*PI)*loopRadius;
    sphere.center.z = loopOffset + cos((mod(iTime,loopTime))/loopTime*2.0*PI)*loopRadius;

}

//Get the Specular color (shiny light)
vec4 calculateSpecular(in vec3 rayd, in vec3 normal, in vec3 toLight, in PointLight light) {
    //specular exponent
    float shinnyness = 5.0;
    //get the reflected light vector
    vec3 reflected = 2.0*dot(toLight, normal)*normal - toLight;
    //calculate specular color
    return light.color * max(0.0,pow(dot(rayd,reflected),shinnyness));
}

//Get the Diffuse color (Basic light effect)
vec4 calculateDiffuse(in vec3 normal, in vec3 toLight, in PointLight light) {
    return light.color * max(0.0,dot(normal,toLight));
}

//Get the direction to the light source (no shadows simplifies check)
vec3 checkLight(in vec3 intersect, in PointLight light) {
    return normalize(light.position - intersect);
}

//Get the sphere surface normal from the intersection point
vec3 getSphereIntersectionNormal(in vec3 intersect, in Sphere sphere) {
	return normalize(intersect - sphere.center);
} 

//Get the 3d point that is depth along the ray
vec3 calculateIntersectionPoint(in Ray ray, in float depth) {
	return ray.origin + ray.direction*depth;
}

// From the Wikipedia page on Line-Plane intersection
float intersectPlane(in Ray ray, in Plane plane) {
	float denominator = dot(ray.direction,plane.normal);
    if(abs(denominator) >= 0.01) {//make sure Ray is not parallel to plane (or nearly parallel)
        return dot((plane.point - ray.origin),plane.normal)/denominator;
    }
	return -1.0; // Any negative number to indicate no intersect (or intersection from behind)
}

// From the Wikipedia page on Line-sphere intersection
float intersectSphere(in Ray ray, in Sphere sphere) {
	// Sphere center to ray origin
	vec3 co = ray.origin - sphere.center;

	// The discriminant is negative for a miss, or a postive value
	// used to calcluate the distance from the ray origin to point of intersection
    //bear in mind that there may be more than one solution
	float discriminant = dot(co, ray.direction) * dot(co, ray.direction)
			- (dot(co, co) - sphere.radius * sphere.radius);

	// If answer is not negative, get ray intersection depth
	if (discriminant >= 0.0)
		return -dot(ray.direction, co) - sqrt(discriminant);
	else
		return -1.; // Any negative number to indicate no intersect
}

bool performPlaneCheck(in Ray ray, in Plane plane, in Sphere sphere, out vec4 color){
//code for plane Check
// Check if the ray from the camera through the pixel intersects the Plane
	float intersectionDepth = intersectPlane(ray,plane);
    if (intersectionDepth >= 0.01 && intersectionDepth <= 1000.0) {//Front and Back Culling
    	//Point light is purple and a little to the left and aboveof the camera start
    	PointLight light = PointLight(vec3(-5.0,5.0,0.0), vec4(1.0,0.0,1.0,1.0));
    	//Get the ray intersection Point
    	vec3 intersect = calculateIntersectionPoint(ray,intersectionDepth);
    	//ambient color of the Plane is dark Grey default Light Grey Checkered
    	vec4 ambientPlaneColor = vec4(0.2,0.2,0.2,1.0);
    	//simple way to make a checkerboard pattern
    	float xmod = mod(intersect.x,1.0);
    	float ymod = mod(intersect.z,1.0);
    	if ((xmod <= 0.5 && ymod <= 0.5)||(xmod >= 0.5 && ymod >= 0.5)){
        	//this intersect is in the light part of the pattern
        	ambientPlaneColor = vec4(0.5,0.5,0.5,1.0);
    	}
    	//Get the direction to the lightsource from the intersection point
    	vec3 toLight = checkLight(intersect, light);
    	//contruct Ray for shadow check
    	Ray shadowRay = Ray(intersect, toLight);
    	//Do a shadow check
    	intersectionDepth = intersectSphere(shadowRay,sphere);
    	if (intersectionDepth >= 0.05) { //Sphere is blocking the Light
        	color = ambientPlaneColor;
    	}else{ //Light is visable from this point
        	color = ambientPlaneColor + 0.5*calculateDiffuse(plane.normal, toLight, light);
    	}
        return true;
    }else{
        // else draw background color (black)
		color = vec4(0, 0, 0, 1);
        return false;
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    // output a 4d fragment color vector [R,G,B,A]
    // input is a 2d fragment Co-ordinate vector [X,Y]
    // can interpret input vector as a pixel (function is called 640x360 times by default per frame)

	// Converting all coordinates (x,y) to be in the range [0,1]
	float u = fragCoord.x / iResolution.x; // iResolution.x is width in pixels
	float v = fragCoord.y / iResolution.y; // iResolution.y is height in pixels

	// Now convert from coordinate range [0,1] to [-1,1]
	// This moves pixel (0,0) to the middle of the image
	u = u * 2.0 - 1.0;
	v = v * 2.0 - 1.0;

    //the following line is necessary since the image may not be a square
    //removing this line results in the x dimension being stretched
    u *= (iResolution.x/iResolution.y);

	// The position of this pixel in 3D space 
    //(imagine a plane centered at the origin perpendicular to the camera, what would be the 3d coordinates of this pixel?)
	vec3 pixelPos = vec3(u, v, 0);

    // The camera position in this example is on a fixed loop.
    // Initialize at 0.0
    vec3 cameraPos = vec3(0.0, 0.0, -4.0); 

	// The ray direction is ray from the camera through the pixel
	vec3 rayDir = normalize(pixelPos - cameraPos);

    // The Sphere is at the origin
    Sphere sphere = Sphere(vec3(0.0, 0.0, 7.0), 1.0);

    //Animate the Sphere
    AnimateSphere(sphere);

    // The plane is the xz plane at y = -2.0
    Plane plane = Plane(vec3(0.0,1.0,0.0),vec3(0.0,-2.0,0.0));

    //Create Ray struct from origin through current pixel
    Ray ray = Ray(cameraPos, rayDir);

	// Check if the ray from the camera through the pixel intersects the sphere
	float intersectionDepth = intersectSphere(ray, sphere);

	// If the depth at which the ray intersects the sphere is positive (in front of camera)
	if (intersectionDepth >= 0.05)//Sphere Check
	{//draw the sphere color (basic ambient + diffuse)
        //ambient color of the sphere is greenish
        vec4 ambientSphereColor = vec4(0.0,0.2,0.0,1.0);

        //Point light is purple and a little to the left of the camera start
        PointLight light = PointLight(vec3(-3.0,0.0,0.0), vec4(1.0,0.0,1.0,1.0));

        //Get the ray intersection Point
        vec3 intersect = calculateIntersectionPoint(ray, intersectionDepth);

        //Get the surface normal at that point
		vec3 normal = getSphereIntersectionNormal(intersect,sphere);

        //Get the direction to the lightsource from the intersection point
        vec3 toLight = checkLight(intersect, light);

        //we are going to skip the checks for shadows for the sphere
        //but in other scenes we will not have this luxury

        //calculate reflected Ray (normalize here is because im paranoid)
        vec3 reflected = normalize(ray.direction - 2.0*dot(ray.direction, normal)*normal);
        Ray rayreflect = Ray(intersect,reflected);

        //initialize reflected color
        vec4 reflectColor = vec4(1.0,1.0,1.0,1.0);
        //Plane Check
        bool intersected = performPlaneCheck(rayreflect,plane,sphere,reflectColor);
        if (intersected){
        	//Combine it all to get the correct color (with reflected color)
        	fragColor = ambientSphereColor + calculateDiffuse(normal,toLight,light) + calculateSpecular(-ray.direction, normal, toLight, light) + 0.5*reflectColor;
        }else{
        	//Combine it all to get the correct color
        	fragColor = ambientSphereColor + calculateDiffuse(normal,toLight,light) + calculateSpecular(-ray.direction, normal, toLight, light);
        }
    } else {//Plane Check
        bool intersected = performPlaneCheck(ray,plane,sphere, fragColor);
    }
}

