#define PI      3.14159265358979
#define PI_HALF (PI / 2.0)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy * 0.5) / min(iResolution.x, iResolution.y);

    float scale = 5.0 + sin(iTime) * 0.25;
    uv.y -= fract(iTime * 0.2) / scale;

    vec2  uvGrid = floor(uv * scale);
    float uvYOffset = mod(uvGrid.x, 2.0) <= 0.0 ? 0.0 : 0.5;
    vec2  uvLocal = fract(uv * scale + vec2(0.0, uvYOffset)) - vec2(0.0, 0.5);
    float dist = 1e+5;

    // below grids (both sides only)
    for(float x=-1.0; x<2.0; x+=2.0)
        dist = min(dist, distance(vec2(x, -1), uvLocal));

    // self grid
    const float WAVE_DISTANCE = 1.0;
	if(WAVE_DISTANCE < dist)
    	dist = min(dist, distance(vec2(0, -0.5), uvLocal));

    // center grids (both sides only)
	if(WAVE_DISTANCE < dist)
  		for(float x=-1.0; x<2.0; x+=2.0)
        	dist = min(dist, distance(vec2(x, 0), uvLocal));

    const float STRIPES = 8.0;
    float stripeOffset = (-cos(fract(iTime * 0.5) * PI) * 0.5 + 0.5) * (4.0 * PI);
    fragColor.rgb =
        (0.0 < sin(dist * STRIPES * PI - stripeOffset)) ?
    	vec3(1.0, 0.8, 0.4) :
    	vec3(1.0, 0.7, 0.2);

    float uvYGradient = fragCoord.y / iResolution.y;
    fragColor.rgb *= uvYGradient * 0.4 + 0.6;
}

