float distToSphere(vec3 pos){
    float r = .25;
    vec3 c = vec3(0., 0.0, 0.0);
    float sub = 1.;

    float s = 4.;

    vec3 a = (pos-c);
    return pow(pow(a.x, s)+pow(a.y, s)+pow(a.z,s),1./s)-r;

    //d.dist = length(mod(pos,sub)-c)-r;
    //d.normal = normalize(mod(pos, sub)-c);
    //return d;
}

vec3 CalcNormal(vec3 p)
{
    float epsilon = 0.001; // arbitrary — should be smaller than any surface detail in your distance function, but not so small as to get lost in float precision
    float centerDistance = distToSphere(p);
    float xDistance = distToSphere(p + vec3(epsilon, 0, 0));
    float yDistance = distToSphere(p + vec3(0, epsilon, 0));
    float zDistance = distToSphere(p + vec3(0, 0, epsilon));
    vec3 normal = (vec3(xDistance, yDistance, zDistance) - centerDistance) / epsilon;
    return normal;
}

mat2 rot(float a)
{
    float c = cos(a);
    float s = sin(a);
    return mat2(c, -s, s, c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = ((fragCoord-0.5*iResolution.xy)/iResolution.y)*.5;

    const int numLights = 2;
    vec3 lightDir[numLights];
    lightDir[0] = normalize(vec3(1., 1., -1.));
    lightDir[1] = normalize(vec3(1., 1., 1.));

    float speed = 1.f;
    vec3 camPosition = vec3(0., .5, -1.);
    camPosition.xz *= rot(iTime*speed);
    vec3 rayPos = camPosition;
    vec3 direction = vec3(uv, .5);
    direction.y -= 0.2f;
    direction.xz *= rot(iTime*speed);

    float d;
    bool hit = false;
    while(length(rayPos-camPosition) < 200.){
        d = distToSphere(rayPos);
        if(d < 0.001){
            hit = true;
            break;
    	}
        else
            rayPos += direction * d;
    }

    vec3 col = vec3(0.);

    vec3 lightColor = vec3(.8, .8, .6);
    vec3 myColor = vec3(.55, .3, .64);
    float shininess = 73.;


    for(int i = 0; i < numLights; i++){
        if(hit){ 
            vec3 normal = CalcNormal(rayPos);
            vec3 emissive = vec3(0.);
            vec3 ambient = vec3(0.05, 0.05, 0.1);
            vec3 diffuse = myColor*lightColor * max(dot(normal, lightDir[i]), 0.);
            vec3 H = normalize((-1.0f * direction) + lightDir[i]);
            float facing = max(dot(normal, lightDir[i]), 0.);
            vec3 specular = myColor * lightColor * facing * pow((max(dot(normal, H), 0.)), shininess);
            col += emissive + ambient + diffuse + specular;
        }
    }
    fragColor = vec4(col,1.0);
}

float rand21(vec2 uv){
    vec2 t = fract(vec2(uv.x*236.73,uv.y*678.47));
    float u = dot(uv*(t+143.66),t);
    return fract(t.x*u*345.87 + 365.22+t.y*u*475.57);
}
float rand21p(vec2 uv){
    vec2 p = floor(uv);
    vec2 fr = fract(uv);

    float grad = (
        rand21(p+vec2(0.,0.)) * (1.-fr.x) * (1.-fr.y) + 
        rand21(p+vec2(1.,0.)) * (fr.x) * (1.-fr.y) +
        rand21(p+vec2(0.,1.)) * (1.-fr.x) * (fr.y) +
        rand21(p+vec2(1.,1.)) * (fr.x) * (fr.y)
    );
    return grad;
}
float perlin(vec2 uv, float layers){
    float ret = 0.;
    for(float i=0.;i<layers;i++){
        ret += rand21p(uv*pow(2.,i)) / layers;
    }
    return ret;
}
float splatter(float time, vec2 uv){
    float n0 = perlin(uv+rand21(uv*dot(uv,vec2(time))),9.);
    return smoothstep(0.5,0.55,n0);
}
float ease(float time, vec2 uv){
    float cur = splatter(floor(time),uv);
    float next = splatter(floor(time)+1.,uv);
    float f = fract(time);
    return (1.-f)*cur + f*next;
}
