    Material(vec3(0.0, 0.1, 0.1), vec3(0.2), 1.0, 0.05)
);

vec2 rotate(vec2 v, float a) { return cos(a)*v + sin(a)*vec2(v.y,-v.x); }

//noise function from https://www.shadertoy.com/view/MsXGWr
vec3 texturize( sampler2D sa, vec3 p, vec3 n )
{
	vec3 x = texture( sa, p.yz ).xyz;
	vec3 y = texture( sa, p.zx ).xyz;
	vec3 z = texture( sa, p.xy ).xyz;
	return x*abs(n.x) + y*abs(n.y) + z*abs(n.z);
}

vec2 opUnion(vec2 a, vec2 b) { return a.x < b.x ? a : b; }

//distance function from http://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
float sdRoundBox( vec3 p, vec3 b, float r )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0) - r;
}

vec2 map(vec3 p)
{
    float objID = 0.0;
    vec2 res = vec2(sdRoundBox(p-vec3(0.0, 0.0, 0.0), vec3(0.7, 0.7, 0.7), 0.2), objID++);
    vec3 p2 = p - vec3(0.0, 1.3, 0.0); p2.xz = rotate(p2.xz, PI/6.0);
    res = opUnion(res, vec2(sdRoundBox(p2, vec3(0.3, 0.3, 0.3), 0.1), objID++));
    p2 = p - vec3(0.0, 2.2, 0.0); p2.xz = rotate(p2.xz, PI/9.0);
    res = opUnion(res, vec2(sdRoundBox(p2, vec3(0.5, 0.3, 0.5), 0.2), objID++));
    p2 = p - vec3(3.0, 0.3, 0.0); p2.xz = rotate(p2.xz, PI/9.0);
    res = opUnion(res, vec2(sdRoundBox(p2, vec3(0.7, 1.0, 0.7), 0.2), objID++));
    p2 = p - vec3(-3.0, -0.2, 0.5); p2.xz = rotate(p2.xz, PI/4.0);
    res = opUnion(res, vec2(sdRoundBox(p2, vec3(0.5, 0.5, 0.5), 0.2), objID++));
    p2 = p - vec3(-2.8, 1.3, 0.5); p2.xz = rotate(p2.xz, 0.0);
    res = opUnion(res, vec2(sdRoundBox(p2, vec3(0.6, 0.6, 0.6), 0.2), objID++));
    p2 = p - vec3(1.0, -0.4, 2.5); p2.xz = rotate(p2.xz, PI/6.0);
    res = opUnion(res, vec2(sdRoundBox(p2, vec3(0.4, 0.4, 0.4), 0.1), objID++));
    res = opUnion(res, vec2(p.y + 0.9, objID++));
    return res;
}

vec3 calculateTransmittance(vec3 ro, vec3 rd, float tmin, float tmax, vec3 atten)
{
    float thickness = 0.0;
    for (float t = tmin; t < tmax;)
    {
        float h = map(ro + t * rd).x;
        float dt = (abs(h) < 1e-5) ? 0.01 : min(tmax-t, abs(h));
        if (h < 0.0) thickness += dt; 
        t += dt;
    }

    return exp(-atten * thickness);
}

vec2 rayMarch(vec3 ro, vec3 rd, float tmin, float tmax)
{
	for (float t = tmin; t < tmax;)
    {
        vec3 pos = ro + t * rd;
        vec2 res = map(pos);

        float dist = res.x;
        if (dist < 0.001) return vec2(t, res.y);

        t += dist;
    }
    return vec2(1e6, -1.0);
}

vec3 calculateNormal(vec3 p)
{
    vec3 dt = vec3(0.001, 0.0, 0.0);
    return normalize( vec3 ( map(p+dt.xyy).x - map(p-dt.xyy).x,
    						 map(p+dt.yxy).x - map(p-dt.yxy).x,
                             map(p+dt.yyx).x - map(p-dt.yyx).x ) );
}

float calculateAO(vec3 pos, vec3 nor) //ambient occlusion
{
    float ao = 0.0, sca = 1.0;

    for (int i = 0; i < 5; ++i)
    {
    	float h = 0.01 + 0.11*float(i)/4.0;
        float d = map(pos+h*nor).x;
        ao += (h-d)*sca;
        sca *= 0.95;
    }

    return clamp(1.0-2.0*ao, 0.0, 1.0);
}

vec3 render(vec3 ro, vec3 rd)
{
	vec3 color = vec3(0.0);

    float an = iMouse.z>0.0 ? 10.0*(iMouse.y/iResolution.y-0.5) : (1.0-0.2*iTime);
    vec3 lightPos = vec3(3.0*cos(an), 1.3, 3.0*sin(an));
    vec3 lightColor = vec3(10.0, 8.0+sin(iTime)*6.0, 7.0+cos(iTime)*5.0);

    vec2 res = rayMarch(ro, rd, 0.01, 15.0);
    float t = res.x;
    float objID = res.y;

    //draw entities
    if (objID > -0.5)
    {
        Material mat = materials[int(objID)];
        vec3 pos = ro + t * rd;
        vec3 nor = calculateNormal(pos);
        vec3 bnor = -1.0 + 2.0 * texturize(iChannel0, 0.5*pos, nor);
		nor = normalize(nor + mat.bumpiness*bnor);

        vec3 lightDir = normalize(lightPos - pos);
        float lightDist = length(lightPos - pos);		
        float atten = 1.0 / (1.0 + 0.5 * lightDist * lightDist);
        vec3 light = atten * lightColor;
        light *= calculateTransmittance(pos+0.01*nor, lightDir, 0.01, min(lightDist, 20.0), vec3(1.0, 0.6, 0.5));

        float ao = calculateAO(pos, nor);
        color +=  light * mat.diffuseAlbedo;
        color += light * mat.specularAlbedo * pow(max(0.0, dot(reflect(lightDir,nor),rd)), mat.specularPower);
        color *= ao;
    }

    //draw lights
    {
        vec3 lv = lightPos - ro;
        float ll = length(lv);
        if (ll < t)
        {
            float angle = acos(clamp(dot(rd, lv/ll), 0.0, 1.0));
            color += 1e2*lightColor*pow((1.0-smoothstep(0.0, 0.8, angle*ll)), 32.0);    
        }
    }

    return color;
}

mat3 lookAt(vec3 eye, vec3 target, vec3 up)
{
    vec3 w = normalize(target - eye);
    vec3 u = normalize(cross(w, up));
    vec3 v = cross(u, w);
    return mat3(u, v, -w);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    float an = iMouse.z>0.0 ? 10.0*(iMouse.x/iResolution.x-0.5) : 0.0;

	vec3 ro = 5.0*vec3(1.2*sin(an), 0.3, cos(an)+0.2);
    mat3 viewMat = lookAt(ro, vec3(0.0, 0.0, 0.0), vec3(0.0, 1.0, 0.0));

    vec3 color = vec3(0.0);
    for (int i = 0; i < AA; ++i) //super sampling
    {
        for (int k = 0; k < AA; ++k)
        {
            vec2 offset = vec2(float(i)+0.5,float(k)+0.5) / float(AA) - 0.5;
            vec2 uv = (2.0*(fragCoord+offset)-iResolution.xy)/iResolution.y;    
    	    vec3 rd = viewMat * normalize(vec3(uv, -2.0));

            color += render(ro, rd);
        }
    }

	color /= float(AA*AA);
	color = pow(color, vec3(0.4545));

    fragColor = vec4(color, 1.0);
}

float circ(in vec2 st, in vec2 pos, float r)
{
    vec2 d = st - pos;
 	return (sqrt(d.x * d.x + d.y * d.y) < r) ? 0.0 : 1.0;   
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float aspect = iResolution.x / iResolution.y;
    vec2 st = vec2(uv.x * aspect, uv.y);

    // Time varying pixel color
    vec3 col = vec3(0.0);

    col = vec3(uv.y * 0.25 + 0.5);

    float rotRad = 0.4;
    float degs = iTime * 0.1;
    vec2 pOrigin = vec2(0.5 * aspect, 0.5);
    col *= circ(st, pOrigin, 0.025);

    //Orbit
    //float numRows = mod(iTime * 8.0, 64.0);
    float numRows = sin(iTime * 0.5) * 32.0 + 32.0;//, 64.0);
    for(float j = 0.0; j < numRows; j += 1.0)
    {
        for(float i = 0.0; i < 44.0; i += 1.0)
        {
            //offset j
            float oj = j - (numRows * 0.5);
            degs = iTime * 0.1 + i + j * 0.01;

            //normalized j
            float nj = (oj / numRows);
            vec2 circPos = vec2(cos(degs) * rotRad * nj+ oj * 0.01, sin(degs) * rotRad * nj + oj * 0.01);
            col *= circ(st, pOrigin + circPos, 0.0025 + j * 0.0001);
        }
    }

    degs = iTime * 0.1;
    for(float i = 1.0; i < 36.0; i += 1.0)
    {
		vec2 circPos = vec2(cos(degs * 2.0 * i) * rotRad, sin(degs * i * 1.1) * rotRad);
    	col *= circ(st, pOrigin + circPos, 0.01 * abs((pOrigin - st).y) * 4.1);
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define PI 3.14159265359
#define PHI 1.618033988749895
#define v1 vec3(1.0, 0.0, 0.0)
#define v3 vec3(0.0, 0.0, 1.0)
vec3 v2, w1, w2, w3;
vec3 vtxP, edgN, faceN;
float rad = 1.0;
void init(float m) {
    v2 = vec3(-0.5, sqrt(0.75 - pow(cos(PI / m), 2.0)), - cos(PI / m));
    float triProd = dot(cross(v1, v2), v3);
    w1 = normalize(cross(v3, v2) / triProd);
    w2 = normalize(cross(v1, v3) / triProd);
    w3 = normalize(cross(v2, v1) / triProd);
}
float edgSDF(vec3 p, float t) {// t:thickness
    return max(abs(dot(p - vtxP, faceN)), abs(dot(p - vtxP, edgN))) - t;
}
float vtxSDF(vec3 p, float t) {
    return length(p - vtxP) - t;
}
float faceSDF(vec3 p, float t) {
    return dot(p - vtxP, faceN) - t;
}
#define fold(w)p = p - 2.0 * max(dot(w, p), 0.0) * w;
float tetraSDF(vec3 p) {
    init(3.0);
    float scale = 2.0;
    float scalePow = 1.0;
    vtxP = rad * w1;
    for(int i = 0; i < 4; i ++ ) {
        fold(v1)fold(v3)fold(v2)fold(v3)fold(v1)fold(v2)
        p *= scale;
        scalePow *= scale;
        p -= (scale - 1.0) * vtxP;
    }  
    edgN = w2;
    faceN = w3;
    return edgSDF(p, 0.1) / scalePow;
}
float cubeSDF(vec3 p) {
    init(4.0);
    vtxP = rad * w3;
    float scale = 2.5;
    float scalePow = 1.0;
    for(int i = 0; i < 4; i ++ ) {
        fold(v1)fold(v2)fold(v3)fold(v1)fold(v2)fold(v3)fold(v1)fold(v2)fold(v3)
        p *= scale;
        scalePow *= scale;
        p -= (scale - 1.0) * vtxP;
    }
    edgN = w2;
    faceN = w1;
    return faceSDF(p, 0.5) / scalePow;
}
float octaSDF(vec3 p) {
    init(4.0);
    vtxP = rad * w1;
    float scale = 2.0;
    float scalePow = 1.0;
    for(int i = 0; i < 3; i ++ ) {
        fold(v1)fold(v2)fold(v3)fold(v1)fold(v2)fold(v3)fold(v1)fold(v2)fold(v3)
        p *= scale;
        scalePow *= scale;
        p -= (scale - 1.0) * vtxP;
    }
    edgN = w2;
    faceN = w3;
    return min(edgSDF(p, 0.02), vtxSDF(p, 0.3)) / scalePow;
}
float dodecaSDF(vec3 p) {
    init(5.0); 
    vtxP = rad * w1;
    float scale = PHI + 1.0;
    float scalePow = 1.0;
    for(int i = 0; i < 3; i ++ ) {
        fold(v1)fold(v2)fold(v3)fold(v1)fold(v2)fold(v3)fold(v1)fold(v2)fold(v3)fold(v1)fold(v2)fold(v3)fold(v1)fold(v2)fold(v3)
        p *= scale;
        scalePow *= scale;
        p -= (scale - 1.0) * vtxP;
    }
    edgN = w2;
    faceN = w3;
    return faceSDF(p, 0.0) / scalePow;
}
float icosaSDF(vec3 p) {
    init(5.0);
    vtxP = rad * w3;
    float scale = PHI + 1.0;
    float scalePow = 1.0;
    for(int i = 0; i < 3; i ++ ) {
        fold(v1)fold(v2)fold(v3)fold(v1)fold(v2)fold(v3)fold(v1)fold(v2)fold(v3)fold(v1)fold(v2)fold(v3)fold(v1)fold(v2)fold(v3)
        p *= scale;
        scalePow *= scale;
        p -= (scale - 1.0) * vtxP;
    }
    edgN = w2;
    faceN = w1;
    return edgSDF(p, 0.08) / scalePow;
}
mat3 euler(float h, float p, float r) {
    return mat3(
        cos(r) * cos(h) - sin(r) * sin(p) * sin(h), - sin(r) * cos(p), cos(r) * sin(h) + sin(r) * sin(p) * cos(h),
        sin(r) * cos(h) + cos(r) * sin(p) * sin(h), cos(r) * cos(p), sin(r) * sin(h) - cos(r) * sin(p) * cos(h),
        - cos(p) * sin(h), sin(p), cos(p) * cos(h)
    );
}
#define seq(v, s)d = mix(d, v, smoothstep(s - 0.3, s, t));
float sceneSDF(vec3 p) {
    float seqStep = 5.0;
    float t = abs(mod(iTime * 0.5, seqStep * 2.0) - seqStep);
    float d = tetraSDF(p);
    seq(cubeSDF(p), 1.0)
    seq(octaSDF(p), 2.0)
    seq(dodecaSDF(p), 3.0)
    seq(icosaSDF(p), 4.0)
    return d;
}
vec3 gradSDF(vec3 p) {
    float d = 0.0001;
    return normalize(vec3(
            sceneSDF(p + vec3(d, 0.0, 0.0)) - sceneSDF(p + vec3(-d, 0.0, 0.0)),
            sceneSDF(p + vec3(0.0, d, 0.0)) - sceneSDF(p + vec3(0.0, - d, 0.0)),
            sceneSDF(p + vec3(0.0, 0.0, d)) - sceneSDF(p + vec3(0.0, 0.0, - d))
        ));
    }
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // fragment position
    vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);

    //camera transform
    float t = 0.3 * iTime;
    mat3 g = euler(t, t, t);

    // camera
    vec3 cPos = g * vec3(0.0, 0.0, 2.0);
    vec3 cDir = g * vec3(0.0, - 0.0, - 1.0);
    vec3 cUp = g * vec3(0.0, 1.0, 0.0);
    float targetDepth = 1.0;
    vec3 cSide = cross(cDir, cUp);

    // light
    vec3 lightPos = g * vec3(0.0, 1.0, 1.0);

    // ray
    vec3 ray = normalize(cSide * p.x + cUp * p.y + cDir * targetDepth);
    vec3 rPos = cPos ;

    // marching loop
    for(int i = 0; i < 60; i ++ ) {
        rPos += sceneSDF(rPos) * ray;
    }

    // collision detection
    if (sceneSDF(rPos) < 0.001) {
        vec3 normal = gradSDF(rPos);
        float diff = clamp(dot(normalize(lightPos - rPos), normal), 0.01, 1.0);
        vec3 amb = vec3(0.0, 1.0, 0.0);
        fragColor = vec4(diff * amb, 1.0);
    } else {
        fragColor = vec4(1.0);
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

        // the sound texture is 512x2
    //int tx = int(uv.x*512.0);
    int tx = 1;

	// first row is frequency data (48Khz/4 in 512 texels, meaning 23 Hz per texel)
	float fft  = texelFetch( iChannel0, ivec2(tx,0), 0 ).x; 

    // second row is the sound wave, one texel is one mono sample
    float wave = texelFetch( iChannel0, ivec2(tx,1), 0 ).x;


    col += sin(uv.x * cos(iTime/30.*fft/20.) * 60.) + sin(uv.y * cos(iTime / 20.) * 18.);

    col += cos(uv.x* sin(iTime/60.) * 60.) + cos(uv.y * sin(iTime / 10.*wave) * 18. * fft);

    //col *= sin(iTime / 30.);

    col += mix(col,(cos(iMouse.yxy+vec3(0,2,4))),uv.y);
    // Output to screen
    fragColor = vec4(col,1.0);
}

