#define M_PI 3.14159265358979323846264338327950288

float sdf(vec2 xy, float t, vec2 center)
{
    float x = xy.x - center.x;
    float y = xy.y - center.y;
    float theta = atan(y, x);
    float angleDist = min(abs(theta-mod(t, 2.*M_PI)), abs(mod(t, 2.*M_PI)-theta));

    float p = (sin(t*2.) + 1.0) / 2.0;
    float power = p * .5 + (1.-p) * 8.;
    float r = pow((abs(pow(abs(x), power)) + abs(pow(abs(y), power))), 1./power);

    float param = (sin(10.*angleDist) + 1.)/2.;
    param = smoothstep(0., 1., param);

    return r * (1.0 * param + .75 * (1.-param));
}

vec3 thingy(vec2 xy, float t, vec2 center, float size, vec3 color)
{
    float sizeIn = size * 24./25.;
    vec3 colorIn = color / 3.;

    float dist = sdf(xy, t, center);

    vec3 col;

    if (dist < sizeIn)
        col = color;
    else if (dist < size)
        col = colorIn;
    else
        col = vec3(0.);
	return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y;

    // Time varying pixel color
    //float dist = sdf(uv, iTime);

    vec3 col = vec3(0.);
    col += thingy(uv, iTime, vec2(1.5, .5), .25, vec3(153.,216.,201.)/255.);
    col += thingy(uv, iTime+.5, vec2(0., 1.), .45, vec3(254.,178.,76.)/255.);
    col += thingy(uv, (iTime+.25)/5., vec2(0.7, 0.35), .15, vec3(103.,138.,76.)/255.);
    col += thingy(uv, 2.*iTime, vec2(0.35, 0.32), .2, vec3(200.,100.,6.)/255.);
    col += thingy(uv, 4.*(iTime+.75), vec2(1., .8), .35, vec3(176.,116.,197.)/255.);
    col += thingy(uv, 1.5*(iTime+.5), vec2(1.6, 0.), .45, vec3(250.,159.,181.)/255.);


    // Output to screen
    fragColor = vec4(col,1.0);
}

