float leg(vec3 pos, vec3 n)
{
    float d = sdConeBend(s.pos, pos, pos+n*0.2, 2.1, 2.5);

    d = opDiff(d, 0.2, sdPlane(s.pos, pos+n*0.5, -n));
    d = opDiff(d, 0.1, sdSphere(s.pos, pos+n*0.7, 0.4));

    return d;
}

