float ear(vec3 pos, vec3 n)
{
    float d = sdConeBend(s.pos, pos, pos+n*0.23, 0.7, 1.1);

    d = opDiff(d, 0.25, sdSphere(s.pos, pos+n*(0.5 + 0.15*brth/2.0), 0.1));

    return d;
}

