float dfScene(vec3 rp, vec3 rd) {

    float msd = 999999.0;

    float scale = GR;

    float numLayers = 4.;
    float numBranches = 4.;
    vec3 base = vec3(0., 0., 0.);
    vec3 height = vec3(.75, .75, .75);

    vec3 po = rp;

    for(float branch = 0.0; branch < numBranches; branch += 1.)
    {
        rp = po;
        float factor = 1.;
        for(float layer = 0.0; layer < numLayers; layer += 1.)
        {
            vec3 p = rp;
            float a = atan(p.z,p.x);
            a = a/PI*.5+.5;
            a = (saw(a*numBranches)*2.-1.)*PI;
            float rots = saw(time)*4.;


            rp = rotatePoint(p, vec3(0., 1., 0.), -a);
            for(float branch = 0.0; branch < numBranches; branch += 1.)
            {

                msd = min(msd, sdSphere(p, base, 0.06*GR)*factor);
                msd = min(msd, sdCapsule(p, base, base+height, 0.02*GR)*factor);
                float a = atan(rp.z, rp.x);
                p = rotatePoint(p, vec3(0., 1., 0.), PI*2./numBranches);
            }
            rp = (p-height)/height;
            factor *= min(height.x, min(height.y, height.z));        
            rp = rotatePoint(rp, vec3(0., 1., 0.), PI*2./numBranches+time*PI);
        }
        po = rotatePoint(po, vec3(0., 1., 0.), PI*2./numBranches);
    }

    return msd;
}

vec3 surfaceNormal(vec3 p, vec3 rd) { 
    vec2 e = vec2(5.0 / iResolution.y, 0);
	float d1 = dfScene(p + e.xyy, rd), d2 = dfScene(p - e.xyy, rd);
	float d3 = dfScene(p + e.yxy, rd), d4 = dfScene(p - e.yxy, rd);
	float d5 = dfScene(p + e.yyx, rd), d6 = dfScene(p - e.yyx, rd);
	float d = dfScene(p, rd) * 2.0;	
    return normalize(vec3(d1 - d2, d3 - d4, d5 - d6));
}

//IQ
float calcAO(vec3 pos, vec3 nor, vec3 rd) {   
    float occ = 0.0;
    float sca = 1.0;
    for(int i = 0; i < 5; i++) {
        float hr = 0.01 + 0.05*float(i);
        vec3 aopos = pos + nor*hr;
        occ += smoothstep(0.0, 0.7, hr - dfScene(aopos, rd)) * sca;
        sca *= 0.97;
    }
    return clamp(1.0 - 3.0 * occ , 0.0, 1.0);
}

//main march
vec3 marchScene(vec3 ro, vec3 rd) {

    vec3 pc = vec3(0.0); //returned pixel colour
    float d = 0.0; //distance marched
    vec3 rp = vec3(0.0); //ray position
    vec3 lp = normalize(vec3(5.0, 8.0, -3.0)); //light position

    for (int i = 0; i < 64; i++) {
        rp = ro + rd * d;
        eye = rp;
        float ns = dfScene(rp, rd);
        d += ns;
        if (ns < 1.0/MAX_DIM || d > FAR) break;
    }

    if (d < FAR) {

        vec3 sc = vec3(1.0, 0.0, 0.0); //surface colour
        vec3 n = surfaceNormal(rp, rd);
        float ao = calcAO(rp, n, rd);

        float diff = max(dot(n, lp), 0.0); //diffuse
	    pc = sc * 0.5 + diff * sc * ao;
        float spe = pow(max(dot(reflect(rd, n), lp), 0.), 16.); //specular.
        pc = pc + spe * vec3(1.0);
    }

    return pc;
}


