#define S(a, b, t) smoothstep(a, b, t)
#define sat(x) clamp(x, 0., 1.)

float remap01(float a, float b, float t){
	return sat((t-a)/(b-a));
}

float remap(float a, float b, float c, float d, float t){
	return sat((t-a)/(b-a)) * (d-c) + c;
}

vec2 within(vec2 uv, vec4 rect){
    return (uv - rect.xy)/(rect.zw - rect.xy);
}

vec4 Eye(vec2 uv){
    uv -= 0.5;

    float d = length(uv);

    vec4 irisCol = vec4(.3, .5, 1., 1.);

    vec4 col = mix(vec4(1.), irisCol, S(.1, .7, d)*0.5);

    col.rgb *= 1. - S(.45, .5, d)*0.5*sat(-uv.y-uv.x);

    col.rgb = mix(col.rgb, vec3(0.), S(.3, .28, d));

    irisCol.rgb *= 1. + S(.3, .05, d);
    col.rgb = mix(col.rgb, irisCol.rgb, S(.28, .25, d));

    col.rgb = mix(col.rgb, vec3(0.), S(.16, .14, d));

    float highlight = S(.1, .09, length(uv - vec2(-.15, .15)));
    highlight += S(.07, .05, length(uv + vec2(-.08, .08)));

    col.rgb = mix(col.rgb, vec3(1.), highlight);

    col.a = S(.5, .48, d);


    return col;
}

vec4 Mouth(vec2 uv){
	uv -= .5;
    vec4 col = vec4(.5, .18, .05, 1.);

    uv.y *= 1.5+ abs(2.*cos(0.5*3.14*iTime));
    uv.y -= uv.x * uv.x*2.; 
    float d = length(uv);
    col.a = S(.5, .48, d);

    float td = length(uv-vec2(0., .6));

    vec3 toothCol = vec3(1.) * S(.6, .35, d);
    col.rgb = mix(col.rgb, toothCol, S(.4, .37, td));

    td = length(uv+vec2(0., .5));
    col.rgb =mix(col.rgb, vec3(1., .5, .5), S(.5, .2, td));
    return col;
}

vec4 Head(vec2 uv){
	vec4 col = vec4(.9, .65, .1, 1.);
    float d = length(uv);
    col.a = S(0.5, 0.49, d);
    float edgeShade = remap01(.35, .5, d);
    edgeShade *= edgeShade;

    col.rgb *= 1. - edgeShade*.5;
    col.rgb = mix(col.rgb, vec3(.6, .3, .1), S(0.47, 0.48, d));

    float highlight = S(0.41, 0.405, d);
    highlight *=remap(0.41, -.1, 0.75, 0., uv.y);
    col.rgb = mix(col.rgb, vec3(1.0), highlight);



    d = length(uv - vec2(.25, -.2));
    float cheek = S(0.2, 0.01, d)*0.4;
    cheek *= S(0.17, 0.16, d);
    col.rgb = mix(col.rgb, vec3(1., .1, .1), cheek);

    return col;
}

vec4 Smiley(vec2 uv){
	vec4 col = vec4(0.0);
    uv.x = abs(uv.x);
    vec4 head = Head(uv);
    vec4 eye = Eye(within(uv, vec4(0.03, -.1, .37, .25)));
    vec4 mouth = Mouth(within(uv, vec4(-.3, -.4, .3, -.1)));

    col = mix(col, head, head.a);
    col = mix(col, eye, eye.a);
    col = mix(col, mouth, mouth.a);
    return col;
}


