//	Classic Perlin 3D Noise 
//	by Stefan Gustavson
//
vec4 permute(vec4 x){return mod(((x*34.0)+1.0)*x, 289.0);}
vec4 taylorInvSqrt(vec4 r){return 1.79284291400159 - 0.85373472095314 * r;}
vec3 fade(vec3 t) {return t*t*t*(t*(t*6.0-15.0)+10.0);}

float cnoise(vec3 P){
  vec3 Pi0 = floor(P); // Integer part for indexing
  vec3 Pi1 = Pi0 + vec3(1.0); // Integer part + 1
  Pi0 = mod(Pi0, 289.0);
  Pi1 = mod(Pi1, 289.0);
  vec3 Pf0 = fract(P); // Fractional part for interpolation
  vec3 Pf1 = Pf0 - vec3(1.0); // Fractional part - 1.0
  vec4 ix = vec4(Pi0.x, Pi1.x, Pi0.x, Pi1.x);
  vec4 iy = vec4(Pi0.yy, Pi1.yy);
  vec4 iz0 = Pi0.zzzz;
  vec4 iz1 = Pi1.zzzz;

  vec4 ixy = permute(permute(ix) + iy);
  vec4 ixy0 = permute(ixy + iz0);
  vec4 ixy1 = permute(ixy + iz1);

  vec4 gx0 = ixy0 / 7.0;
  vec4 gy0 = fract(floor(gx0) / 7.0) - 0.5;
  gx0 = fract(gx0);
  vec4 gz0 = vec4(0.5) - abs(gx0) - abs(gy0);
  vec4 sz0 = step(gz0, vec4(0.0));
  gx0 -= sz0 * (step(0.0, gx0) - 0.5);
  gy0 -= sz0 * (step(0.0, gy0) - 0.5);

  vec4 gx1 = ixy1 / 7.0;
  vec4 gy1 = fract(floor(gx1) / 7.0) - 0.5;
  gx1 = fract(gx1);
  vec4 gz1 = vec4(0.5) - abs(gx1) - abs(gy1);
  vec4 sz1 = step(gz1, vec4(0.0));
  gx1 -= sz1 * (step(0.0, gx1) - 0.5);
  gy1 -= sz1 * (step(0.0, gy1) - 0.5);

  vec3 g000 = vec3(gx0.x,gy0.x,gz0.x);
  vec3 g100 = vec3(gx0.y,gy0.y,gz0.y);
  vec3 g010 = vec3(gx0.z,gy0.z,gz0.z);
  vec3 g110 = vec3(gx0.w,gy0.w,gz0.w);
  vec3 g001 = vec3(gx1.x,gy1.x,gz1.x);
  vec3 g101 = vec3(gx1.y,gy1.y,gz1.y);
  vec3 g011 = vec3(gx1.z,gy1.z,gz1.z);
  vec3 g111 = vec3(gx1.w,gy1.w,gz1.w);

  vec4 norm0 = taylorInvSqrt(vec4(dot(g000, g000), dot(g010, g010), dot(g100, g100), dot(g110, g110)));
  g000 *= norm0.x;
  g010 *= norm0.y;
  g100 *= norm0.z;
  g110 *= norm0.w;
  vec4 norm1 = taylorInvSqrt(vec4(dot(g001, g001), dot(g011, g011), dot(g101, g101), dot(g111, g111)));
  g001 *= norm1.x;
  g011 *= norm1.y;
  g101 *= norm1.z;
  g111 *= norm1.w;

  float n000 = dot(g000, Pf0);
  float n100 = dot(g100, vec3(Pf1.x, Pf0.yz));
  float n010 = dot(g010, vec3(Pf0.x, Pf1.y, Pf0.z));
  float n110 = dot(g110, vec3(Pf1.xy, Pf0.z));
  float n001 = dot(g001, vec3(Pf0.xy, Pf1.z));
  float n101 = dot(g101, vec3(Pf1.x, Pf0.y, Pf1.z));
  float n011 = dot(g011, vec3(Pf0.x, Pf1.yz));
  float n111 = dot(g111, Pf1);

  vec3 fade_xyz = fade(Pf0);
  vec4 n_z = mix(vec4(n000, n100, n010, n110), vec4(n001, n101, n011, n111), fade_xyz.z);
  vec2 n_yz = mix(n_z.xy, n_z.zw, fade_xyz.y);
  float n_xyz = mix(n_yz.x, n_yz.y, fade_xyz.x); 
  return 2.2 * n_xyz;
}



/**
 * Signed distance function for a sphere centered at the origin with radius 1.0;
 */
float sphereSDF(vec3 samplePoint) {
    vec3 sp2 = samplePoint;
    sp2.x = fract(sp2.x*0.5+1.0)/0.5-1.0;
    sp2.z = fract(sp2.z*0.5+1.0)/0.5-1.0;
    return length(sp2) - 0.4;
}

float lightSdf(vec3 samplePoint ) {
    float l = length(samplePoint);
    if ( l < 0.3 ) {
    	return l+0.3;
    } else {
    	return l-0.3;
    }
}


float planeSDF(vec3 sp) {
	return abs(sp.y+cnoise(sp*5.0+iTime*vec3(0,1.0,0.0))*.5) ;
}

/**
 * Signed distance function describing the scene.
 * 
 * Absolute value of the return value indicates the distance to the surface.
 * Sign indicates whether the point is inside or outside the surface,
 * negative indicating inside.
 */
vec2 sceneSDF(vec3 samplePoint) {
    vec3 light1Pos = vec3(4.0 * sin(iTime),
                          2.0,
                          4.0 * cos(iTime));

    vec3 light2Pos = vec3(2.0 * sin(0.37 * iTime),
                          2.0 * cos(0.37 * iTime),
                          2.0);

    float stuff[10];
    stuff[0] = lightSdf(samplePoint-light1Pos);
    stuff[1] = lightSdf(samplePoint-light2Pos);
    stuff[2] = sphereSDF(samplePoint);
    stuff[3] = planeSDF(samplePoint);

    float m=99999.0;
    float mat=0.;
    for(int i = 0; i<=3; i++) {
        if( stuff[i] < m ) {
            mat = float(i);
            m = stuff[i];
        }
    }

    return vec2(m, mat);
}

/**
 * Return the shortest distance from the eyepoint to the scene surface along
 * the marching direction. If no part of the surface is found between start and end,
 * return end.
 * 
 * eye: the eye point, acting as the origin of the ray
 * marchingDirection: the normalized direction to march in
 * start: the starting distance away from the eye
 * end: the max distance away from the ey to march before giving up
 */
vec2 shortestDistanceToSurface(vec3 eye, vec3 marchingDirection, float start, float end) {
    vec2 depth = vec2(start,0.);

    for (int i = 0; i < MAX_MARCHING_STEPS; i++) {
        vec2 dist = sceneSDF(eye + depth.x * marchingDirection);
        if (dist[0] < EPSILON) {
			return depth;
        }
        depth += dist;
        depth.y=dist.y;
        if (depth.x >= end) {
            return vec2(end,0.);
        }
    }
    return vec2(end,0.);
}


/**
 * Return the normalized direction to march in from the eye point for a single pixel.
 * 
 * fieldOfView: vertical field of view in degrees
 * size: resolution of the output image
 * fragCoord: the x,y coordinate of the pixel in the output image
 */
vec3 rayDirection(float fieldOfView, vec2 size, vec2 fragCoord) {
    vec2 xy = fragCoord - size / 2.0;
    float z = size.y / tan(radians(fieldOfView) / 2.0);
    return normalize(vec3(xy, -z));
}

/**
 * Using the gradient of the SDF, estimate the normal on the surface at point p.
 */
vec3 estimateNormal(vec3 p) {
    return normalize(vec3(
        sceneSDF(vec3(p.x + EPSILON, p.y, p.z)).x - sceneSDF(vec3(p.x - EPSILON, p.y, p.z)).x,
        sceneSDF(vec3(p.x, p.y + EPSILON, p.z)).x - sceneSDF(vec3(p.x, p.y - EPSILON, p.z)).x,
        sceneSDF(vec3(p.x, p.y, p.z  + EPSILON)).x - sceneSDF(vec3(p.x, p.y, p.z - EPSILON)).x
    ));
}

/**
 * Lighting contribution of a single point light source via Phong illumination.
 * 
 * The vec3 returned is the RGB color of the light's contribution.
 *
 * k_a: Ambient color
 * k_d: Diffuse color
 * k_s: Specular color
 * alpha: Shininess coefficient
 * p: position of point being lit
 * eye: the position of the camera
 * lightPos: the position of the light
 * lightIntensity: color/intensity of the light
 *
 * See https://en.wikipedia.org/wiki/Phong_reflection_model#Description
 */
vec3 phongContribForLight(vec3 k_d, vec3 k_s, float alpha, vec3 p, vec3 eye,
                          vec3 lightPos, vec3 lightIntensity) {

    vec3 N = estimateNormal(p);
    vec3 L = normalize(lightPos - p);
    vec3 V = normalize(eye - p);
    vec3 R = normalize(reflect(-L, N));

    float dotLN = dot(L, N);
    float dotRV = dot(R, V);

    if (dotLN < 0.0) {
        // Light not visible from this point on the surface
        return vec3(0.0, 0.0, 0.0);
    } 

    if (dotRV < 0.0) {
        // Light reflection in opposite direction as viewer, apply only diffuse
        // component
        return lightIntensity * (k_d * dotLN);
    }
    return lightIntensity * (k_d * dotLN + k_s * pow(dotRV, alpha));
}

// light source material
vec3 lightSource(vec3 lightColor, vec3 lightPos, vec3 p, vec3 eye){
	return lightColor/pow(length(p-lightPos),1.0);
}

/**
 * Lighting via Phong illumination.
 * 
 * The vec3 returned is the RGB color of that point after lighting is applied.
 * k_a: Ambient color
 * k_d: Diffuse color
 * k_s: Specular color
 * alpha: Shininess coefficient
 * p: position of point being lit
 * eye: the position of the camera
 *
 * See https://en.wikipedia.org/wiki/Phong_reflection_model#Description
 */


vec3 phongIllumination(vec3 k_a, vec3 k_d, vec3 k_s, float alpha, vec3 p, vec3 eye, vec3 light1Pos, vec3 light2Pos, vec3 light1Intensity, vec3 light2Intensity) {
    vec3 color =  k_a;


    color = color + 10.*light1Intensity/pow(length(p-light1Pos),2.0);
    color = color + 10.*light2Intensity/pow(length(p-light2Pos),2.0);

    return color;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0*fragCoord-iResolution.xy)/iResolution.y;
	vec3 dir = rayDirection(50.0, iResolution.xy, fragCoord);
    vec3 eye = vec3(0, 1.5 , 9.0);
    vec2 dist = shortestDistanceToSurface(eye, dir, MIN_DIST, MAX_DIST);

    vec3 initColor = vec3(uv, 1.0-uv.y);
    vec3 color = initColor;
    if (dist.x > MAX_DIST - EPSILON) {
        // Didn't hit anything
    } else {

        // The closest point on the surface to the eyepoint along the view ray
        vec3 p = eye + dist.x * dir;

        vec3 K_a = vec3(0.5, 0.1, 0.1);
        vec3 K_d = vec3(0.7, 0.5, 0.4);
        vec3 K_s = vec3(1.0, 1.0, 1.0);
        float shininess = 200.0;

        vec3 light1Pos = vec3(4.0 * sin(iTime),
                          2.0,
                          4.0 * cos(iTime));
    	vec3 light2Pos = vec3(2.0 * sin(0.37 * iTime),
                          2.0 * cos(0.37 * iTime),
                          2.0);
    	vec3 light1Intensity = vec3(1., 0.4, 0.1);
    	vec3 light2Intensity = vec3(0.0, 0.4, 0.4);

        if (dist.x>2.) {
            if (dist.y==2.){
                color = phongIllumination(K_a, K_d, K_s, shininess, p, eye,  light1Pos, light2Pos, light1Intensity, light2Intensity);
            } else if (dist.y==3.) {
                color = phongIllumination(vec3(0.0, 0., 0.), vec3(0.1, 0.1, 0.5), vec3(.5, .5, 1.), 50., p, eye, light1Pos, light2Pos, light1Intensity, light2Intensity);
            } else if (dist.y==0.) {
                color = lightSource(light1Intensity, light1Pos, p, eye);
            } else if (dist.y==1.) {
                color = lightSource(light2Intensity, light2Pos, p, eye);
            }
        }

        color += estimateNormal(p)*0.2;
    }


    float foctor = fract(dist.x/MAX_DIST);
    color = color*(1.0-foctor)+foctor*initColor;//vec3(1.,.9,.8)/;

    //color=vec3(fract(dist));

    fragColor = vec4(color, 1.0);
}

