}

vec3 obscureColor(vec3 original, float factor)
{
    float seed = hash(vec2(original.r, factor)) * 2. - 1.;
    float obscureCoeff = .1;
    vec3 rgbAdjustmentCoeff = original * factor * obscureCoeff;
    return original + rgbAdjustmentCoeff * ( 2.*seed-1. );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = vec3(0.);

    float aspectRatio =  iResolution.y / iResolution.x;

    // brickHeight indicates screenspace dimension
    float brickHeight = .1;
    float brickWidth = brickHeight * 3.39 * aspectRatio;
    float colorRepeat = 20.;

    float timeScale = .15;

    uv.x += iTime * timeScale;
    uv.y += normalizeSin(sin(iTime * timeScale)) * colorRepeat * brickHeight;

    float fractY = fract(uv.y / brickHeight);

    // calcs based on nominal brick dimensions
    float mortarThickness = brickHeight * 1.25 / 3. / 2.25 / 2.;
    float xThickness = mortarThickness * aspectRatio;

    bool evenRow = mod(floor(uv.y / brickHeight), 2.) == 0.; 

    float repeatIncrementX = colorRepeat * brickWidth;
    float repeatIncrementY = colorRepeat * brickHeight;
    float xDist = evenRow ? uv.x + brickWidth / 2. : uv.x;
    float shadeValue = .6;
    vec3 mortarColor = vec3(.8);

    // horizontal mortar
    if (abs(uv.y - round(uv.y / brickHeight) * brickHeight) < mortarThickness / 2.)
    {   
        col = mortarColor;
        float floorSegY = floorSegment(uv.y, brickHeight);
        if ((uv.y - floorSegY) < mortarThickness * .75)
        {
            if (xDist - floorSegment(xDist, brickWidth) < brickWidth - mortarThickness * aspectRatio)
            {
                col *= shadeValue; 
            }
			else // angled shadow
            {
                float deltaY = floorSegY + (mortarThickness / 2.) - uv.y;
                float deltaX = (ceilSegment(xDist, brickWidth) - (xThickness / 2.) - xDist) / aspectRatio;
                if (deltaY < deltaX)
                {
                    col *= shadeValue;
                } 
            }
    	}
    }
    else // vertical mortar
    {
        // vertical mortar
        if (abs(xDist - roundSegment(xDist, brickWidth)) < xThickness / 2.)
        {
            col = mortarColor;
            float floorSegX = floorSegment(xDist, brickWidth);
            if (xDist - floorSegX < xThickness / 2.)
            {
                if (ceilSegment(uv.y, brickHeight) - uv.y > mortarThickness)
                {
                    col *= shadeValue;
                }
                else // angled shadow
                {
                    float deltaY = ceilSegment(uv.y, brickHeight) - (mortarThickness / 2.) - uv.y;
                    float deltaX = (floorSegX + (xThickness / 2.) - xDist) / aspectRatio;
                    if (deltaY > deltaX)
                    {
                        col *= shadeValue;
                    }                
                }
            }             
        }
        else
        {
            vec2 floored = vec2(floorSegment(xDist, brickWidth), floorSegment(uv.y, brickHeight));

            col = obscureColor(vec3(1., 156./255., 123./255.), hash(floored));

            // George's bricks
            if (false)
            {
                float gVal = (fragCoord/iResolution.xy).x;
                float dividedGVal = fract(gVal / .333);

                vec3[] colors = vec3[](
                    vec3(1.0, 0.0, 0.0), 	// red
                    vec3(1.0, .5, 0.0), 	// orange
                    vec3(1.0, 1.0, 0.), 	// yellow
                    vec3(0.0, 1.0, 0.0), 	// green
                    vec3(0.0, 1.0, 1.0), 	// blue
                    vec3(1.0, 0.0, 1.0) 	// purple
                );

                int colorCount = 7;
                float colorStep = 1. / float(colorCount);
                for (int i=0; i<colorCount; i++)
                {
                    vec3 color1 = colors[i];
                    vec3 color2 = (i == (colorCount - 1)) ? colors[2] : colors[i + 1];

                    float leftSide = float(i) * colorStep;
                    float rightSide = leftSide + colorStep;
                    if (gVal < leftSide || gVal > rightSide)
                        continue;

                    float cVal = smoothstep(leftSide, leftSide + colorStep, gVal);

                    if (cVal > 0.)
                    {
                        col = mix(color1, color2, cVal);
                    }
                } 
            }   
        }
    }

    fragColor = vec4(col,1.0);
}

