#define INFINITY         999.9e16
#define PI               3.14159265358979323
#define PHI              1.61803398874989484820459  // Golden Ratio   
#define EARTH_RADIUS      6360000.0 // in m
#define ATMOSPHERE_RADIUS 6420000.0 // in m
