float hash( vec2 p ) {
  return fract(sin(dot(p.xy ,vec2(12.9898,78.233))) * 43758.5453);
}
float noise( in vec2 p ) {
    vec2 i = floor( p );
    vec2 f = fract( p );	
	vec2 u = f*f*(3.0-2.0*f);
    return -1.0+2.0*mix( mix( hash( i + vec2(0.0,0.0) ), 
                     hash( i + vec2(1.0,0.0) ), u.x),
                mix( hash( i + vec2(0.0,1.0) ), 
                     hash( i + vec2(1.0,1.0) ), u.x), u.y);}


float sea_octave(vec2 uv, float choppy) {
    uv += noise(uv);        
    vec2 wv = 1.0-abs(sin(uv));
    vec2 swv = abs(cos(uv));    
    wv = mix(wv,swv,wv);
    return pow(1.0-pow(wv.x * wv.y,0.65),choppy);
}

float map(vec3 p) {
    float freq = SEA_FREQ;
    float amp = SEA_HEIGHT;
    float choppy = SEA_CHOPPY;
    vec2 uv = p.xz; uv.x *= 0.75;

    float d, h = 0.0;    
    for(int i = 0; i < ITER_GEOMETRY; i++) {        
    	d = sea_octave((uv+iTime)*freq,choppy);
    	d += sea_octave((uv-iTime)*freq,choppy);
        h += d * amp;        
    	uv *= octave_m; freq *= 1.9; amp *= 0.22;
        choppy = mix(choppy,1.0,0.2);
    }
    return p.y - h;
}

float map_detailed(vec3 p) {
    float freq = SEA_FREQ;
    float amp = SEA_HEIGHT;
    float choppy = SEA_CHOPPY;
    vec2 uv = p.xz; uv.x *= 0.75;

    float d, h = 0.0;    
    for(int i = 0; i < ITER_FRAGMENT; i++) {        
    	d = sea_octave((uv+iTime)*freq,choppy);
    	d += sea_octave((uv-iTime)*freq,choppy);
        h += d * amp;        
    	uv *= octave_m; freq *= 1.9; amp *= 0.22;
        choppy = mix(choppy,1.0,0.2);
    }
    return p.y - h;
}

vec3 getNormal(vec3 p, float eps) {
    vec3 n;
    n.y = map_detailed(p);    
    n.x = map_detailed(vec3(p.x+eps,p.y,p.z)) - n.y;
    n.z = map_detailed(vec3(p.x,p.y,p.z+eps)) - n.y;
    n.y = eps;
    return normalize(n);
}

float heightMapTracing(Ray ray, out vec3 p) {  
    float tm = 0.0;
    float tx = 1000.0;    
    float hx = map(ray.origin + ray.direction * tx);
    if(hx > 0.0) return tx;   
    float hm = map(ray.origin + ray.direction * tm);    
    float tmid = 0.0;
    for(int i = 0; i < NUM_STEPS; i++) {
        tmid = mix(tm,tx, hm/(hm-hx));                   
        p = ray.origin + ray.direction * tmid;                   
    	float hmid = map(p);
		if(hmid < 0.0) {
        	tx = tmid;
            hx = hmid;
        } else {
            tm = tmid;
            hm = hmid;
        }
    }
    return tmid;
}

float diffuse(vec3 n,vec3 l,float p) {
    return pow(dot(n,l) * 0.4 + 0.6,p);
}
float specular(vec3 n,vec3 l,vec3 e,float s) {    
    float nrm = (s + 8.0) / (PI * 8.0);
    return pow(max(dot(reflect(e,n),l),0.0),s) * nrm;
}


bool solveQuadratic(float a, float b, float c, out float t0, out float t1){
	float discrim = b * b - 4.0 * a * c;
	if (discrim < 0.0) return false;
	float rootDiscrim = sqrt(discrim);
	float q = (b > 0.0) ? -0.5 * (b + rootDiscrim) : -0.5 * (b - rootDiscrim); 
	t1 = q / a; 
	t0 = c / q;
	return true;
}

bool PlanetSphereIntersect( Ray ray, float rad, vec3 pos, inout float t0, inout float t1 ) {
	vec3 L = ray.origin - pos;
	float a = dot( ray.direction, ray.direction );
	float b = 2.0 * dot( ray.direction, L );
	float c = dot( L, L ) - (rad * rad);

	if (!solveQuadratic( a, b, c, t0, t1))
		return false;

	float temp;
	if (t0 > t1)
	{
		temp = t0;
		t0 = t1;
		t1 = temp;
	}
	return true;
}

vec3 calculateSkySphereColor(Ray r, float tmin, float tmax, vec3 sunDirection){ 
  vec3 earthCenter = vec3(0, -1.0 * EARTH_RADIUS, 0);
	vec3 betaR = vec3(3.8e-6, 13.5e-6, 33.1e-6); 
	vec3 betaM = vec3(21e-6);  
	float Hr = 7994.0;
	float Hm = 1200.0;
	float t0, t1; 
	if (!PlanetSphereIntersect(r, ATMOSPHERE_RADIUS, earthCenter, t0, t1) || t1 < 0.0) return vec3(0); 
	if (t0 > tmin && t0 > 0.0) tmin = t0; 
	if (t1 < tmax) tmax = t1; 
  float segmentLength = (tmax - tmin) / float(SKY_SAMPLES); 
	float tCurrent = tmin; 
	vec3 sumR = vec3(0); // rayleigh contribution
	vec3 sumM = vec3(0); // mie contribution 
	float opticalDepthR = 0.0;
	float opticalDepthM = 0.0; 
	float mu = dot(r.direction, sunDirection); // mu in the paper which is the cosine of the angle between the sun direction and the ray direction 
	float phaseR = 3.0 / (16.0 * PI) * (1.0 + mu * mu); 
	float g = 0.76; 
	float phaseM = 3.0 / (8.0 * PI) * ((1.0 - g * g) * (1.0 + mu * mu)) / ((2.0 + g * g) * pow(max(0.0, 1.0 + g * g - 2.0 * g * mu), 1.5)); 

  for (int i = 0; i < SKY_SAMPLES; ++i) { 
		vec3 samplePosition = r.origin + (tCurrent) * r.direction; 
		float height = length(samplePosition - earthCenter) - EARTH_RADIUS; 

		// compute optical depth for light
		float hr = exp(-height / Hr) * segmentLength; 
		float hm = exp(-height / Hm) * segmentLength; 
		opticalDepthR += hr; 
		opticalDepthM += hm; 

		// light optical depth
		float t0Light, t1Light; 
		PlanetSphereIntersect(Ray(samplePosition, sunDirection), ATMOSPHERE_RADIUS, earthCenter, t0Light, t1Light); 
		float segmentLengthLight = t1Light / float(SUN_SAMPLES);
		float tCurrentLight = 0.0; 
		float opticalDepthLightR = 0.0;
		float opticalDepthLightM = 0.0; 
		int jCounter = 0; 
    for (int j = 0; j < SUN_SAMPLES; ++j) { 
			vec3 samplePositionLight = samplePosition + (tCurrentLight) * sunDirection; 
			float heightLight = length(samplePositionLight - earthCenter) - EARTH_RADIUS; 
			if (heightLight < 0.0) break;
			jCounter += 1;
			opticalDepthLightR += exp(-heightLight / Hr) * segmentLengthLight; 
			opticalDepthLightM += exp(-heightLight / Hm) * segmentLengthLight; 
			tCurrentLight += segmentLengthLight; 
		} 
		if (jCounter == SUN_SAMPLES) { 
			vec3 tau = betaR * (opticalDepthR + opticalDepthLightR) + betaM * 1.1 * (opticalDepthM + opticalDepthLightM); 
			vec3 attenuation = vec3(exp(-tau.x), exp(-tau.y), exp(-tau.z)); 
			sumR += attenuation * hr; 
			sumM += attenuation * hm; 
		} 
		tCurrent += segmentLength; 
  } 

  vec3 attenuate_vec = (sumR * betaR * phaseR + sumM * betaM * phaseM) * SUN_POWER;
  //return attenuate_vec;
  // color map
  if (attenuate_vec.x < 1.413) { 
    attenuate_vec.x = pow(attenuate_vec.x * 0.38317, 1.0 / 2.2); 
  } else { 
    attenuate_vec.x = 1.0 - exp(-attenuate_vec.x);
  } 
  if (attenuate_vec.y < 1.413){
    attenuate_vec.y = pow(attenuate_vec.y * 0.38317, 1.0 / 2.2); 
  } else { 
    attenuate_vec.y = 1.0 - exp(-attenuate_vec.y);
  } 
  if (attenuate_vec.z < 1.413){
    attenuate_vec.z = pow(attenuate_vec.z * 0.38317, 1.0 / 2.2); 
  } else { 
    attenuate_vec.z = 1.0 - exp(-attenuate_vec.z);
  }
  return attenuate_vec;
} 

vec3 calculateSkyColor(Ray r, float tmin, float tmax, vec3 sunDirection){
  return calculateSkySphereColor(r, tmin, tmax, sunDirection);
}

vec3 perspectiveCameraRayDirection(in vec2 pixelCoord) {
  vec2 pixelPos = (pixelCoord) * 2.0 - 1.0;
  vec3 camRight   = vec3( 1.0,  0.0,  0.0);
  vec3 camUp      = vec3( 0.0,  1.0,  0.0);
  vec3 camForward = vec3(0.0, 0.0, 1.0);
  return normalize( pixelPos.x * camRight + pixelPos.y * camUp + camForward );
}


vec3 getSeaColor(vec3 p, vec3 n, vec3 l, vec3 eye, vec3 dist) {  
    float fresnel = clamp(1.0 - dot(n,-eye), 0.0, 1.0);
    fresnel = pow(fresnel,3.0) * 0.5;

    vec3 reflected = calculateSkyColor(Ray(vec3(0), reflect(eye,n)), 0.0, INFINITY, l);    
    vec3 base = SEA_BASE * calculateSkyColor(Ray(vec3(0), vec3(1.0)), 0.0, INFINITY, l); // SEA_BASE
    vec3 refracted = base + diffuse(n,l,80.0) * SEA_WATER_COLOR * 0.12; 

    vec3 color = mix(refracted,reflected,fresnel);

    float atten = max(1.0 - dot(dist,dist) * 0.01, 0.0);
    color += SEA_WATER_COLOR * (p.y - SEA_HEIGHT) * 0.18 * atten;
    color += vec3(specular(n,l,eye,20.0))* 0.8 * reflected;
    return color;
}

vec4 pixel(in vec2 uv ) {
  Ray r = Ray( vec3(0.0, 3.0,  -15.0), perspectiveCameraRayDirection(uv));
  vec3 sunDir = normalize(vec3(0,abs(cos(SUNTIME))/2.0 - 0.2, abs(sin(SUNTIME)) * 2.0));
  vec3 sky = calculateSkyColor(r, 0.0, INFINITY, sunDir);
  vec3 p;
  heightMapTracing(r,p);
  vec3 dist = p - r.origin;
  vec3 n = getNormal(p, dot(dist,dist) * EPSILON_NRM);

  vec3 waves = mix(
        sky,
        getSeaColor(p,n, sunDir, r.direction, dist),
    	pow(smoothstep(0.0,-0.5,r.direction.y),0.5));

  return vec4(waves, 1);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord ) {
  vec2 uv = fragCoord.xy / iResolution.xy;
  vec2 aa = 0.25 / iResolution.xy;
  fragColor = pixel(uv); 
  fragColor += pixel(uv + aa);
  fragColor *= 0.5;  
}

/**
 A very lazy and quick doodle of the Indian flag on this year's Indian Republic Day.
 Not the most efficient code, but I think it still looks cool. 
*/

#define PI 3.1415926535

#define ROT(x) mat2(cos(x), -sin(x), sin(x), cos(x))

#define ORANGE vec3(255., 154., 48.) / 255.
#define GREEN vec3(9., 137, 1.) / 255.
#define NAVY_BLUE vec3(0., 0., 137.) / 255.

#define RADIUS .7
#define HALF_RADIUS RADIUS * .5

// Hash by Dave_Hoskins
float hash(vec2 p)
{
	uvec2 q = uvec2(ivec2(p)) * uvec2(1597334673U, 3812015801U);
	uint n = (q.x ^ q.y) * 1597334673U;
	return float(n) / float(0xffffffffU);
}

// iq's 2d sdf for iscosceles triangles (https://www.shadertoy.com/view/MldcD7)
float isoscelesTriangle(in vec2 q, in vec2 p)
{
    p.y -= .5;
    p.x = abs(p.x);

	vec2 a = p - q * clamp(dot(p, q) / dot(q, q), 0., 1.);
    vec2 b = p - q * vec2(clamp(p.x / q.x, 0., 1.), 1.);

    float s = -sign(q.y);

    vec2 d = min(vec2(dot(a, a), s * (p.x * q.y - p.y * q.x)),
                  vec2(dot(b, b), s * (p.y - q.y)));

	return -sqrt(d.x) * sign(d.y);
}

// Probably not the best way to model the chakra, would have used the abs
// mirroring technique to efficiently draw it, but since it's a lazy and quick
// doodle so I said whatever, it doesn't matter either way.
float getChakra(vec2 uv)
{
	float outerCircle = smoothstep(.01, -.01, abs(length(uv) - RADIUS - .22) - .06);
    float innerCircle = smoothstep(.22, .20, length(uv));
    float spokes = 0., spokeThickness = .03, notches = 0., theta = 2. * PI / 24.;
    for (int i = 0; i < 24; ++i)
    {
        vec2 suv = ROT(float(i) * theta) * uv;
        // shorter inward pointing triangle
        suv.y += HALF_RADIUS;
        spokes += smoothstep(.015, -.015,
					isoscelesTriangle(vec2(spokeThickness, RADIUS * .334), suv));
        // longer outward pointing triangle
        suv.y -= RADIUS + .0025;
        spokes += smoothstep(.015, -.015,
					isoscelesTriangle(vec2(spokeThickness, -RADIUS * .666), suv));

        // boundary notches
		vec2 nuv = ROT(float(i) * theta + theta * .5) * uv;
        nuv.y -= RADIUS + .16;
        notches += smoothstep(.04, .032, length(nuv));
    }

    float chakra = notches + innerCircle + outerCircle + spokes;
    return clamp(chakra, 0., 1.);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2. * fragCoord - iResolution.xy) / min(iResolution.x, iResolution.y);
    vec2 st = fragCoord / iResolution.xy;
	float w = sin((uv.x + uv.y - iTime * .75 + sin(1.5 * uv.x + 4.5 * uv.y) * PI * .3)
                  * PI * .6); // fake waviness factor

    uv *= 1. + (.036 - .036 * w);
    vec3 col = vec3(0.);

    // flag colors
    col += 1.;
    col = mix(col, ORANGE, smoothstep(.35, .36, uv.y));
    col = mix(col, GREEN, smoothstep(-.35, -.36, uv.y));
    col = mix(col, NAVY_BLUE, getChakra(uv * 3.2));
    col += w * .225;

    float v = 16. * st.x * (1. - st.x) * st.y * (1. - st.y); // vignette
	col *= 1. - .6 * exp2(-1.75 * v);
    col = clamp(col - hash(fragCoord) * .004, 0., 1.);
    fragColor = vec4(col, 1.);
}

//rotate function from https://wgld.org/d/glsl/g017.html
vec3 rotate(vec3 p, float angle, vec3 axis){
    vec3 a = normalize(axis);
    float s = sin(angle);
    float c = cos(angle);
    float r = 1.0 - c;
    mat3 m = mat3(
        a.x * a.x * r + c,
        a.y * a.x * r + a.z * s,
        a.z * a.x * r - a.y * s,
        a.x * a.y * r - a.z * s,
        a.y * a.y * r + c,
        a.z * a.y * r + a.x * s,
        a.x * a.z * r + a.y * s,
        a.y * a.z * r - a.x * s,
        a.z * a.z * r + c
    );
    return m * p;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //from -1 to 1
    vec2 pos2d=( 2.*fragCoord - iResolution.xy )/iResolution.y;

    vec3 pos=vec3(pos2d,0.);
    float length2dSquared=dot(pos2d,pos2d);
    if(length2dSquared<=1.){
        pos.z=sqrt(1.0-length2dSquared);
    }else{
        discard;
    }
    pos=rotate(pos,iTime,vec3(0.,1.,0.));

    float t=(1.+sqrt(5.))/2.;
    vec3 planes[12];
    planes[0]=normalize(vec3(-1.,+t,0.));
    planes[1]=normalize(vec3(+1.,+t,0.));
    planes[2]=normalize(vec3(-1.,-t,0.));
    planes[3]=normalize(vec3(+1.,-t,0.));

    planes[4]=normalize(vec3(0.,-1.,+t));
    planes[5]=normalize(vec3(0.,+1.,+t));
    planes[6]=normalize(vec3(0.,-1.,-t));
    planes[7]=normalize(vec3(0.,+1.,-t));

    planes[8]=normalize(vec3(+t,0.,-1.));
    planes[9]=normalize(vec3(+t,0.,+1.));
    planes[10]=normalize(vec3(-t,0.,-1.));
    planes[11]=normalize(vec3(-t,0.,+1.));

    vec3 colorR=vec3(1.,0.,0.);
    vec3 colorG=vec3(0.,1.,0.);
    vec3 colorB=vec3(0.,0.,1.);
    vec3 colorC=vec3(0.,1.,1.);
    vec3 colorM=vec3(1.,0.,1.);
    vec3 colorY=vec3(1.,1.,0.);
    vec3 colors[12];
    colors[0]=colorR;
    colors[1]=colorG;
    colors[2]=colorG;
    colors[3]=colorR;

    colors[4]=colorB;
    colors[5]=colorC;
    colors[6]=colorC;
    colors[7]=colorB;

    colors[8]=colorM;
    colors[9]=colorY;
    colors[10]=colorY;
    colors[11]=colorM;

    int nearestIndex=0;
    float nearestCos=-1.;
    for(int i=0;i<12;i++){
        float c=dot(pos,planes[i]);
        if(nearestCos<c){
            nearestIndex=i;
            nearestCos=c;
        }
    }
    fragColor=vec4(colors[nearestIndex]*nearestCos*nearestCos,1.);

}

vec3 Circle(vec2 uv, vec2 p,float r, float blur, vec3 c){

    float d = length(uv - p);    
    d = smoothstep(blur, 0., d -r);


    vec3 col = vec3(d*c.r, d*c.g, d*c.b);
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;
    vec2 p = vec2(0., 0.);
    float offset = .125 * (sin(iTime)*.5+.5);
    vec3 c = Circle(uv, p, 0.3, 0.004, vec3(1., 0., 0.));
    c += Circle(uv, vec2(p.x - offset, p.y), 0.3, 0.004, vec3(0., 1., 0.));
    c += Circle(uv, vec2(p.x + offset, p.y), 0.3, 0.004, vec3(0., 0., 1.));

    // Output to screen
    fragColor = vec4(vec3(c), 1.0);
}

#define ZERO (min(iFrame,0))
#define PI 3.1415926535898
#define FAR 12.
#define bg_color vec3(0.02,0.15,0.5)
#define bg_color2 vec3(0.2,0.35,0.5)

float camYaw;
vec3 camPos;
vec2 global_uv;

// http://iquilezles.org/www/articles/checkerfiltering/checkerfiltering.htm
float checkersGradBox( in vec2 p )
{
    vec2 w = fwidth(p) + 0.001;
    vec2 i = 2.0*(abs(fract((p-0.5*w)*0.5)-0.5)-abs(fract((p+0.5*w)*0.5)-0.5))/w;
    return 0.5 - 0.5*i.x*i.y;
}

vec3 get_background( vec3 pos )
{
    return mix( bg_color2, bg_color, global_uv.y * 2. + 0.5 );
}

float sdSphere( vec3 p, float s )
{
  return length(p)-s;
}

vec3 distortPos(in vec3 pos)
{
    vec3 p = pos;
    float xx = (p - camPos).x;
    float zz = (p - camPos).z;
    float xxx = -(cos(camYaw)*zz-sin(camYaw)*xx);
    float zzz = -(cos(camYaw)*xx+sin(camYaw)*zz);
    float cx = cos(xxx * 0.3 ) * 1.1;
    if (cx < 0.0) cx *= 2.0;
    float cz = cos((zzz + 3.0)* 0.3 ) * 1.1;
    if (cz < 0.0) cz *= 1.5;
    p.y -= cx + cz;
    return p;
}

vec2 map(vec3 pos)
{
    vec3 p = distortPos(pos);
    vec2 res;

    res.x = FAR; // distance
    res.y = 0.0; // material id

    // floor plane
    if(p.y > 0.0) res = vec2(min(FAR, p.y), 1.);

    vec3 sPos;
    // blue spheres
    sPos.x = mod(p.x - 0.5, 2.0) -0.5;
    sPos.y = p.y - 0.15;
    sPos.z = mod(p.z - 0.5, 4.0) - 0.5;
    float d2 = sdSphere(sPos, 0.15);
    if (d2 < res.x) res = vec2(d2, 3.0);

    // red spheres
    sPos.x = mod(p.x - 1.5, 4.0) -0.5;
    sPos.z = mod(p.z - 1.5, 1.0) - 0.5;
    float d3 = sdSphere(sPos, 0.15);
    if (d3 < res.x) res =vec2(d3, 4.0);

    // white spheres
    sPos.x = mod(p.x - 0.5, 2.0) -0.5;
    sPos.z = mod(p.z - 1.5, 2.0) - 0.5;
    float d4 = sdSphere(sPos, 0.15);
    if (d4 < res.x) res = vec2(d4, 5.0);

    return res;
}

vec3 get_normal(vec3 p) {
    const vec2 e = vec2(0.0001, 0);
    return normalize(vec3(map(p + e.xyy).x-map(p - e.xyy).x,
                          map(p + e.yxy).x-map(p - e.yxy).x,
                          map(p + e.yyx).x-map(p - e.yyx).x));
}

float get_ao(vec3 p, vec3 n)
{
    float r = 0.0, w = 1.0, d;
    for(float i=1.0; i<5.0+1.1; i++)
    {
        d = i/5.0;
        r += w*(d - map(p + n*d).x);
        w *= 0.5;
    }
    return 1.0-clamp(r,0.0,1.0);
}

vec2 intersect(vec3 ro, vec3 rd)
{
    float t = 0.0, dt;
    vec2 r;
    for(int i=0; i<128; i++){
        r = map(ro + rd * t);
        dt = r.x;
        if(dt<0.002 || t>FAR){ break; }
        t += dt * 0.8;
    }
    return vec2(t,r.y);
}

// http://iquilezles.org/www/articles/rmshadows/rmshadows.htm
float calcSoftshadow( in vec3 ro, in vec3 rd, in float mint, in float tmax )
{
    float res = 1.0;
    float t = mint;
    for( int i=ZERO; i<16; i++ )
    {
        float h = map( ro + rd*t ).x;
        res = min( res, 8.0*h/t );
        t += clamp( h, 0.02, 0.10 );
        if( res<0.005 || t>tmax ) break;
    }
    return clamp( res, 0.0, 1.0 );
}

vec3 star2d(in vec3 pos)
{
    // adapted from: http://pryme8.com/tag/glsl/
    vec3 c = vec3(1.5,1.5,1.5);
    vec2 offsetFix = vec2(1.,0.83);
    float ss, tt, angle, r, a;
    float starAngle = 2.*PI/5.;
    vec3 p0 = 0.14*vec3(cos(0.),sin(0.), 0.);
    vec3 p1 = 0.06*vec3(cos(starAngle/2.),sin(starAngle/2.), 0.);
    vec3 d0 = p1 - p0;
    vec3 d1;
    vec3 p = distortPos(pos);
    ss = mod(p.x,2.0) - offsetFix.x;
    tt = (1. - mod(p.y,2.0)) - offsetFix.y;
    angle = atan(ss, tt) + PI;
    r = sqrt(ss*ss + tt*tt);
    a = mod(angle, starAngle)/starAngle;
    if (a >= 0.5){a = 1.0 - a;}
    d1 = r*vec3(cos(a), sin(a), 0.) - p0;
    float in_out = smoothstep(0., 0.001, cross(d0 , d1).z);
    return mix(vec3(1.5), vec3(1.,0.,0.0), in_out); ;
}

vec3 lighting(vec3 rd, vec3 ro, vec3 pos, vec3 n, float matid)
{
    if ( matid < 1.0 ){
        return get_background( pos );
    }

    float z = length(pos - ro);

    vec3 mate = vec3(1.0);

    float spe_factor = 3.0;
    float fog_factor = 0.005;
    float black_rim_factor = 1.0;

    if (matid < 1.9)
    {
        mate = vec3(1.0,0.5,0.1) * (vec3(0.4) + vec3(checkersGradBox( vec2(pos.x, pos.z) ) ));
        spe_factor = 0.05;
        fog_factor = 0.002;
        black_rim_factor = 0.0;
    }
    else if (matid < 3.9)
    {
        // blue spheres
        mate = vec3(0.05,0.3,0.9);
    }
    else if (matid < 4.9){
        // red spheres
        mate = vec3(1.0,0.05,0.0);
    }
    else if (matid < 5.9)
    {
        // white spheres
        mate = star2d(pos);
    }

    vec3 lp0 = camPos +  vec3(0.0, 1.0, -1.0);
    vec3 ld0 = normalize(lp0 - pos);
    float dif = max(0.0, dot(n, ld0));
    vec3 lin = vec3(1.0);
    float spe = max(0.0, pow(clamp(dot(ld0, reflect(rd, n)), 0.0, 1.0), 20.0));
    float ao = get_ao(pos, n);
    lin = 10.0 * vec3(1.0) * spe * spe_factor;
    lin += (1.0 + dif) * ao;
    lin = lin * 0.22 * mate;
    vec3  lig = normalize( vec3(0.0, 1.0, 0.0) );
    lin *= vec3(0.5) + calcSoftshadow( pos, lig,  0.05, 1.0 );
    // extra black rim to mimic original game spheres shade
    float black_rim = max(dot(n,-rd),0.);
    black_rim += max(dot(n,vec3(0.,1.,0.)),0.);
    black_rim *= black_rim;
    black_rim = smoothstep(0.,0.6,black_rim);
    lin = mix( lin, lin * (0.25+0.75*black_rim), black_rim_factor);
    // fog
    lin = mix( lin, get_background(pos), 1.-exp(-fog_factor*z*z));

    return lin;
}

vec3 shade(vec3 ro, vec3 rd)
{
    vec3 col = get_background(camPos);
    vec2 res = intersect(ro, rd);
    if(res.x < FAR)
    {
        vec3 pos = ro + rd * res.x;
        vec3 n = get_normal(pos);
        col = lighting(rd,ro, pos, n, res.y);
    }
    return col;
}

void updateCamera( in vec2 fragCoord, out vec3 rayOrigin, out vec3 rayDirection )
{
    vec2 uv = (fragCoord.xy - iResolution.xy * 0.5)/ iResolution.xy;
    uv.x *= iResolution.x / iResolution.y;
    global_uv = uv;
    float velocity = 1.25;

    float t = iTime;

    float xpos = 0.0;
    xpos = sin(t * 0.5)*cos(t * 0.25) * 4.0;

    float phase = t * 0.1;
    vec3 ro = vec3(xpos, 3.25+0.75*cos(phase)*cos(phase), -t * velocity);
    vec3 ta = ro + vec3( sin(phase), 0.85 - 0.25*sin(phase)*sin(phase) , cos(phase)*cos(phase));

    float FOV = 1.0;
    vec3 fwd = normalize(ro - ta);
    vec3 rgt = normalize(vec3(fwd.z, 0., -fwd.x ));
    vec3 up = cross(fwd, rgt);
    vec3 rd = fwd + FOV*(uv.x*rgt + uv.y*up);
    camYaw = atan( fwd.z, fwd.x );
    rd = normalize(rd);

    rayOrigin = ro;
    rayDirection = normalize(rd);

    camPos = ro;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 ro, rd;
    updateCamera( fragCoord, ro, rd );
    vec3 col = shade(ro, rd);
    col=pow(clamp(col,0.0,1.0),vec3(0.45));
    col=mix(col, vec3(dot(col, vec3(0.33))), -0.5);
    fragColor = vec4(col, 1.0);
}

