    float Luz;
    float sdbfin;

    numbar=10.0;
    anchoMat=0.25;
    numLuz=numbar-1.0;
    total=13.0;
    Luz=(total-numbar*anchoMat)/numLuz;

    sdbfin=999.9;



    //horizontal
    float sdbh1sup =sdBox(p-vec3(-total/2.0,6.0,0.0), vec3(total/2.0,0.25,0.25) );
    float sdbh1inf =sdBox(p-vec3(-total/2.0,1.0,0.0), vec3(total/2.0,0.25,0.25) );

    //vertical
    float sdbv1 =sdBox(p-vec3(-total-0.,0.0,0.0), vec3(0.25,6.5,0.25) ); //inicio
    float sdbv2 =sdBox(p-vec3(total-0.,0.0,0.0), vec3(0.25,6.5,0.25) );  //fin


    res =opU(res, vec2(sdbh1sup,8.0));
    res =opU(res, vec2(sdbh1inf,8.0));


