    float a = atan(p.y, p.x) + angle/2.;
    float r = length(p);
    float c = floor(a/angle);
    a = mod(a,angle) - angle/2.*fix;
    p = vec2(cos(a), sin(a))*r;

    return p;
}

// puma logo
float dPumaLogo(vec3 p, float artifact) {
    float y = 0.25;
    // stripe
    mat3 rot = matRotateX(radians(90.0));
    float dstripe0 = sdCappedCylinder((p-vec3(0.0,1.0+y,0.0))*rot,1.0,0.1);
    float dstripe1 = sdCappedCylinder((p-vec3(-1.15,1.0+y,0.0))*rot,1.0,0.2);

    mat3 rotZ = matRotateZ(radians(6.0));
    vec3 bp = (p-vec3(-0.63,1.88+y,0.0));

    const float k = -1.0;
    float c = cos(k*bp.x);
    float s = sin(k*bp.x);
    mat2  m = mat2(c,-s,s,c);
    vec3  q = vec3(m*bp.xy,bp.z);

    mat3 rotZ2 = matRotateZ(radians(3.5));
    vec3 bp2 = (p-vec3(-0.85,1.87+y,0.0));
    float sc = mix(1.5,0.77,smoothstep(-1.0,1.0,bp2.x));
    bp2.y *= sc;    

    float dstripe2 = sdBox(q*rotZ,vec3(1.0,0.15,0.1))*0.6;
    float dstripe3 = sdBox(bp2*rotZ2,vec3(1.0,0.15,0.1))*0.6;

    float dstripe = min(max(-p.y+1.3,max(-dstripe1,dstripe0 )),min(max(-p.x-0.5, dstripe2),dstripe3));

    // logo outline
    p.x *= 0.55;
    float doutline0 = sdCappedCylinder((p-vec3(-0.2,1.55+y,0.0))*rot,1.1,0.1)*artifact;
    float doutline1 = sdCappedCylinder((p-vec3(-0.2,1.55+y,0.0))*rot,1.0,0.2)*artifact;
    float doutline = max(-doutline1,doutline0);
    return min(dstripe,doutline);
}

vec4 pumaLogo(vec3 p){
    p.y -= iTime*5.0;
    p.y = mod(p.y,10.0)-5.0;
    p *=  matRotateY(radians(-90.0));
    p*=0.8;


    float dlogo = dPumaLogo(p,0.3);

    return vec4(vec3(1.0),dlogo*0.9);
}

float dTwoZeroX2(vec3 p) {
    vec3 prevP = p;
    p.x = mod(p.x,2.8)-1.4;

    vec3 twoPos = vec3(-0.7,0.0,0.0);
    float dtwo = sdBox(p+twoPos,vec3(0.6,1.0,0.1));
    float dtwo1 = sdBox(p+twoPos+vec3(-0.6,-0.4,0.0),vec3(0.8,0.25,0.2));
    float dtwo2 = sdBox(p+twoPos+vec3(0.6,0.45,0.0),vec3(0.8,0.25,0.2));
    float dresTwo = max(-dtwo2,max(-dtwo1,dtwo))*0.9;

    vec3 zeroPos = vec3(0.7,0.0,0.0);
    float dzero = sdBox(p+zeroPos,vec3(0.6,1.0,0.1));
    float dzero1 = sdBox(p+zeroPos,vec3(0.3,0.7,0.2));
    float dresZero = max(-dzero1,dzero)*0.9;

    p = prevP;

    float dres = max(sdBox(p,vec3(2.8,2.0,0.3)),min(dresTwo,dresZero));
    return dres*0.9;
}

float cubicInOut(float t) {
    return t < 0.5
    ? 4.0 * t * t * t
    : 0.5 * pow(2.0 * t - 2.0, 3.0) + 1.0;
}

// A smart way to control the animation. function is from "http://qiita.com/gaziya5/items/29a51b066cb7d24983d6"
float animscene(in float t, float w, float s) {
    return clamp(t - w, 0.0, s) / s;  
}

vec4 GetDist(vec3 p) {
    vec3 prevP = p;

    p.xz = pModPolar(p.xz,10.0,1.0);
    p.x -= 20.0;

    vec4 logo = pumaLogo(p);

    p = prevP;
    float d2020 = dTwoZeroX2(p);
    float dlogo = dPumaLogo(p+vec3(0.0,1.7,0.0),0.6);

    float animTime = mod(iTime,6.0);
    float morphVal = 0.0;
    morphVal += cubicInOut(animscene(animTime, 1.0, 1.0))*1.0;
    morphVal -= cubicInOut(animscene(animTime, 3.0, 1.0))*1.0;

    vec4 logo2 = vec4(vec3(1.0),mix(dlogo,d2020,morphVal));

    vec4 model = combine(logo,logo2);
    return model;
}

vec4 RayMarch(vec3 ro, vec3 rd) {
    vec4 dO= vec4(0.0);

    for(int i=0; i<MAX_STEPS; i++) {
        vec3 p = ro + rd*dO.w;
        vec4 dS = GetDist(p);
        dO.w += dS.w;
        dO.xyz = dS.xyz;
        if(dO.w>MAX_DIST || dS.w<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
    float d = GetDist(p).w;
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy).w,
        GetDist(p-e.yxy).w,
        GetDist(p-e.yyx).w);

    return normalize(n);
}

float shadowMap(vec3 ro, vec3 rd){
    float h = 0.0;
    float c = 0.001;
    float r = 1.0;
    float shadow = 0.5;
    for(float t = 0.0; t < 30.0; t++){
        h = GetDist(ro + rd * c).w;
        if(h < 0.001){
            return shadow;
        }
        r = min(r, h * 16.0 / c);
        c += h;
    }
    return 1.0 - shadow + r * shadow;
}

float GetAmbientOcclusion(vec3 p, vec3 n)
{
    const int steps = 4;
    const float delta = 0.15;

    float a = 0.0;
    float weight = 4.;
    for(int i=1; i<=steps; i++) {
        float d = (float(i) / float(steps)) * delta; 
        a += weight*(d - RayMarch(p + n*d,n).w);
        weight *= 0.5;
    }
    return clamp(1.0 - a, 0.0, 1.0);
}

vec2 GetLight(vec3 p) {
    vec3 lightPos = vec3(2,8,3);
    vec3 l = normalize(lightPos-p);
    vec3 n = GetNormal(p);

    float dif = clamp(dot(n, l)*.5+.5, 0., 1.);
    float d = RayMarch(p+n*SURF_DIST*2., l).w;

    float ao = GetAmbientOcclusion(p,n);

    float lambert = max(.0, dot( n, l))*0.1;

    float shadow = shadowMap(p + n * 0.001, l);

    return vec2((lambert+dif*ao),max(0.9, shadow)) ;
}

vec3 R(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = p+f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i-p);
    return d;
}

vec3 bg(vec2 p){
    vec3 col = .94*max(mix(vec3(0.0,0.5,0.0)+(.1-length(p)/3.),vec3(1),.1),0.);
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 m = iMouse.xy/iResolution.xy;

    vec3 col = vec3(0);

    vec3 ro = vec3(0, 4, -5);
    #if USE_MOUSE == 1
    ro.yz *= Rot(-m.y*3.14+1.);
    ro.xz *= Rot(-m.x*6.2831);
    #else
    ro.xz *= Rot(radians(iTime*-30.0));
    ro.yz *= Rot(radians(30.0));
    #endif

    vec3 rd = R(uv, ro, vec3(0,1,0), 1.);

    vec4 d = RayMarch(ro, rd);

    if(d.w<MAX_DIST) {
        vec3 p = ro + rd * d.w;
        vec3 n = GetNormal(p);
        vec3 r = reflect(rd,n);
        float spec = pow(max(0.0,r.y),20.);
        float dif = dot(n,normalize(vec3(1,2,3)))*0.5+0.5;
        col = mix(bg(r.xy),vec3(dif),0.5)+spec;
    } else {
        // background
        col = bg(uv);
    }

    fragColor = vec4(col,1.0);
}

//Carter S. Jan 2020
//Basic Raymarch of a Jack
//Based on https://www.shadertoy.com/view/tdXGWr


//from the Sphere example
float distance_from_sphere(in vec3 p, in vec3 c, float r)
{
    return length(p - c) - r;
}

// Most Shadertoy users use these sd functions to define implicit volumes
// I typically prefer more descriptive names in educational code but
// IQ's code is so widely used on the site that I'm going to begin using functions here and there
// http://iquilezles.org/www/articles/distfunctions/distfunctions.htm
float dot2(in vec3 v ) { return dot(v,v); }
float sdRoundCone(vec3 p, vec3 a, vec3 b, float r1, float r2)
{
    // sampling independent computations (only depend on shape)
    vec3  ba = b - a;
    float l2 = dot(ba,ba);
    float rr = r1 - r2;
    float a2 = l2 - rr*rr;
    float il2 = 1.0/l2;

    // sampling dependant computations
    vec3 pa = p - a;
    float y = dot(pa,ba);
    float z = y - l2;
    float x2 = dot2( pa*l2 - ba*y );
    float y2 = y*y*l2;
    float z2 = z*z*l2;

    // single square root!
    float k = sign(rr)*rr*rr*x2;
    if( sign(z)*a2*z2 > k ) return  sqrt(x2 + z2)        *il2 - r2;
    if( sign(y)*a2*y2 < k ) return  sqrt(x2 + y2)        *il2 - r1;
                            return (sqrt(x2*a2*il2)+y*rr)*il2 - r1;
}


//This function grows with more objects and operations (unions, distortions, perturbations, ect)
//This is usually where acceleration structures and ad hoc hackery goes down
float map_the_world(in vec3 p)
{
    float jack_dist = sdRoundCone(p, vec3(0.0,0.0,0.0), vec3(0.4,0.0,0.0), 0.1, 0.05 );
	jack_dist = min(jack_dist,sdRoundCone(p, vec3(0.0,0.0,0.0), vec3(-0.4,0.0,0.0), 0.1, 0.05 ));
    jack_dist = min(jack_dist,sdRoundCone(p, vec3(0.0,0.0,0.0), vec3(0.0,0.4,0.0), 0.1, 0.05 ));
    jack_dist = min(jack_dist,sdRoundCone(p, vec3(0.0,0.0,0.0), vec3(0.0,-0.4,0.0), 0.1, 0.05 ));
    jack_dist = min(jack_dist,sdRoundCone(p, vec3(0.0,0.0,0.0), vec3(0.0,0.0,0.4), 0.1, 0.05 ));
    jack_dist = min(jack_dist,sdRoundCone(p, vec3(0.0,0.0,0.0), vec3(0.0,0.0,-0.4), 0.1, 0.05 ));
    jack_dist = min(jack_dist,distance_from_sphere(p,vec3(0.4,0.0,0.0),0.1));
    jack_dist = min(jack_dist,distance_from_sphere(p,vec3(-0.4,0.0,0.0),0.1));
    jack_dist = min(jack_dist,distance_from_sphere(p,vec3(0.0,0.0,0.4),0.1));
    jack_dist = min(jack_dist,distance_from_sphere(p,vec3(0.0,0.0,-0.4),0.1));



    return jack_dist;
}


//Find the normal of the surface at this point P
vec3 calculate_normal( in vec3 pos )
{
    vec2 e = vec2(1.0,-1.0)*0.5773;
    const float eps = 0.0005;
    return normalize( e.xyy*map_the_world( pos + e.xyy ) + 
					  e.yyx*map_the_world( pos + e.yyx ) + 
					  e.yxy*map_the_world( pos + e.yxy ) + 
					  e.xxx*map_the_world( pos + e.xxx ) );  
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //the bulk of this comes from https://www.shadertoy.com/view/tdXGWr
    //Ive removed the Anti-Aliasing for simplicity and performance

    // camera movement	
	float an = 0.5*(iTime-10.0);
	vec3 ray_origin = vec3( 1.0*cos(an), 0.4, 1.0*sin(an) );
    vec3 origin = vec3( 0.0, 0.0, 0.0 );
    // camera matrix (for ease of animation)
    vec3 ww = normalize( origin - ray_origin );
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
    vec3 vv = normalize( cross(uu,ww));


    //initialize color
    vec3 total = vec3(0.0);

 	//what pixel are we marching through
    vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;


	// create view ray
    vec3 ray_direction = normalize( p.x*uu + p.y*vv + 1.5*ww );

    // raymarch
    const float tmax = 2.0; //max march depth
    float t = 0.0;//current depth
    for( int i=0; i<256; i++ )
    {
        vec3 pos = ray_origin + t*ray_direction;
        //get dist to nearest surface
        float h = map_the_world(pos);
        //if we hit something break
        if( h<0.0001 || t>tmax ) break;
        //step forward
        t += h;
    }


     // ambient + diffuse light color calculations
     vec3 color = vec3(0.0);
     if( t<tmax )
     {
        vec3 pos = ray_origin + t*ray_direction;
        vec3 normal = calculate_normal(pos);
        float diffuse = clamp( dot(normal,ray_origin + vec3(0.2,0.0,0.0)), 0.0, 1.0 );
        float ambient = 0.7 + 0.5*dot(normal,vec3(1.0,1.0,0.0));
        color = vec3(0.2,0.3,0.4)*ambient + vec3(0.8,0.7,0.5)*diffuse*0.5;
     }

     // gamma color calculation    
     color = sqrt( color );
	 total += color;

	 fragColor = vec4( total, 1.0 );
}

#define MAX_ITER 1000.0

#define float highp float

float map(float value, float min1, float max1, float min2, float max2) {
  return min2 + (value - min1) * (max2 - min2) / (max1 - min1);
}

vec2 complexMul(vec2 a, vec2 b) {
	return vec2(a.x * b.x - a.y * b.y, a.x * b.y + a.y * b.x);
}

bool absLessThan(vec2 c, float b) {
	return c.x * c.x + c.y * c.y < b * b;
}

vec2 mandelbrot(vec2 c) {
    float i = 0.;
	vec2 z = c;
    while (++i < MAX_ITER && absLessThan(z, 2.0)) {
    	z = complexMul(z, z) + c;
    }

    return vec2(i, z.x * z.x + z.y * z.y);
} 

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float zoom = 1.0/(iTime*iTime);
    vec2 offset = 1.0-iMouse.xy/iResolution.xy*2.0;

    vec4 w = vec4(-2.5, 1.0, -1.0, 1.0);
    vec2 coord = vec2(map(fragCoord.x, 0.0, iResolution.x, w.x, w.y), 
                      map(fragCoord.y, 0.0, iResolution.y, w.z, w.w)) * zoom + offset;

    vec2 iter = mandelbrot(coord);

    // Time varying pixel color
    vec3 col = vec3(iter/(MAX_ITER-800.0), 0.0);

    // Output to screen
    fragColor = vec4(col,1.0);
}

precision highp float;

#define Time floor(iTime)*30.0

const float pi = acos(-1.);
const float pi2 = pi*2.;
float pmodid;

mat3 m = mat3( 0.00,  0.80,  0.60,
              -0.80,  0.36, -0.48,
              -0.60, -0.48,  0.64);

float hash(float n)
{
    return fract(sin(n) * 4378.553);
}

///
/// Noise function
///
float noise(in vec3 x)
{
    vec3 p = floor(x);
    vec3 f = fract(x);

    f = f * f * (3.0 - 2.0 * f);

    float n = p.x + p.y * 57.0 + 113.0 * p.z;

    float res = mix(mix(mix(hash(n +   0.0), hash(n +   1.0), f.x),
                        mix(hash(n +  57.0), hash(n +  58.0), f.x), f.y),
                    mix(mix(hash(n + 113.0), hash(n + 114.0), f.x),
                        mix(hash(n + 170.0), hash(n + 171.0), f.x), f.y), f.z);
    return res;
}


float rand(vec2 co){
    float a = 52.9498;
    float b = 48.213;
    float c = 438.5453;
    float dt= dot(co.xy ,vec2(a,b));
    float sn= mod(dt,3.14);
    return fract(sin(sn) * c);
}

///
/// Fractal Brownian motion.
///
/// Refer to:
/// EN: https://thebookofshaders.com/13/
/// JP: https://thebookofshaders.com/13/?lan=jp
///
float fbm(vec3 p)
{
    float f;
    f  = 0.5000 * noise(p); p = m * p * 2.02;
    f += 0.2500 * noise(p); p = m * p * 2.03;
    f += 0.1250 * noise(p);
    return f;
}

vec3 hsv(float h, float s, float v) {
	vec3 a = fract(h+vec3(0.,2.,1.)/3.)*6.-3.;
	a = clamp(abs(a)-1.,0.,1.)-1.;
	a = a*s +1.;
	return a*v;
}

mat2 rot(float t){
    float s = sin(t),c = cos(t);
    return mat2(c,s,-s,c);
}

vec2 pmod(vec2 p, float r)
{
    float a = atan(p.x, p.y) + pi / r;
    float n = pi2 / r;
    pmodid = floor(a / n);
    a = pmodid * n;
    return p * rot(-a);
}

vec2 opI(vec2 d1, vec2 d2)
{
    return (d1.x<d2.x) ? d2 : d1;
}

vec2 map(vec3 p){

    vec3 bp = p;    
    vec3 pp = p;

    float period = 5.0;
    float v = floor(iTime/period)*period+smoothstep(0.2, 0.8,mod(iTime,period));
    p.xz *= rot(pi*v);

    float period2 = 8.0;
    v = floor(iTime/period2)*period2+smoothstep(0.2, 0.8,mod(iTime,period2));
    p.yz *= rot(pi*v*2.0);


    float f = fbm(bp * 0.2+iTime+0.001);


    bp.xy = pmod(bp.xy,40.0);


    bp.y -= 30.0;
    vec2 bd = vec2(0.1 - length(bp) * 0.1+f*0.6,1.0);


    for(int i = 0; i < 2;i++){
        bp.y -= 6.0;

        bp.xy *= rot(pi/20.0*float(i));
    	bd = opI(bd,vec2(0.1 - length(bp) * 0.1+f*0.6,1.0));
    }


    vec3 lp = p;
    lp.xy *= rot(-Time/60.0);  
    float f2 = fbm(lp * 0.1+iTime);


    lp.y -= 10.;
    vec2 longhand = vec2(0.1 - length(lp*vec3(1.5,0.3,1.0)) * 0.1 +f2,3.0);


    vec3 sp = p;  
    sp.xy *= rot(-Time/720.0);   
    sp.y -= 6.;
    vec2 shorthand = vec2(0.1 - length(sp*vec3(1.5,0.5,1.0)) * 0.1 +f2,3.0);



    p.xy = pmod(p.xy,12.0);
    p.y -= 23.0;
    vec2 dial = vec2(0.1 - length(p) * 0.1 + f*0.5,2.0);

    pmod(pp.xy,40.0);

    //return opI(opI(shorthand,longhand),bd);
    return opI(opI(dial,opI(shorthand,longhand)),bd);
}


//https://www.shadertoy.com/view/WslGWl

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord*2.0 - iResolution.xy)/min(iResolution.x,iResolution.y);
    vec3 ro = vec3(0.0,0.0,-45.0);
    float screenZ = 1.0;
    vec3 rd = normalize(vec3(uv,screenZ));


    // For raymarching const values.
    const int sampleCount = 34;
    const int sampleLightCount = 2;
    const float eps = 0.01;

    // Raymarching step settings.
    float zMax = 65.0;
    float zstep = zMax / float(sampleCount);

    float zMaxl = 20.0;
    float zstepl = zMaxl / float(sampleLightCount);

    // Easy access to the ray origin
    vec3 p = ro;

    // Transmittance
    float T = 1.0;

    // Substantially transparency parameter.
    float absorption = 60.0;

    // Light Direction
    vec3 sun_direction = normalize(vec3(1.0, 0.0, 0.0));

    vec4 col = vec4(0.0);

    vec2 mat;


    for (int i = 0; i < sampleCount; i++)
    {


        mat = map(p);
        float density = mat.x;

        if (density > 0.0)
        {

            float tmp = density / float(sampleCount);

            T *= 1.0 - (tmp * absorption);

            if (T <= 0.01)
            {
                break;
            }

            float Tl = 1.0;

            vec3 lp = p;

            for (int j = 0; j < sampleLightCount; j++)
            {
                float densityLight = map(lp).x;

                if (densityLight > 0.0)
                {
                    float tmpl = densityLight / float(sampleCount);
                    Tl *= 1.0 - (tmpl * absorption);
                }

                if (Tl <= 0.01)
                {
                    break;
                }

                lp += sun_direction * zstepl;
            }

            float opaity = 50.0;
            float k = opaity * tmp * T;            

            vec4 cloudColor = vec4(vec3(0.01,0.11,0.5),1.0);

            if(mat.y == 1.0){
                cloudColor.rgb = hsv(pmodid/40.0,1.,1.5);           
            }else if(mat.y == 3.0){
                cloudColor.rgb = vec3(.0,.8,.9); 

            }else{
                pmod(p.xy,12.0);
                cloudColor.rgb = hsv(pmodid/12.0,1.0,1.8);                   
            }

            vec4 col1 = cloudColor * k;

            float opacityl = 30.0;
            float kl = opacityl * tmp * T * Tl;
            vec4 lightColor = vec4(.0, 0., .1, 1.0);
            vec4 col2 = lightColor * kl;

            col += col1 + col2*3.0;
        }

        p += rd * zstep;
    }

    col.a *= 0.5;

    if(col.a == 0.0){
        col.rgb = vec3(0.0);
    }
    // Output to screen
    fragColor = vec4(col);
}

