const int N = 12;

float sdPoly( in vec2[N] v, in vec2 p )
{
    const int num = v.length();
    float d = dot(p-v[0],p-v[0]);
    float s = 1.0;
    for( int i=0, j=num-1; i<num; j=i, i++ )
    {
        // distance
        vec2 e = v[j] - v[i];
        vec2 w =    p - v[i];
        vec2 b = w - e*clamp( dot(w,e)/dot(e,e), 0.0, 1.0 );
        d = min( d, dot(b,b) );

        // winding number from http://geomalgorithms.com/a03-_inclusion.html
        bvec3 cond = bvec3( p.y>=v[i].y, p.y<v[j].y, e.x*w.y>e.y*w.x );
        if( all(cond) || all(not(cond)) ) s*=-1.0;
    }

    return s*sqrt(d);
}

float sdLogo(vec2 p) {
    vec2 center = vec2(-.45, .75);
    float scale = .75;
    vec2 v0 = center - vec2(-0.127,0) * scale;
    vec2 v1 = center - vec2(-0.295,0.262) * scale;
    vec2 v2 = center - vec2(-0,0.524) * scale;
    vec2 v3 = center - vec2(-0.51,0.524) * scale;
    vec2 v4 = center - vec2(-0.558,0.419) * scale;
    vec2 v5 = center - vec2(-0.277,0.419) * scale;
    vec2 v6 = center - vec2(-0.447,0.261) * scale;
    vec2 v7 = center - vec2(-0.348,0.105) * scale;
    vec2 v8 = center - vec2(-0.812,0.105) * scale;
    vec2 v9 = center - vec2(-0.404,1) * scale;
    vec2 v10 = center - vec2(-0.544,1) * scale;
    vec2 v11 = center - vec2(-1,0) * scale;

    // add more points
    vec2[] poly = vec2[](v0,v1,v2,v3,v4,v5,v6,v7,v8,v9,v10,v11);

	return sdPoly(poly, p);
}

