vec2 map(in vec3 pos)
{
#if ENABLE_BENDING == 1
    float k = 2. * sin(iTime) * .15;
#else
    float k = 0.;
#endif
    float c = cos(k*pos.y);
    float s = sin(k*pos.y);
    mat2  m = mat2(c,-s,s,c);
    vec3  q = vec3(m*pos.xy, pos.z);

    vec2 logo = vec2(opExtrussion(q, sdLogo(q.xy), 0.05), LOGO);
    vec2 plane = vec2(sdPlane(pos.xyz), PLANE);
    return uniOp(plane, logo);
}

