float calcSoftshadow( in vec3 ro, in vec3 rd, in float mint, in float tmax )
{
	float res = 1.0;
    float t = mint;
    float ph = 1e10; // big, such that y = 0 on the first iteration

    for( int i=0; i<32; i++ )
    {
		vec2 h = map( ro + rd*t );
        float y = h.x * h.x / (2.0 * ph);
        float d = sqrt(h.x * h.x - y * y);
        res = min( res, 10.0*d/max(0.0,t-y) );
        ph = h.x;

        t += h.x;

        if( res<0.0001 || t>tmax ) break;
    }
    return clamp( res, 0.0, 1.0 );
}

vec3 calcNormal( in vec3 pos )
{
    vec2 e = vec2(1.0,-1.0)*0.5773*0.0005;
    return normalize( e.xyy*map( pos + e.xyy ).x +
					  e.yyx*map( pos + e.yyx ).x +
					  e.yxy*map( pos + e.yxy ).x +
					  e.xxx*map( pos + e.xxx ).x );
}

vec2 castRay( in vec3 ro, in vec3 rd )
{
    float tmin = 1.0;
    float tmax = 20.0;
    vec2 m = vec2(0., 0.); // dist marched + material info

#if 1
    // bounding volume
    float tp1 = (0.0-ro.y)/rd.y; if( tp1>0.0 ) tmax = min( tmax, tp1 );
    float tp2 = (1.0-ro.y)/rd.y; if( tp2>0.0 ) { if( ro.y>1.0 ) tmin = max( tmin, tp2 );
                                                 else           tmax = min( tmax, tp2 ); }
#endif

    float t = tmin;
    for( int i=0; i<64; i++ )
    {
	    float precis = 0.0005*t;
	    vec2 res = map( ro + rd * t );
        m.y = res.y;

        if( res.x < precis || t > tmax ) break;
        t += res.x;
    }

    if( t > tmax ) t = -1.0;
    m.x = t;
    return m;
}

float calcAO( in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float h = 0.001 + 0.15*float(i)/4.0;
        vec2 d = map( pos + h*nor );
        occ += (h - d.x)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 1.5*occ, 0.0, 1.0 );
}

vec3 render( in vec3 ro, in vec3 rd )
{
    vec3  col = vec3(0.0);
    vec2 t = castRay(ro,rd);

    if( t.x > -0.5 )
    {
        vec3 pos = ro + t.x * rd;
        vec3 nor = calcNormal( pos );      
		vec3 color = vec3(.14, .21, .3);

        if(t.y == LOGO) {
        	color = vec3(0., 0.72, 1.);
        }

        // key light
        vec3  lig = normalize( vec3(-0.5, 0.2, 0.25) );
        vec3  hal = normalize( lig-rd );
        float dif = clamp( dot( nor, lig ), 0.0, 1.0 ) *
                    calcSoftshadow( pos, lig, 0.01, 4.0 );

		float spe = pow( clamp( dot( nor, hal ), 0.0, 1.0 ), 16.0)*
                    dif *
                    (0.04 + 0.96*pow( clamp(1.0+dot(hal,rd),0.0,1.0), 5.0 ));

		col = color * dif * .7;
        col += spe * vec3(1.);

        // ambient light
        float occ = calcAO( pos, nor );
		float amb = clamp( 0.5 + 0.5 * nor.y, 0.0, 1.0 );
        col += color * amb * .7 * occ * .25;

        // fog
        col *= exp( -0.03 * t.x * t.x * t.x );
    }

	return col;
}

mat3 setCamera( in vec3 ro, in vec3 ta, float cr )
{
	vec3 cw = normalize(ta-ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv = normalize( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // camera
    float an = 8.0 - sin(2.0 * iTime);
    vec3 ro = vec3(cos(.58 * an), 1., .9 + sin(an));
    vec3 ta = vec3(0.0, -.5, -3.0);
    // camera-to-world transformation
    mat3 ca = setCamera( ro, ta, 0.0 );

    vec3 tot = vec3(0.0);
#if AA>1
    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (-iResolution.xy + 2.0*(fragCoord+o))/iResolution.y;
#else
        vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
#endif

        // ray direction
        vec3 rd = ca * normalize( vec3(p.xy,2.0) );

        // render
        vec3 col = render( ro, rd );

		// gamma
        col = pow( col, vec3(0.4545) );

        tot += col;
#if AA>1
    }
    tot /= float(AA*AA);
#endif


    fragColor = vec4( tot, 1.0 );
}

