vec3 DrawBox(int i, vec2 uv, vec4 dim, vec3 col, float blend){
	vec3 colorBox = vec3(0.0);

    if(uv.x>dim[0] && uv.x<dim[1] && uv.y>dim[2] && uv.y<dim[3] ) {
        colorBox = col;
    }

    vec3 blendBar = vec3(177.0/255., 162./255., 97./255.);

     if(uv.x>dim[0] && uv.x<dim[1] && uv.y>-0.2 && uv.y<0.2 ){
     	colorBox = mix(colorBox, blendBar, blend);
	}

    if(uv.x>dim[0]+0.1 && uv.x<dim[1]+0.1 && uv.y>-0.2 && uv.y<0.2 ){

       colorBox = mix(colorBox, blendBar, .7);;
    }

