void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	//Step 1: Definition of constants
    const int maxIt = 1000;
	vec2 c = fragCoord/iResolution.xy;	//complex number
    vec2 zoomp = vec2(-0.9, 0.23);
    //vec2 zoomp = vec2(0.0,0.0);

    //Step 2: Setting window boundaries
    float r = 500.0*(cos(iTime * 0.1 + 3.141592) + 1.0) / 2.0;
    float zoomf = 1.0 / (r*r);

    vec2 P1 = vec2(zoomp.x-2.0 * zoomf, zoomp.y-1.0 * zoomf);
    vec2 P2 = vec2(zoomp.x+1.0 * zoomf, zoomp.y+1.0 * zoomf);

    //Step 3: Transforming c into new coordinate space
    c.x = (P2.x - P1.x) * c.x + P1.x;
    c.y = (P2.y - P1.y) * c.y + P1.y;

	//Step 4: Calculating limit of z
    vec2 z = vec2(0.0,0.0);
    int anzIt = 0;
    for(int i = 0; i<maxIt; i++){
        z = vec2((z.x * z.x - z.y * z.y), (z.x * z.y + z.x * z.y)) + c;
        if (length(z)>1.9) {
           anzIt = i;	//number of iterations -> color
        }
    }

	//Step 5: Checking whether c is an element of the Mandelbrot set
    if(length(z) <= 2.0){
        fragColor = vec4(0.0,0.0,0.0,1.0);		//color it black
    }else{
    	//fragColor = vec4(1.0,1.0,1.0,1.0);	//color it white
        fragColor = vec4(	(sin(float(anzIt))+1.0) / 2.0,		
							(cos(float(anzIt))+1.0) / 2.0,
							(sin(float(anzIt)+2.0)+1.0) / 2.0,1.0);
    }								//color it depending on anzIt
}

// an antialiased ALU maze.
// Fabrice Neyret's teensy golfed maze algorithm:
// https://www.shadertoy.com/view/lt2cRR
// I just put his maze on a 3d plane since I had some code handy.
// https://www.shadertoy.com/view/3tVGRz

float smoothGrid(float x, float s, float g, float w)
{
    return mix(x, s, exp2(-g / w));    
}

// it's not his exact macro, but changed to have less gaps
float mazeFabrice(vec2 p)
{
	return mod(p[int(1e4*length(ceil(p/4.)))&1], 4.) >= 1. ? 0. : 1.;
}

float mazeFabriceAA(vec2 p)
{
    vec2 dx = dFdx(p), dy = dFdy(p);
    // fine, I give up on analytically filtering this; I'm just gonna discretely filter it.
    // at least to get the value to blend to along the edge.
    // still shows some unwanted grid cell edges though. 
    // Probably blurs too much toward the horizon.
    vec2 w = vec2(length(dx), length(dy)); //filterWidth2(p);
    w = w + .0001;
	float x = mazeFabrice(p);
	float y = .25 * (mazeFabrice(p + dx*vec2( .5, 0.))
                   + mazeFabrice(p + dx*vec2(-.5, 0.))
                   + mazeFabrice(p + dy*vec2( 0., .5))
                   + mazeFabrice(p + dy*vec2( 0.,-.5)));
    y = mix(y, .2, min(1., length(w))); // rebalance due to scale of maze empty vs. walls ratio; maze has more empty than filled
    vec2 f = fract(p);
    float g = min(f.x * (1.-f.x), f.y * (1.-f.y)) * 4.; //1.; // parabola box?
    return smoothGrid(x, y, .125 * g, length(w));
}


// shown on right side of split screen
float ImageR(vec2 uv)
{
    return mazeFabriceAA(uv);
}
// shown on left half of split screen
float ImageL(vec2 uv)
{
    return mazeFabrice(uv);
}


const float maxd = 850.;

// halfspace
float tracePlaneY(vec3 rp, vec3 rd)
{
    return rp.y <= 0. ? 0. :
    	rd.y >= 0. ? -1. : //maxd :
    	rp.y / -rd.y;
}

// cs is a cosine,sine pair representing angle of rotation as unit spinor
vec2 rot(inout vec2 v, vec2 cs)
{
    float c = cs.x, s = cs.y;
    // inlined rotation matrix 2x2 transforming v.
    return v = vec2(v.x * c - v.y * s, v.x * s + v.y * c);
}
// there're so many ways to do this though.  at least it's not all inlined and undocumented.

vec2 rot(inout vec2 v, float radians)
{
    return rot(v, vec2(cos(radians), sin(radians)));
}


vec3 CameraRay(vec2 p)
{
    vec2 q = (2. * p - iResolution.xy) / iResolution.y; // Fabrice's aspect-correct signed uv trick :)  I like it!
    vec3 rd = normalize(vec3(q / 2., 1.));
    float pitch = .4, yaw = iTime * .04;
    rot(rd.yz, pitch);
    rot(rd.xz, yaw);
    return rd;
}


const vec3 C1 = vec3(.5,.9,.8); //1.,1.,1.); //1.,1.,0.); //
const vec3 C2 = vec3(0.,.2,0.); //1.,0.,1.);
const vec3 Csky = vec3(.8,.9,1.);


void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // a simple camera
    vec3 rp = vec3(0,1,0);
    rp.y += sin(iTime * 0.07f) * .99; // bob slowly up and down
    vec3 rd = CameraRay(fragCoord);

    // Y plane distance
    float d = maxd;
    float dplane = tracePlaneY(rp, rd);
    vec2 cc = vec2(1., 0.);
    if (dplane >= 0.) {
        d = min(d, dplane);
	    cc = rp.xz + rd.xz * d; // grid uv at hit point
    	cc *= 4.0; // tiling
    }

    // split screen at mouse click
    float splits = fragCoord.x - iMouse.x;
    bool left = splits < 0.; //fragCoord.x < iMouse.x;
    // depending on last mouse click, split screen between two different functions
    float check = left 
        ? ImageL(cc)
        : ImageR(cc);

    vec3 c = mix(C1, C2, check); // colorize

    float fog = 1.0 - exp2(-.03*d); //0.; //
    c = mix(c, Csky, fog);
    // green vertical line at split
    c = mix(c, vec3(.0,.5,.0), exp2(-2. * abs(splits)));
    c = pow(c, vec3(1./2.2)); // to srgb gamma
    // output to screen
    fragColor = vec4(c, 1.);
}

precision highp float;
#define PI 3.14159265359

float euc(vec3 p, float e){
    float scale = 1.0;
    return scale * pow(pow(abs(p[0]),e)+pow(abs(p[1]),e)+pow(abs(p[2]),e),1.0/e) ;
}
float dist(vec3 p){
    float max_p = 4.0;
    float min_p = 0.2;
    float t = (max_p-min_p)*(0.5*(sin(0.8*iTime)+1.0))+min_p;
    return euc(p,t);
}
float ballSDF(vec3 p) {
    return dist(p) - 1.;
}

vec3 gradSDF(vec3 p) {
    float d = 0.0001;
    return normalize(vec3(
        ballSDF(p + vec3(d, 0.0, 0.0)) - ballSDF(p + vec3(-d, 0.0, 0.0)),
        ballSDF(p + vec3(0.0, d, 0.0)) - ballSDF(p + vec3(0.0, - d, 0.0)),
        ballSDF(p + vec3(0.0, 0.0, d)) - ballSDF(p + vec3(0.0, 0.0, - d))
    ));
}

mat3 euler(float h, float p, float r){
    return mat3(
        cos(r)*cos(h)-sin(r)*sin(p)*sin(h), -sin(r)*cos(p), cos(r)*sin(h)+sin(r)*sin(p)*cos(h),
        sin(r)*cos(h)+cos(r)*sin(p)*sin(h), cos(r)*cos(p), sin(r)*sin(h)-cos(r)*sin(p)*cos(h),
        -cos(p)*sin(h), sin(p), cos(p)*cos(h)
    ); 
}
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // fragment position
    vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);

    //camera transform
    float t = 0.3 * iTime;
    mat3 g = euler(t, t, t);

    // camera
    vec3 cPos = g * vec3(0.0, 0.0, 2.0);
    vec3 cDir = g * vec3(0.0, 0.0, - 1.0);
    vec3 cUp = g * vec3(0.0, 1.0, 0.0);
    vec3 cSide = cross(cDir, cUp);
    float targetDepth = 1.0;

    // light
    vec3 lightPos = g *vec3(0.0, 1.0, 2.0);

    // ray
    vec3 ray = cSide * p.x + cUp * p.y + cDir * targetDepth;
    ray = ray / dist(ray);

    // marching loop
    // needs subpix sampling to cover sharp edges?
    float hit_dist = 0.01;
    vec3 rPos = cPos;
    for(int i = 0; i < 1000; i++ ) {
        rPos += ray * ballSDF(rPos);
//        rPos += ray * hit_dist;
//        if (abs(ballSDF(rPos)) < hit_dist){
//           break;
//        }
    }

    // collision detection
    if (abs(ballSDF(rPos)) < hit_dist) { //object
        vec3 normal = gradSDF(rPos);
        float diff = dot(normalize(lightPos - rPos), gradSDF(rPos));
        vec3 amb = vec3(0.0, 1.0, 0.0);
        fragColor = vec4(diff * amb, 1.0);
    } else {    //background
        fragColor = vec4(1.0);
    }
}

