        vec2 d2 = d*vec2(-1., 1.);
        if(p.x > 0.)
        	p = (invBilinear( p, a, b, c, d ));
        else
        	p = (invBilinear( p, a2, b2, c2, d2 ));

        map += (1.-smoothstep(0., .1, abs(p.x-.5)))*(1.-smoothstep(0.9, 1., abs(p.y)+.5));
    }
    fragColor = vec4(flux(map*PI), 1.0 )*clamp(map, 0., 1.)+clamp(1.-map, 0., 1.);
}

