  float z=(noise((p+(time*1.0+3.5)*2.0)*0.2)*2.0-1.0)*15.0;
  p+=vec3(x,y,z);
	return max((noise(p*scale)+noise(p*2.0*scale)*0.5+
              noise(p*3.0*scale)*0.33+
              noise(p*4.0*scale)*0.25)*0.4807,0.0)*5.0;
}


