float GetDist(vec3 p  ) {	



    float d;
    float dif1;
    float dif2;

    d=999.9;
    float planeDist = p.y;

   vec3 pp;
    pp=p;



    vec2 res;
    res = vec2(9999, 0);






     if (res.x < planeDist)
      { res.x = res.x;     	} 
    else
        { 
          res.x = planeDist;
        if (planeDist > 0.5 )
          { res=vec2(res.x,8); }
        else
         { 
            res=vec2(res.x, 5);
                 } 
     }


    float sdSp1 = sdSphere( p-vec3(0.+0.5*sin(iTime),3.0+cos(iTime),-5.), 0.5 );
    float sdSp2 = sdSphere( p-vec3(0.+0.5*cos(iTime),3.0,-5.+cos(iTime)), 0.5 );
    float sdSp3 = sdSphere( p-vec3(0.+1.5*cos(iTime),3.0+0.5*sin(iTime*cos(iTime*0.5)),-5.+2.5*cos(iTime)), 0.25 );

    float sdcy1 =sdCylinder(  p.yzx, vec2(7.0,6.0) ) ;
    float sdcy2 =sdCylinder(  p.yzx, vec2(5.85,6.78) ) ;


    dif1 = differenceSDF(sdcy1,sdcy2);


    res=opU(res, vec2(dif1,13.0) );
    res=opU(res, vec2(sdSp1,8.0) );
    res=opU(res, vec2(sdSp2,18.0) );
    res=opU(res, vec2(sdSp3,19.0) );


