void mainImage(out vec4 O, in vec2 U)
{
    vec2 R = iResolution.xy;
    float _asp = ((min(iResolution.x/iResolution.y,iResolution.y/iResolution.x)));
    float dc = max(0.,(abs(fract(U.x/_asp)-.5)-abs(fract(U.y/_asp)-.5))); //backtothe70seffect

    O.xyz = dc + vec3(.2, 0.2, 0.25);
    vec2 M = .2 * iTime + (6. * atan(iMouse.xy / R));
    #define R3d(_a)(mat3(1,0,0,0,cos(_a.y),-sin(_a.y),0,sin(_a.y),cos(_a.y))*mat3(cos(_a.x),0,sin(_a.x),0,1,0,-sin(_a.x),0,cos(_a.x)))
    vec3 o = R3d(M) * vec3(.001, .001, float( (fract(.1*iTime)<.5)?1.:-.25));
    vec3 d = R3d(M) * vec3((U - .5 * R) / R.y, 1);

    // -------- box
    // http://iquilezles.org/www/articles/boxfunctions/boxfunctions.htm
    vec3 no = o * 1. / d;
    vec3 k = .25 * abs(1. / d);
    vec3 pin = (-k) - no, pot = k - no;
    float tN = max(pin.x, max(pin.y, pin.z));
    float tF = min(pot.x, min(pot.y, pot.z));
    if (tN < tF)
    {
        vec3 pos = (o + tF * d);
        vec3 n = /*sign(d) */ step(pot, pot.zxy) * step(pot, pot.yzx);
        float tx = (abs(n.x * tex(pos.zy) + n.y * tex(pos.zx) + n.z * tex(pos.xy)));
        // -------- shader box
        O.xyz = sin(vec3(1.8, 2.4, 2.7) * tx * 2.25 + 2.) * 0.35 + 0.53;
        O.xyz =(vec3(.7, .1, 0) * pow(dot(pos, vec3(-2, 1, -2)), 2.))//red lines
            	+ mix(O.xyz, vec3(.24, .36, .4), tx - 1.);
    };
}

