// refactored out the common derivative filtering portion:
// trivial to change to any dimension.  
// can ignore the axis not being striped.
// wound up using fwidth instead, as it's far more convenient!
float filterWidth1(float u)
{
    float dx = dFdx(u), dy = dFdy(u);
    return dx * dx + dy * dy + .0001;
}

float filterWidth2(vec2 uv)
{
     vec2 dx = dFdx(uv), dy = dFdy(uv);
    return dot(dx, dx) + dot(dy, dy) + .0001;
}
//    vec2 fw = fwidth(uv); return dot(fw, fw) + .0001; //max(fw.x, fw.y) + .0001; //

float filterWidth3(vec3 uvw)
{
    vec3 dx = dFdx(uvw), dy = dFdy(uvw);
    return dot(dx, dx) + dot(dy, dy) + .0001;
}

float filterWidthb2(vec2 uvw)
{
    vec2 dx = dFdx(uvw), dy = dFdy(uvw);
    return max(dot(dx, dx), dot(dy, dy)) + .0001; // tried; looks same to me
}

float filterWidthc2(vec2 uvw)
{
    vec2 w = fwidth(uvw);
    return dot(w, w) + .0001; //max(w.x, w.y) + .0001; //
}

float filterWidthc3(vec3 uvw)
{
    vec3 w = fwidth(uvw);
    return dot(w, w) + .0001; //max(w.x, max(w.y, w.z)) + .0001;
}

float tentbox2(vec2 f) // tent box?
{
    return min(min(f.x, 1.-f.x), min(f.y, 1.-f.y)) * 2.;
}

float parabox2(vec2 f) // parabola box?
{
    return min(f.x * (1.-f.x), f.y * (1.-f.y)) * 4.;
}

float paraboloid2(vec2 f) // paraboloid
{
	return f.x * (1.-f.x) * f.y * (1.-f.y) * 16.;
}

// exp2 filters return 1 when should blur completely out
// g is a positive distance to an edge to be smoothed
// idk if I really needed to refactor this, after all.
float smoothCheck(float x, float g, float w)
{
    return mix(x, .5, exp2(-clamp(g / w, 0., 1.)));
}


// simple unfiltered checkerboard texture as reference; aliases badly.
float checkerUnfiltered(vec2 p)
{
	return float((int(floor(p.x))^int(floor(p.y)))&1);
}

float checkerIso(vec2 p)
{
	return smoothCheck(checkerUnfiltered(p)
        , .25 //, tentbox2(fract(p)) //
        , exp2(max(0., fwidth(p.x) + fwidth(p.y))) - 1.);
}

// for @FabriceNeyret2; parameterized like a texlodbias but takes a log2 of fraction, so is -1 .. 0 range?! idk
float checkerIso(vec2 p, float miplodbias)
{
	return smoothCheck(checkerUnfiltered(p)
        , .25 //, tentbox2(fract(p)) //
        , 1./(1.-min(0., miplodbias))/*exp2(max(0., .05*miplodbias))*/ - 1.);
}

// considers each check axis separately
float checkerAA(vec2 p)
{
    vec2 f = fract(p);
    float x = checkerUnfiltered(p)
    , gx = min(f.x, 1.-f.x)
    , gy = min(f.y, 1.-f.y)
    , fx = fwidth(p.x)
    , fy = fwidth(p.y)
    , lx = gx / (1./R.x + fx*(1.+16.*fx))
    , ly = gy / (1./R.y + fy*(1.+16.*fy))
    , l = 2. * min(lx, ly);
    return mix(.5, x, clamp(l, 0., 1.));
}

//, s = .01*R.y
//gx / (1. + s * fx) //gx * exp2(-16./R.x*(fx - 64.)) //
//gy / (1. + s * fy) //gy * exp2(-16./R.y*(fy - 64.)) //

// like checkerAASimple but with squared falloff for
// distance blur, similar to how checkerExp works; decent
float checkerAA2(vec2 p)
{
    vec2 f = fract(p);
    float x = checkerUnfiltered(p)
    , g = tentbox2(f) //
    , w = max(fwidth(p.x), fwidth(p.y)) //
    , r = 2.*R.y
    , l = r*g / (1. + r*w*(8.*w+1.)); //r*g / (1. + 2.*r*w); //
    return mix(.5, x, clamp(l, 0., 1.));
}

// it's simple because it uses simple tent function
// and simple fwidth with relatively simple rational function inlined
float checkerAASimple(vec2 p)
{
    vec2 f = fract(p); //p - floor(p); //
    float x = checkerUnfiltered(p)
    , g = tentbox2(f) //min(min(f.x, 1.-f.x), min(f.y, 1.-f.y)) * 2. //
    , w = max(fwidth(p.x), fwidth(p.y)) //fwidth(p.x) + fwidth(p.y) //
    , r = 2.*R.y // can scale more for more blur in distance
    , l = r*g / (1. + r*w); //min(.5*iResolution.y*g, 1.-w); //8. * g - .5 * w; //8. * g / w; //g; // can try different functions
    return mix(.5, x, clamp(l, 0., 1.)); // smoothCheck
}
// the tent shape is not the main problem
// the filter width measurement is not the main problem
// those are both different yet the result
// appears identical to checkerAASimple
float checkerAAAlt(vec2 p)
{
    float x = checkerUnfiltered(p);
    vec2 f = fract(p);
    float g = parabox2(f);
    float w = fwidth(p.x) + fwidth(p.y); //filterWidthc2(p);
    //w = sqrt(w); // ALL of my filter width measurement wrappers are squared.
    return mix(x, .5, exp2(-2. * max(0., g / (w + 1e-7))));
     //min(.5*R.y*g, 1.-w); //8. * g - .5 * w; //8. * g / w; // can try different functions
}

// my exponential blending over edges, works pretty well but axes are joined awkwardly
float checkerExp(vec2 p)
{
    float x = checkerUnfiltered(p);
    vec2 f = fract(p);
    float g = parabox2(f);
    float w = filterWidth2(p);
    // g and w just get multiplied together anyway; just simplify this?  currently shared with the stripe toy
    return smoothCheck(x, .125 * g, w);
}

// my axis-separated exponentiated sinusoid
float checkerSine2(vec2 p)
{
    float x = sin(p.x * -pi) * exp2(-24.*fwidth(p.x) + 3.)
            * sin(p.y *  pi) * exp2(-24.*fwidth(p.y) + 3.); // tune blur rate
    return clamp(x, -1., 1.) * .5 + .5;
}
// mine.  Doesn't hold up when very close.
float checkerSine(vec2 p)
{
    float x = sin(p.x * -pi) * sin(p.y * pi);
    float w = max(fwidth(p.x), fwidth(p.y));
    x *= exp2(-16.*w + 4.); // tune blur rate
    return clamp(x, -1., 1.) * .5 + .5;
}

